mapper.select:
select Distinct BUDGET_CODE,BUDGET_CODE_TEXT from ( select T1.BUDGET_CODE ,T2.BUDGET_CODE_TEXT from FCM0101M T1 LEFT JOIN FCM0010M T2 ON T1.BUDGET_CODE = T2.BUDGET_CODE left join FCM0104D T3 ON T1.BUDGET_CODE = T3.BUDGET_CODE AND T1.COST_CENTR = T3.COST_CENTR AND T1.GL_ACC_CODE = T3.GL_ACC_CODE where 1=1 AND BUDGET_PLAN_YEAR = TO_CHAR(SYSDATE, 'YYYY') and LPAD(BUDGET_PLAN_MONTH, 2,0) = TO_CHAR(SYSDATE, 'MM') AND UPPER(T1.COST_CENTR) = UPPER(#{costCentr}) and NVL(T3.CNTRL_TYPE,'INVALID') <> 'INVALID'
AND T2.BUDGET_CODE != '99999'
AND T2.BUDGET_CODE_TEXT LIKE '%'|| #{budgetCodeText} || '%'
AND T2.BUDGET_CODE = #{budgetCode}
)

/* webInvoiceMapper.selectClamUn */
SELECT #{clmType} || NVL (TO_CHAR (MAX (SUBSTR (CLAM_UN, 3, 7) + 1), 'FM0000000'), '0000001') AS CLAM_UN
FROM   FCM0022M
WHERE  CLM_TYPE = #{clmType}

/* webInvoiceMapper.getAppvExcelInfo */ SELECT M.APPV_REQ_KEY_NO, TO_CHAR(M.REQST_DT, 'DD/MM/YYYY') AS REQST_DT, M.REQST_USER_ID, D.INVC_NO, TO_CHAR(D.INVC_DT, 'DD/MM/YYYY') AS INVC_DT, ( CASE D.INVC_TYPE WHEN 'F' THEN 'Full Tax invoice' WHEN 'S' THEN 'Simplified Tax invoice' END ) AS INVC_TYPE, D.MEM_ACC_ID, (SELECT MEM_ACC_NAME FROM FCM0006M WHERE MEM_ACC_ID = D.MEM_ACC_ID) AS MEM_ACC_NAME, D.SUPPLIR, TO_CHAR(D.PAY_DUE_DT, 'DD/MM/YYYY') AS PAY_DUE_DT, D.EXP_TYPE, D.EXP_TYPE_NAME, D.COST_CENTR, D.COST_CENTR_NAME, D.GL_ACC_CODE, D.GL_ACC_CODE_NAME, D.BUDGET_CODE, D.BUDGET_CODE_NAME, D.TAX_CODE, (SELECT INDUSTRY_CODE || ' ' || '(' || TAX_NAME || ')' FROM FCM0007C WHERE TAX_CODE = D.TAX_CODE) AS TAX_NAME, D.NET_AMT, D.TAX_AMT, D.TAX_NON_CLM_AMT, D.APPV_AMT, D.UTIL_NO, D.J_PAY_NO, TO_CHAR(D.BIL_PERIOD_F, 'DD/MM/YYYY') AS BIL_PERIOD_F, TO_CHAR(D.BIL_PERIOD_T, 'DD/MM/YYYY') AS BIL_PERIOD_T,
D.EXP_DESC
FROM FCM0004M M JOIN FCM0015D D ON M.APPV_PRCSS_NO = D.APPV_PRCSS_NO WHERE M.APPV_PRCSS_NO IN
#{item}
AND M.APPV_PRCSS_STUS IN
#{item}

SELECT (CASE APPV_STUS WHEN 'R' THEN 0 ELSE 1 END) AS RESULT_DATA
  FROM FCM0005D
 WHERE APPV_PRCSS_NO = #{appvPrcssNo}
 
    AND APPV_LINE_USER_ID = #{memCode}
 
 
    AND APPV_LINE_USER_ID IN (
        SELECT
            APPR_MEM_CODE
        FROM FCM0023M
        WHERE APPR_GRP = #{apprGrp}
    )
    AND APPV_STUS = 'R'

SELECT
    USER_NAME
FROM SYS0047M
WHERE

    HR_CODE = #{memCode}


    HR_CODE = (
        SELECT
            APPV_LINE_USER_ID
        FROM FCM0005D
        WHERE APPV_PRCSS_NO = #{appvPrcssNo}
    AND APPV_LINE_SEQ = #{appvLineSeq}
    )

SELECT APPR_GRP FROM FCM0023M WHERE 1=1
AND APPR_MEM_CODE = #{memCode}
AND APPR_USER_ID = #{userId}
AND APPR_MEM_STUS = '1'
