mapper.update:
/* webInvoiceMapper.updateWebInvoiceInfo */
   UPDATE FCM0002M
   SET COST_CENTR = #{costCentr}
       ,COST_CENTR_NAME = #{costCentrName}
       ,MEM_ACC_ID = #{memAccId}
       ,GST_RGIST_NO = #{gstRgistNo}
       ,BANK_CODE = #{bankCode}
       ,BANK_ACC_NO = #{bankAccNo}
       ,INVC_TYPE = #{invcType}
       ,INVC_NO = #{invcNo}
       ,INVC_DT = TO_DATE(#{invcDt}, 'DD/MM/YYYY')
       ,PAY_DUE_DT = TO_DATE(#{payDueDt}, 'DD/MM/YYYY')
       ,ATCH_FILE_GRP_ID = #{atchFileGrpId}
       ,INVC_REM = #{invcRem}
       ,TOT_AMT = #{totAmt}
       ,UTIL_NO = #{utilNo}
       ,UPD_DT = SYSDATE
       ,UPD_USER_ID = #{userId}
    WHERE CLM_NO = #{clmNo}

/* webInvoiceMapper.updateWebInvoiceDetail */

   UPDATE FCM0003D
   SET EXP_TYPE = #{expType}
       ,EXP_TYPE_NAME = #{expTypeName}
       ,GL_ACC_CODE = #{glAccCode}
       ,GL_ACC_CODE_NAME = #{glAccCodeName}
       ,BUDGET_CODE = #{budgetCode}
       ,BUDGET_CODE_NAME = #{budgetCodeName}
       ,TAX_CODE = #{taxCode}
       ,TAX_NON_CLM_AMT = #{taxNonClmAmt}
       ,NET_AMT = #{netAmt}
       ,TAX_AMT = #{taxAmt}
       ,TOT_AMT = #{totAmt}
       ,EXP_DESC = #{expDesc}
       ,UPD_DT = SYSDATE
       ,UPD_USER_ID = #{userId}
    WHERE CLM_NO = #{clmNo}
    AND CLM_SEQ = #{clmSeq}

