mapper.select:
SELECT T1.BUDGET_CODE ,T2.BUDGET_CODE_TEXT FROM FCM0104D T1 LEFT JOIN FCM0010M T2 ON T1.BUDGET_CODE = T2.BUDGET_CODE WHERE 1=1
AND T1.COST_CENTR = #{costCentr}
AND T2.BUDGET_CODE_TEXT LIKE '%'|| #{budgetCodeText} || '%'

/* webInvoiceMapper.selectClamUn */
SELECT #{clmType} || NVL (TO_CHAR (MAX (SUBSTR (CLAM_UN, 3, 7) + 1), 'FM0000000'), '0000001') AS CLAM_UN 
FROM   FCM0022M 
WHERE  CLM_TYPE = #{clmType}

/* webInvoiceMapper.selectSameVender */
SELECT CLM_NO 
FROM   FCM0002M 
WHERE  MEM_ACC_ID = #{memAccId} AND INVC_NO = #{invcNo}
