mapper.update:
           MERGE INTO SAL0335D A USING
		   (
			       SELECT * FROM SAL0029D WHERE CUST_ID = #{custId}
		   ) SRC ON (A.CUST_ID = SRC.CUST_ID)
		   WHEN MATCHED THEN UPDATE SET A.LOGIN_DT = SYSDATE, LOGIN_IND = 1, A.PHONE_NO = #{mobileNo}
		   WHEN NOT MATCHED THEN  INSERT ( CUST_ID , LOGIN_DT, LOGIN_IND, PHONE_NO) VALUES(SRC.CUST_ID, SYSDATE, 1, #{mobileNo})

mapper.insert:
INSERT INTO SAL0336D
 (
       PGM_PATH
     , FUNCTION_NAME
     , ERROR_MSG
     , CRT_DT
 ) VALUES (
       #{pgmPath}
     , #{functionName}
     , #{errorMsg}
     , SYSDATE
 )

mapper.select:
SELECT
    AREA_ID
FROM SYS0064M
WHERE STATE = #{statValue}
AND CITY = #{cityValue}
AND POSTCODE = #{postCodeValue}
AND AREA = #{areaValue}

SELECT AREA_ID, ISO, COUNTRY, STATE, CITY, POSTCODE, AREA, AREA || ' ' || POSTCODE || ' ' || CITY || ' ' || STATE || ' ' || COUNTRY AS FULL_ADDRESS FROM SYS0064M WHERE ISO = 'MY' AND UPPER(AREA) LIKE UPPER('%'|| #{searchStreet} || '%') AND STATUS_ID = 1
AND UPPER(STATE) LIKE UPPER('%'|| #{state} || '%')
AND UPPER(CITY) LIKE UPPER('%'|| #{city} || '%')
AND POSTCODE = #{postCode}
AND STATE = #{searchState}
AND CITY = #{searchCity}

               SELECT
                        COUNT(*) RESULT
				FROM SAL0339D
				WHERE CUST_ID = #{custId}
				AND ORDER_NO = #{orderNo}
				AND STUS_CODE_ID =1

mapper.update:
UPDATE
        SAL0335D
SET
        TAC_GEN_DATE = SYSDATE,
        TAC_NO = #{tacNo},
        ORDER_NO  = #{orderNo}
WHERE CUST_ID = #{custId}
AND PHONE_NO  = #{mobileNo}
