/* [com.coway.trust.biz.homecare.report.impl.HcPoResultMapper.selecthcPoResultMainListCnt] - KR JIN */ SELECT COUNT(1) AS CNT FROM ( SELECT 1 FROM HMC0004M A , HMC0005D B , ( SELECT D.PO_NO , D.PO_DTL_NO , SUM(D.DELVRY_QTY) AS DELVRY_QTY , SUM(NVL2(C.DELVRY_GI_DT, D.DELVRY_QTY, 0)) AS GI_QTY FROM HMC0007M C , HMC0008D D WHERE C.HMC_DELVRY_NO = D.HMC_DELVRY_NO AND C.PO_NO = D.PO_NO AND C.USE_YN = 'Y' AND D.USE_YN = 'Y' GROUP BY D.PO_NO, D.PO_DTL_NO ) E WHERE A.PO_NO = B.PO_NO AND B.PO_NO = E.PO_NO (+) AND B.PO_DTL_NO = E.PO_DTL_NO (+) AND A.PO_STS_CD = (SELECT CODE_ID FROM SYS0013M WHERE CODE_MASTER_ID = '429' AND CODE IN ('30')) -- Approve AND EXISTS( SELECT 1 FROM HMC0007M X WHERE X.PO_NO = A.PO_NO
AND X.HMC_DELVRY_NO = #{sHmcDelvryNo}
)
AND A.CDC = #{sCdc}
AND A.APPR_DT >= TO_DATE(#{sPoDtFrom}, 'YYYYMMDD')
AND A.APPR_DT <= TO_DATE(#{sPoDtTo}, 'YYYYMMDD') + 1
AND A.MEM_ACC_ID = #{sMemAccId}
AND 1 = 2
AND A.PO_NO = #{sPoNo}
)

/* [com.coway.trust.biz.homecare.report.impl.HcPoResultMapper.selecthcPoResultMainList] - KR JIN */ SELECT * FROM ( SELECT ROWNUM AS RNUM, T1.* FROM ( SELECT A.PO_NO , B.PO_DTL_NO , B.STOCK_ID , B.STOCK_CODE , (SELECT STK_DESC FROM SYS0026M WHERE STK_ID = B.STOCK_ID AND STK_CODE = B.STOCK_CODE) AS STOCK_NAME , A.SUPP_STS_CD , (SELECT CODE_NAME FROM SYS0013M WHERE CODE_MASTER_ID = '438' AND CODE_ID = A.SUPP_STS_CD) AS SUPP_STS_NAME , NVL(B.CONFIRM_QTY, 0) AS CONFIRM_QTY , NVL(C.ACTUAL_QTY, 0) AS ACTUAL_QTY , NVL(E.DELVRY_QTY, 0) AS DELVRY_QTY , NVL(E.GI_QTY, 0) AS GI_QTY , NVL(F.GR_QTY, 0) AS GR_QTY , NVL(F.FAIL_QTY, 0) AS FAIL_QTY , A.APPR_DT , A.MEM_ACC_ID , (SELECT MEM_ACC_NAME FROM FCM0006M WHERE MEM_ACC_ID = A.MEM_ACC_ID) AS MEM_ACC_NAME FROM HMC0004M A , HMC0005D B , ( SELECT PO_NO , PO_DTL_NO , SUM(PO_PLAN_QTY) AS PLAN_QTY , SUM(ACTUAL_QTY) AS ACTUAL_QTY FROM HMC0006D GROUP BY PO_NO, PO_DTL_NO ) C , ( SELECT D.PO_NO , D.PO_DTL_NO , SUM(D.DELVRY_QTY) AS DELVRY_QTY , SUM(NVL2(C.DELVRY_GI_DT, D.DELVRY_QTY, 0)) AS GI_QTY FROM HMC0007M C , HMC0008D D WHERE C.HMC_DELVRY_NO = D.HMC_DELVRY_NO AND C.PO_NO = D.PO_NO AND C.USE_YN = 'Y' AND D.USE_YN = 'Y' GROUP BY D.PO_NO, D.PO_DTL_NO ) E , ( SELECT B.PO_NO , B.PO_DTL_NO , SUM(B.RCIPT_QTY) AS GR_QTY , SUM(B.QC_FAIL_QTY) AS FAIL_QTY FROM HMC0009M A -- GR , HMC0010D B WHERE A.HMC_GR_NO = B.HMC_GR_NO AND A.PO_NO = B.PO_NO AND A.HMC_DELVRY_NO = B.HMC_DELVRY_NO AND A.GR_YN = 'Y' AND A.USE_YN = 'Y' AND B.USE_YN = 'Y' GROUP BY B.PO_NO, B.PO_DTL_NO ) F WHERE A.PO_NO = B.PO_NO AND B.PO_NO = C.PO_NO (+) AND B.PO_DTL_NO = C.PO_DTL_NO (+) AND B.PO_NO = E.PO_NO (+) AND B.PO_DTL_NO = E.PO_DTL_NO (+) AND B.PO_NO = F.PO_NO (+) AND B.PO_DTL_NO = F.PO_DTL_NO (+) AND A.PO_STS_CD = (SELECT CODE_ID FROM SYS0013M WHERE CODE_MASTER_ID = '429' AND CODE IN ('30')) -- Approve AND EXISTS( SELECT 1 FROM HMC0007M X WHERE X.PO_NO = A.PO_NO
AND X.HMC_DELVRY_NO = #{sHmcDelvryNo}
)
AND A.CDC = #{sCdc}
AND A.APPR_DT >= TO_DATE(#{sPoDtFrom}, 'YYYYMMDD')
AND A.APPR_DT <= TO_DATE(#{sPoDtTo}, 'YYYYMMDD') + 1
AND A.MEM_ACC_ID = #{sMemAccId}
AND 1 = 2
AND A.PO_NO = #{sPoNo}
ORDER BY
A.PO_NO ASCDESC B.PO_DTL_NO ASCDESC B.STOCK_ID ASCDESC B.STOCK_CODE ASCDESC STOCK_NAME ASCDESC A.SUPP_STS_CD ASCDESC SUPP_STS_NAME ASCDESC B.CONFIRM_QTY ASCDESC E.DELVRY_QTY ASCDESC E.GI_QTY ASCDESC E.GR_QTY ASCDESC A.APPR_DT ASCDESC A.MEM_ACC_ID ASCDESC MEM_ACC_NAME ASCDESC
A.PO_NO ASC, B.PO_DTL_NO ASC
A.PO_NO ASC, B.PO_DTL_NO ASC
) T1
ROWNUM <= #{lastIndex}
)
RNUM > #{firstIndex}
