/* [com.coway.trust.biz.homecare.report.impl.HcPoStatusMapper.selectHcPoStatusMainListCnt] - KR JIN */ SELECT COUNT(1) AS CNT FROM ( SELECT B.STOCK_CODE , (SELECT STK_DESC FROM SYS0026M WHERE STK_ID = B.STOCK_ID AND STK_CODE = B.STOCK_CODE) AS STOCK_NAME
FROM HMC0004M A , HMC0005D B
, ( SELECT A.PO_NO , B.PO_DTL_NO , A.HMC_DELVRY_NO , B.HMC_DELVRY_NO_DTL_NO , B.DELVRY_QTY , A.DELVRY_DT , A.DELVRY_GI_DT , C.HMC_GR_NO , C.HMC_GR_NO_DTL_NO , C.RCIPT_QTY , C.QC_FAIL_QTY , C.GR_DT FROM HMC0007M A -- Delivery , HMC0008D B , ( SELECT B.HMC_GR_NO , B.HMC_GR_NO_DTL_NO , B.HMC_DELVRY_NO , B.HMC_DELVRY_NO_DTL_NO , B.PO_NO , B.PO_DTL_NO , B.RCIPT_QTY , B.QC_FAIL_QTY , A.GR_DT FROM HMC0009M A -- GR , HMC0010D B WHERE A.HMC_GR_NO = B.HMC_GR_NO AND A.PO_NO = B.PO_NO AND A.HMC_DELVRY_NO = B.HMC_DELVRY_NO AND A.GR_YN = 'Y' AND A.USE_YN = 'Y' AND B.USE_YN = 'Y' ) C WHERE A.HMC_DELVRY_NO = B.HMC_DELVRY_NO AND A.PO_NO = B.PO_NO AND B.HMC_DELVRY_NO = C.HMC_DELVRY_NO (+) AND B.HMC_DELVRY_NO_DTL_NO = C.HMC_DELVRY_NO_DTL_NO (+) AND B.PO_NO = C.PO_NO (+) AND B.PO_DTL_NO = C.PO_DTL_NO (+) AND A.USE_YN = 'Y' AND B.USE_YN = 'Y' AND A.DELVRY_GI_DT IS NOT NULL ) D WHERE A.PO_NO = B.PO_NO
AND B.PO_NO = D.PO_NO (+) AND B.PO_DTL_NO = D.PO_DTL_NO (+)
AND A.CDC = #{sCdc}
AND A.APPR_DT >= TO_DATE(#{sPoDtFrom}, 'YYYYMMDD')
AND A.APPR_DT <= TO_DATE(#{sPoDtTo}, 'YYYYMMDD') + 1
AND A.MEM_ACC_ID = #{sMemAccId}
AND A.PO_NO = #{sPoNo}
AND D.HMC_DELVRY_NO = #{sHmcDelvryNo}
AND D.HMC_GR_NO = #{sHmcGrNo}
) X WHERE 1 = 1
AND ( X.STOCK_CODE = #{sStockCode} OR X.STOCK_NAME LIKE '%'||#{sStockCode}||'%' )

/* [com.coway.trust.biz.homecare.report.impl.HcPoStatusMapper.selectHcPoStatusMainList] - KR JIN */ SELECT * FROM ( SELECT ROWNUM AS RNUM, T1.* FROM ( SELECT Y.PO_KEY_PLAN , Y.PO_DTL_PLAN_NO , Y.PO_PLAN_QTY , Y.PO_PLAN_DT
, NVL(X.PO_NO, Y.PO_NO) AS PO_NO , NVL(X.PO_DTL_NO, Y.PO_DTL_NO) AS PO_DTL_NO , NVL(X.PO_DATE, Y.PO_DATE) AS PO_DATE , NVL(X.PO_STS_CD, Y.PO_STS_CD) AS PO_STS_CD , NVL(X.PO_STS_NAME, Y.PO_STS_NAME) AS PO_STS_NAME , NVL(X.MEM_ACC_ID, Y.MEM_ACC_ID) AS MEM_ACC_ID , NVL(X.MEM_ACC_NAME, Y.MEM_ACC_NAME) AS MEM_ACC_NAME , NVL(X.STOCK_ID, Y.STOCK_ID) AS STOCK_ID , NVL(X.STOCK_CODE, Y.STOCK_CODE) AS STOCK_CODE , NVL(X.STOCK_NAME, Y.STOCK_NAME) AS STOCK_NAME , NVL(X.PO_QTY, Y.PO_QTY) AS PO_QTY , NVL(X.SUPP_STS_CD, Y.SUPP_STS_CD) AS SUPP_STS_CD , NVL(X.SUPP_STS_NAME, Y.SUPP_STS_NAME) AS SUPP_STS_NAME
, X.HMC_DELVRY_NO , X.HMC_DELVRY_NO_DTL_NO , X.DELVRY_DT , X.DELVRY_GI_DT , X.DELVRY_QTY , X.HMC_GR_NO , X.HMC_GR_NO_DTL_NO , X.GR_DT , X.RCIPT_QTY , X.QC_FAIL_QTY
, MOD(DENSE_RANK() OVER (ORDER BY NVL(X.PO_NO, Y.PO_NO) ASC), 2) AS PO_MOD FROM ( SELECT ROW_NUMBER() OVER (PARTITION BY A.PO_NO, B.PO_DTL_NO ORDER BY D.HMC_DELVRY_NO ASC, D.HMC_DELVRY_NO_DTL_NO ASC) AS PO_KEY_DO , A.PO_NO , B.PO_DTL_NO , B.PO_NO || B.PO_DTL_NO AS PO_NO_PK , A.APPR_DT AS PO_DATE , A.PO_STS_CD , (SELECT CODE_NAME FROM SYS0013M WHERE CODE_MASTER_ID = '429' AND CODE_ID = A.PO_STS_CD ) AS PO_STS_NAME , A.MEM_ACC_ID , (SELECT MEM_ACC_NAME FROM FCM0006M WHERE MEM_ACC_ID = A.MEM_ACC_ID) AS MEM_ACC_NAME , B.STOCK_ID , B.STOCK_CODE , (SELECT STK_DESC FROM SYS0026M WHERE STK_ID = B.STOCK_ID AND STK_CODE = B.STOCK_CODE) AS STOCK_NAME , B.PO_QTY , A.SUPP_STS_CD , (SELECT CODE_NAME FROM SYS0013M WHERE CODE_MASTER_ID = '438' AND CODE_ID = A.SUPP_STS_CD) AS SUPP_STS_NAME , D.HMC_DELVRY_NO , D.HMC_DELVRY_NO_DTL_NO , TO_DATE(D.DELVRY_DT, 'YYYYMMDD') AS DELVRY_DT , TO_DATE(D.DELVRY_GI_DT, 'YYYYMMDD') AS DELVRY_GI_DT , D.DELVRY_QTY , D.HMC_GR_NO , D.HMC_GR_NO_DTL_NO , TO_DATE(D.GR_DT, 'YYYYMMDD') AS GR_DT , D.RCIPT_QTY , D.QC_FAIL_QTY
FROM HMC0004M A , HMC0005D B , ( SELECT A.PO_NO , B.PO_DTL_NO , A.HMC_DELVRY_NO , B.HMC_DELVRY_NO_DTL_NO , B.DELVRY_QTY , A.DELVRY_DT , A.DELVRY_GI_DT , C.HMC_GR_NO , C.HMC_GR_NO_DTL_NO , C.RCIPT_QTY , C.QC_FAIL_QTY , C.GR_DT FROM HMC0007M A -- Delivery , HMC0008D B , ( SELECT B.HMC_GR_NO , B.HMC_GR_NO_DTL_NO , B.HMC_DELVRY_NO , B.HMC_DELVRY_NO_DTL_NO , B.PO_NO , B.PO_DTL_NO , B.RCIPT_QTY , B.QC_FAIL_QTY , A.GR_DT FROM HMC0009M A -- GR , HMC0010D B WHERE A.HMC_GR_NO = B.HMC_GR_NO AND A.PO_NO = B.PO_NO AND A.HMC_DELVRY_NO = B.HMC_DELVRY_NO AND A.GR_YN = 'Y' AND A.USE_YN = 'Y' AND B.USE_YN = 'Y' ) C WHERE A.HMC_DELVRY_NO = B.HMC_DELVRY_NO AND A.PO_NO = B.PO_NO AND B.HMC_DELVRY_NO = C.HMC_DELVRY_NO (+) AND B.HMC_DELVRY_NO_DTL_NO = C.HMC_DELVRY_NO_DTL_NO (+) AND B.PO_NO = C.PO_NO (+) AND B.PO_DTL_NO = C.PO_DTL_NO (+) AND A.USE_YN = 'Y' AND B.USE_YN = 'Y' AND A.DELVRY_GI_DT IS NOT NULL ) D WHERE A.PO_NO = B.PO_NO AND B.PO_NO = D.PO_NO (+) AND B.PO_DTL_NO = D.PO_DTL_NO (+) AND A.USE_YN = 'Y' AND B.USE_YN = 'Y'
AND A.CDC = #{sCdc}
AND A.APPR_DT >= TO_DATE(#{sPoDtFrom}, 'YYYYMMDD')
AND A.APPR_DT <= TO_DATE(#{sPoDtTo}, 'YYYYMMDD') + 1
AND A.MEM_ACC_ID = #{sMemAccId}
AND A.PO_NO = #{sPoNo}
AND D.HMC_DELVRY_NO = #{sHmcDelvryNo}
AND D.HMC_GR_NO = #{sHmcGrNo}
) X FULL OUTER JOIN ( SELECT ROW_NUMBER() OVER (PARTITION BY D6D.PO_NO, D6D.PO_DTL_NO ORDER BY D6D.PO_DTL_PLAN_NO ASC) AS PO_KEY_PLAN , D6D.PO_DTL_PLAN_NO , D6D.PO_PLAN_QTY , TO_DATE(D6D.PO_PLAN_DT, 'YYYYMMDD') AS PO_PLAN_DT , D6D.PO_NO , D6D.PO_DTL_NO
, A.APPR_DT AS PO_DATE , A.PO_STS_CD , (SELECT CODE_NAME FROM SYS0013M WHERE CODE_MASTER_ID = '429' AND CODE_ID = A.PO_STS_CD ) AS PO_STS_NAME , A.MEM_ACC_ID , (SELECT MEM_ACC_NAME FROM FCM0006M WHERE MEM_ACC_ID = A.MEM_ACC_ID) AS MEM_ACC_NAME , B.STOCK_ID , B.STOCK_CODE , (SELECT STK_DESC FROM SYS0026M WHERE STK_ID = B.STOCK_ID AND STK_CODE = B.STOCK_CODE) AS STOCK_NAME , B.PO_QTY , A.SUPP_STS_CD , (SELECT CODE_NAME FROM SYS0013M WHERE CODE_MASTER_ID = '438' AND CODE_ID = A.SUPP_STS_CD) AS SUPP_STS_NAME FROM HMC0006D D6D -- Plan , HMC0004M A , HMC0005D B WHERE D6D.PO_NO = A.PO_NO AND D6D.PO_NO = B.PO_NO AND D6D.PO_DTL_NO = B.PO_DTL_NO AND A.USE_YN = 'Y' AND B.USE_YN = 'Y' ) Y ON X.PO_KEY_DO = Y.PO_KEY_PLAN AND X.PO_NO = Y.PO_NO AND X.PO_DTL_NO = Y.PO_DTL_NO WHERE 1 = 1
AND ( X.STOCK_CODE = #{sStockCode} OR X.STOCK_NAME LIKE '%'||#{sStockCode}||'%' )
ORDER BY
PO_NO ASCDESC PO_DTL_NO ASCDESC PO_DATE ASCDESC PO_STS_CD ASCDESC PO_STS_NAME ASCDESC MEM_ACC_ID ASCDESC MEM_ACC_NAME ASCDESC STOCK_ID ASCDESC STOCK_CODE ASCDESC STOCK_NAME ASCDESC PO_QTY ASCDESC SUPP_STS_CD ASCDESC SUPP_STS_NAME ASCDESC Y.PO_DTL_PLAN_NO ASCDESC Y.PO_PLAN_QTY ASCDESC Y.PO_PLAN_DT ASCDESC X.HMC_DELVRY_NO ASCDESC X.HMC_DELVRY_NO_DTL_NO ASCDESC X.DELVRY_DT ASCDESC X.DELVRY_GI_DT ASCDESC X.DELVRY_QTY ASCDESC X.HMC_GR_NO ASCDESC X.HMC_GR_NO_DTL_NO ASCDESC X.GR_DT ASCDESC X.RCIPT_QTY ASCDESC X.QC_FAIL_QTY ASCDESC
PO_NO, PO_DTL_NO, Y.PO_DTL_PLAN_NO, X.HMC_DELVRY_NO, X.HMC_DELVRY_NO_DTL_NO, X.HMC_GR_NO, X.HMC_GR_NO_DTL_NO ASC
PO_NO, PO_DTL_NO, Y.PO_DTL_PLAN_NO, X.HMC_DELVRY_NO, X.HMC_DELVRY_NO_DTL_NO, X.HMC_GR_NO, X.HMC_GR_NO_DTL_NO ASC
) T1
ROWNUM <= #{lastIndex}
)
RNUM > #{firstIndex}
