AND UPPER(B.AREA) LIKE UPPER('%'||#{area}||'%')
AND REPLACE(B.AREA_ID, '-', '') = REPLACE(#{areaID}, '-', '')
AND UPPER(B.CITY) LIKE UPPER('%'||#{city}||'%')
AND B.STATE = #{state}
AND B.POSTCODE = #{postcode}
AND cvrg.STUS_CODE_ID = #{statusId}
ORDER BY cvrg.AREA_ID

SELECT T1.RESN_ID CODE, UPPER(T1.CODE || ' - ' || T1.RESN_DESC) CODE_NAME FROM SYS0032M T1 WHERE T1.RESN_TYPE_ID = #{resnTypeId}
AND T1.STUS_CODE_ID = #{stusCodeId}
AND T1.CODE IN (SELECT REGEXP_SUBSTR (#{codeIn}, '[^,]+', 1, LEVEL) FROM DUAL CONNECT BY REGEXP_SUBSTR (#{codeIn}, '[^,]+', 1, LEVEL) IS NOT NULL)
ORDER BY T1.RESN_ID

