mapper.select:
SELECT A.SUB_ORD_ID AS ORDER_ID, B.SALES_ORD_NO AS AIRCOND_BULK_ORDER_NO, A.COMBO_GRP AS COMBO_GROUP, G.CODE AS APP_TYPE, D.STK_DESC AS PRODUCT, E.PROMO_ID AS PROMO_ID, E.PROMO_CODE AS PROMO_CODE, E.PROMO_DESC AS PROMO_NAME, TO_CHAR(B.SALES_DT, 'DD/MM/YYYY') AS ORDER_DATE, H.CODE AS ORDER_STATUS FROM SAL0349D A JOIN SAL0001D B ON B.SALES_ORD_ID = A.SUB_ORD_ID JOIN SAL0002D C ON C.SALES_ORD_ID = B.SALES_ORD_ID JOIN SYS0026M D ON C.ITM_STK_ID = D.STK_ID JOIN SAL0017D E ON B.PROMO_ID = E.PROMO_ID JOIN SAL0029D F ON B.CUST_ID = F.CUST_ID JOIN SYS0013M G ON B.APP_TYPE_ID = G.CODE_ID JOIN SYS0038M H ON H.STUS_CODE_ID = B.STUS_CODE_ID WHERE
A.MAIN_ORD_ID =#{pckBindingId}
A.MAIN_ORD_ID = #{ordId}
AND A.SUB_ORD_ID <> #{ordId} AND A.DISB = 0 ORDER BY B.SALES_ORD_NO ASC
