mapper.select:
SELECT CODE_ID , CODE , CODE_NAME , DBMS_LOB.SUBSTR (CODE_DESC, 4000) DESCRIPTION FROM SYS0013M WHERE 1=1
AND CODE_MASTER_ID = #{groupCode}
AND CODE = #{Codeval}
AND CODE LIKE #{likeValue}||'%'
AND CODE NOT LIKE #{notlike}||'%'
AND CODE IN (SELECT REGEXP_SUBSTR (#{codeIn}, '[^,]+', 1, LEVEL) FROM DUAL CONNECT BY REGEXP_SUBSTR (#{codeIn}, '[^,]+', 1, LEVEL) IS NOT NULL)
ORDER BY ${orderValue}
ORDER BY CODE_NAME

SELECT CODE_ID , CODE , CODE_NAME , DBMS_LOB.SUBSTR (CODE_DESC, 4000) DESCRIPTION FROM SYS0013M WHERE 1=1
AND CODE_MASTER_ID = #{groupCode}
AND CODE = #{Codeval}
AND CODE LIKE #{likeValue}||'%'
AND CODE NOT LIKE #{notlike}||'%'
AND CODE IN (SELECT REGEXP_SUBSTR (#{codeIn}, '[^,]+', 1, LEVEL) FROM DUAL CONNECT BY REGEXP_SUBSTR (#{codeIn}, '[^,]+', 1, LEVEL) IS NOT NULL)
AND CODE IN ('CS1T','CS1Y','FT1Y','FT1T')
ORDER BY ${orderValue}
ORDER BY CODE_NAME
