SELECT T1.SRV_ORD_ID , T1.SRV_ORD_NO , T1.REF_NO , T1.SALES_DT , T1.CUST_ID , T1.CUST_CNT_ID , T1.CUST_ADD_ID , T1.MEM_ID , T1.BRNCH_ID , T1.APP_TYPE_ID
, T1.TOT_AMT , T1.PROMO_ID , T1.BINDING_NO , T1.CC_PROMO_ID , NVL(T1.TOT_PV, 0) AS TOT_PV , T1.REM , T1.PV_MONTH , T1.PV_YEAR , T1.STUS_CODE_ID , T1.UPD_DT , T1.UPD_USER_ID , T1.SYNC_CHK , T1.CUST_PO_NO , T1.REN_CHK_ID , T1.INST_PRIOD , T1.DO_NO , T1.DEPT_CODE , T1.GRP_CODE , T1.ORG_CODE , T1.SALES_ORD_ID_OLD , T1.EDIT_TYPE_ID , T1.CUST_BILL_ID , NVL(T1.MTH_RENT_AMT, 0) AS MTH_RENT_AMT , T1.LOK , T1.AEON_STUS_ID , T1.COMM_DT , T1.CRT_USER_ID , T1.CRT_DT , T1.PAY_COM_DT , T1.DEF_RENT_AMT , T1.REF_DOC_ID
, T1.SALES_HM_ID , T1.SALES_SM_ID , T1.SALES_GM_ID , T1.ADV_BILL , T1.CNVR_SCHEME_ID , T1.CUST_CARE_CNT_ID , T1.EMP_CHK , T1.EX_TRADE , T1.ECASH
, T1.PROMO_DISC_PERIOD_TP , T1.PROMO_DISC_PERIOD , NVL(T1.NOR_AMT, 0) AS NOR_AMT , NVL(T1.NOR_RNT_FEE, 0) AS NOR_RNT_FEE , NVL(T1.DISC_RNT_FEE, 0) AS DISC_RNT_FEE , T1.GST_CHK , T2.TYPE_ID , T3.CODE_NAME APP_TYPE_NAME , T4.NAME ORDER_STATUS , NVL(T5.STUS_CODE_ID, '-') RENTAL_STATUS , T6.CNTRCT_RENTAL_PRIOD RENTAL_PERIOD FROM SAL0225D T1 LEFT JOIN SAL0029D T2 ON T2.CUST_ID = T1.CUST_ID LEFT JOIN SYS0013M T3 ON T3.CODE_ID = T1.APP_TYPE_ID LEFT JOIN SYS0038M T4 ON T4.STUS_CODE_ID = T1.STUS_CODE_ID LEFT JOIN SAL0071D T5 ON T5.SALES_ORD_ID = T1.SRV_ORD_ID LEFT JOIN SAL0003D T6 ON T6.CNTRCT_SALES_ORD_ID = T1.SRV_ORD_ID WHERE T1.SRV_ORD_ID = #{salesOrdId}
AND T1.APP_TYPE_ID = #{appTypeId}
AND ROWNUM <= 1

SELECT SUM(T1.RENT_AMT) RENT_AMT
  FROM PAY0022D T1
 WHERE T1.RENT_SO_ID = #{value}

SELECT T.PROMO_ID CODE , T.PROMO_DESC CODE_NAME FROM ( SELECT DISTINCT T2.PROMO_ID PROMO_ID , T2.PROMO_CODE || '-' || T2.PROMO_DESC PROMO_DESC , T2.PROMO_DT_FROM FROM SAL0017D T2 WHERE T2.PROMO_STUS_ID = 1 AND T2.PROMO_APP_TYPE_ID IN (3221,3220) AND T2.PROMO_TYPE_ID = 2282 AND 0 = T2.PROMO_IS_TRIAL_CNVR AND TO_CHAR(T2.PROMO_DT_FROM, 'YYYYMMDD') <= TO_CHAR(SYSDATE, 'YYYYMMDD') AND TO_CHAR(T2.PROMO_DT_END, 'YYYYMMDD') >= TO_CHAR(SYSDATE, 'YYYYMMDD') AND T2.EMP_CHK = #{empChk} AND T2.EX_TRADE = #{exTrade} AND (T2.PROMO_CUST_TYPE = #{promoCustType} OR T2.PROMO_CUST_TYPE = 0 OR T2.PROMO_CUST_TYPE IS NULL) AND T2.PROMO_APP_TYPE_ID = #{appTypeId} AND T2.E_SALES = 0
AND (T2.STK_SIZE = #{stkSize} OR T2.STK_SIZE IS NULL)
) T ORDER BY T.PROMO_DT_FROM DESC

SELECT T.MEM_ID , T.MEM_CODE , T.NAME , T.NRIC , T.MEM_TYPE , T.CODE_NAME , T.DEPT_CODE , T.GRP_CODE , T.ORG_CODE , T.MEM_LVL , T.LVL3_UP_ID , T.LVL2_UP_ID , T.LVL1_UP_ID FROM ( SELECT T1.MEM_ID , T1.MEM_CODE , T1.MEM_TYPE , T1.NAME , T1.NRIC , T2.CODE_NAME , CASE WHEN (NOT((T3.MEM_ID IS NULL) AND (T3.MEM_CODE IS NULL) AND (T3.MEM_LVL IS NULL) AND (T3.DEPT_CODE IS NULL) AND (T3.GRP_CODE IS NULL) AND (T3.ORG_CODE IS NULL) AND (T3.TOP_ORG_CODE IS NULL) AND (T3.MEM_UP_ID IS NULL) AND (T3.LVL3_UP_ID IS NULL) AND (T3.LVL2_UP_ID IS NULL) AND (T3.LVL1_UP_ID IS NULL) AND (T3.LVL0_UP_ID IS NULL))) THEN T3.DEPT_CODE ELSE '' END DEPT_CODE , CASE WHEN (NOT((T3.MEM_ID IS NULL) AND (T3.MEM_CODE IS NULL) AND (T3.MEM_LVL IS NULL) AND (T3.DEPT_CODE IS NULL) AND (T3.GRP_CODE IS NULL) AND (T3.ORG_CODE IS NULL) AND (T3.TOP_ORG_CODE IS NULL) AND (T3.MEM_UP_ID IS NULL) AND (T3.LVL3_UP_ID IS NULL) AND (T3.LVL2_UP_ID IS NULL) AND (T3.LVL1_UP_ID IS NULL) AND (T3.LVL0_UP_ID IS NULL))) THEN T3.GRP_CODE ELSE '' END GRP_CODE , CASE WHEN (NOT((T3.MEM_ID IS NULL) AND (T3.MEM_CODE IS NULL) AND (T3.MEM_LVL IS NULL) AND (T3.DEPT_CODE IS NULL) AND (T3.GRP_CODE IS NULL) AND (T3.ORG_CODE IS NULL) AND (T3.TOP_ORG_CODE IS NULL) AND (T3.MEM_UP_ID IS NULL) AND (T3.LVL3_UP_ID IS NULL) AND (T3.LVL2_UP_ID IS NULL) AND (T3.LVL1_UP_ID IS NULL) AND (T3.LVL0_UP_ID IS NULL))) THEN T3.ORG_CODE ELSE '' END ORG_CODE , CASE WHEN (NOT((T3.MEM_ID IS NULL) AND (T3.MEM_CODE IS NULL) AND (T3.MEM_LVL IS NULL) AND (T3.DEPT_CODE IS NULL) AND (T3.GRP_CODE IS NULL) AND (T3.ORG_CODE IS NULL) AND (T3.TOP_ORG_CODE IS NULL) AND (T3.MEM_UP_ID IS NULL) AND (T3.LVL3_UP_ID IS NULL) AND (T3.LVL2_UP_ID IS NULL) AND (T3.LVL1_UP_ID IS NULL) AND (T3.LVL0_UP_ID IS NULL))) THEN T3.MEM_LVL ELSE 0 END MEM_LVL , CASE WHEN (NOT((T3.MEM_ID IS NULL) AND (T3.MEM_CODE IS NULL) AND (T3.MEM_LVL IS NULL) AND (T3.DEPT_CODE IS NULL) AND (T3.GRP_CODE IS NULL) AND (T3.ORG_CODE IS NULL) AND (T3.TOP_ORG_CODE IS NULL) AND (T3.MEM_UP_ID IS NULL) AND (T3.LVL3_UP_ID IS NULL) AND (T3.LVL2_UP_ID IS NULL) AND (T3.LVL1_UP_ID IS NULL) AND (T3.LVL0_UP_ID IS NULL))) THEN T3.LVL3_UP_ID ELSE 0 END LVL3_UP_ID , CASE WHEN (NOT((T3.MEM_ID IS NULL) AND (T3.MEM_CODE IS NULL) AND (T3.MEM_LVL IS NULL) AND (T3.DEPT_CODE IS NULL) AND (T3.GRP_CODE IS NULL) AND (T3.ORG_CODE IS NULL) AND (T3.TOP_ORG_CODE IS NULL) AND (T3.MEM_UP_ID IS NULL) AND (T3.LVL3_UP_ID IS NULL) AND (T3.LVL2_UP_ID IS NULL) AND (T3.LVL1_UP_ID IS NULL) AND (T3.LVL0_UP_ID IS NULL))) THEN T3.LVL2_UP_ID ELSE 0 END LVL2_UP_ID , CASE WHEN (NOT((T3.MEM_ID IS NULL) AND (T3.MEM_CODE IS NULL) AND (T3.MEM_LVL IS NULL) AND (T3.DEPT_CODE IS NULL) AND (T3.GRP_CODE IS NULL) AND (T3.ORG_CODE IS NULL) AND (T3.TOP_ORG_CODE IS NULL) AND (T3.MEM_UP_ID IS NULL) AND (T3.LVL3_UP_ID IS NULL) AND (T3.LVL2_UP_ID IS NULL) AND (T3.LVL1_UP_ID IS NULL) AND (T3.LVL0_UP_ID IS NULL))) THEN T3.LVL1_UP_ID ELSE 0 END LVL1_UP_ID FROM ORG0001D T1 JOIN SYS0013M T2 ON T2.CODE_ID = T1.MEM_TYPE LEFT JOIN (SELECT V1.MEM_ID , V1.MEM_CODE , V1.MEM_LVL , V1.DEPT_CODE , V1.GRP_CODE , V1.ORG_CODE , V1.TOP_ORG_CODE , V1.MEM_UP_ID , V1.LVL3_UP_ID , V1.LVL2_UP_ID , V1.LVL1_UP_ID , V1.LVL0_UP_ID FROM ORG1001V V1 ) T3 ON T3.MEM_ID = T1.MEM_ID WHERE 1 = 1
AND T1.MEM_ID = #{memId}
AND T1.MEM_CODE = #{memCode}
AND T1.STUS = #{stus}
AND (MEM_LVL = 4 AND MEM_TYPE IN (1,2,7) OR MEM_TYPE IN (4,6) )
AND ROWNUM <= 1 ) T

SELECT TT.MEM_ID , TT.MEM_CODE , TT.MEM_TYPE , TT.MEM_TYPE_NAME , TT.NAME , TT.NRIC , TT.RANK , TT.UPD_DT , TT.BRNCH , TT.STUS , TT.TEL_HUSE , TT.TEL_MOBILE , TT.TEL_OFFICE , TT.MEM_LVL , TT.DEPT_CODE , TT.EMAIL FROM ( SELECT T.MEM_ID , T.MEM_CODE , T.MEM_TYPE , T.MEM_TYPE_NAME , T.NAME , T.NRIC , T.RANK , T.UPD_DT , T.BRNCH , T.STUS , T.TEL_HUSE , T.TEL_MOBILE , T.TEL_OFFICE , T.MEM_LVL , T.DEPT_CODE , T.EMAIL , ROW_NUMBER() OVER ( ORDER BY T.MEM_CODE ASC ) ROW_NUMBER FROM ( SELECT T1.MEM_ID , T1.MEM_CODE , T1.MEM_TYPE , T3.CODE_NAME AS MEM_TYPE_NAME , T1.NAME , T1.NRIC , T1.RANK , T1.UPD_DT , NVL(T1.BRNCH, 0) BRNCH , NVL(T1.STUS, 0) STUS , NVL(T1.TEL_HUSE, '') TEL_HUSE , NVL(T1.TEL_MOBILE, '') TEL_MOBILE , NVL(T1.TEL_OFFICE, '') TEL_OFFICE , NVL(T2.MEM_LVL, 0) MEM_LVL , NVL(T2.DEPT_CODE, '') DEPT_CODE , NVL(T1.EMAIL, '') EMAIL FROM ORG0001D T1 LEFT JOIN ORG0005D T2 ON T2.MEM_ID = T1.MEM_ID LEFT JOIN SYS0013M T3 ON T3.CODE_ID = T1.MEM_TYPE AND T3.CODE_MASTER_ID = 1 WHERE 8 != T1.STUS
AND T1.STUS = #{stus}
AND T1.MEM_TYPE = #{memType}
AND T1.MEM_CODE = #{memCode}
AND UPPER(T1.NAME) LIKE '%'||UPPER(#{name})||'%'
AND UPPER(T1.NRIC) LIKE '%'||UPPER(#{nric})||'%'
AND T1.BRNCH = #{brnch}
AND T2.MEM_LVL = #{memLvl}
) T ) TT ORDER BY MEM_CODE ASC

