/* com.coway.trust.biz.homecare.sales.order.impl.HcOrderListMapper.selectHcOrderList */ WITH HC_CATEGORY AS (SELECT AA.CODE_ID FROM SYS0013M AA, SYS0094M BB WHERE AA.CODE = BB.CODE AND AA.CODE_MASTER_ID = 11 AND BB.IND = 'HOMECARE') SELECT T.APP_TYPE_CODE , T.APP_TYPE_NAME , T.CRT_USER_ID , T.CUST_IC , T.CUST_NAME , T.CUST_VA_NO , T.DSC_BRNCH_ID , T.KEYIN_BRNCH_ID , T.ORD_ID , T.ORD_NO , T.ORD_STUS_CODE , T.PO_NO , T.PRODUCT_CODE , T.PRODUCT_NAME , T.REF_NO , T.RENT_STUS , T.SALESMAN_MEM_ID , T.SALESMAN_CODE , T.SALESMAN_MEM_TYPE_ID , T.BILLING_GRP_ID , T.MAIL_TEL_MOB , T.MAIL_TEL_FAX , T.MAIL_TEL_OFF , T.MAIL_TEL_RES , T.INST_TEL_FAX , T.INST_TEL_MOB , T.INST_TEL_OFF , T.INST_TEL_RES , T.SIRIM_NO , T.SERIAL_NO , T.PROMO_CODE , T.PROMO_DESC , T.RELATED_NO , T.RELATED_ID , 1 C1 , NVL(T.APP_TYPE_ID, 0) AS APP_TYPE_ID , NVL(T.CUST_ID, 0) AS CUST_ID , TO_CHAR(T.ORD_DT, 'DD/MM/YYYY') AS ORD_DT , NVL(T.ORD_STUS_ID, 0) AS ORD_STUS_ID , NVL(T.PRODUCT_ID, 0) AS PRODUCT_ID , T.PV_MONTH , T.PV_YEAR , T.BNDL_NO
FROM ( SELECT /*+ use_nl( V1.som V1.ir V1.ie V1.ei V1.i V1.sod V1.cp3 V1.cp2 V1.rs V1.c V1.u V1.mem V1.cd V1.stk V1.pm V1.S ) */ V1.APP_TYPE_CODE , V1.APP_TYPE_ID , V1.APP_TYPE_NAME , V1.CRT_USER_ID , V1.CUST_IC , V1.CUST_NAME , V1.CUST_ID , V1.CUST_VA_NO , V1.DSC_BRNCH_ID , V1.KEYIN_BRNCH_ID , V1.ORD_DT , V1.ORD_ID , V1.ORD_NO , V1.ORD_STUS_CODE , V1.ORD_STUS_ID , V1.PO_NO , V1.PRODUCT_ID , V1.PRODUCT_CODE , V1.PRODUCT_NAME , V1.REF_NO , V1.RENT_STUS , V1.SALESMAN_MEM_ID , V1.SALESMAN_CODE , V1.SALESMAN_MEM_TYPE_ID , V1.BILLING_GRP_ID , V1.MAIL_TEL_MOB , V1.MAIL_TEL_FAX , V1.MAIL_TEL_OFF , V1.MAIL_TEL_RES , V1.INST_TEL_FAX , V1.INST_TEL_MOB , V1.INST_TEL_OFF , V1.INST_TEL_RES , V1.SIRIM_NO , V1.SERIAL_NO , V1.ITM_PRC_ID , V1.PV_MONTH , V1.PV_YEAR , V1.PROMO_CODE , V1.PROMO_DESC , V1.RELATED_NO , V1.RELATED_ID , D1.BNDL_NO FROM SAL1013V V1 JOIN SYS0026M S1 ON V1.PRODUCT_ID = S1.STK_ID JOIN HC_CATEGORY H1 ON H1.CODE_ID = S1.STK_CTGRY_ID LEFT JOIN HMC0011D D1 ON V1.BNDL_ID = D1.ORD_SEQ_NO
INNER JOIN ( SELECT IE.SALES_ORD_ID FROM SAL0047D IR LEFT OUTER JOIN SAL0046D IE ON IE.INSTALL_ENTRY_ID = IR.ENTRY_ID WHERE
IR.SERIAL_NO = #{serialNo}
AND IR.SIRIM_NO = #{sirimNo}
IR.SIRIM_NO = #{sirimNo}
) A on V1.ORD_ID = A.SALES_ORD_ID
JOIN SAL0001D SAL01 ON V1.ORD_ID = SAL01.SALES_ORD_ID
JOIN( SELECT DISTINCT S1.CUST_ID , S1.SALES_ORD_ID FROM SAL0001D S JOIN SAL0001D S1 on S1.CUST_ID = S.CUST_ID JOIN SAL0090D G ON S.SALES_ORD_ID = G.SRV_SO_ID JOIN SAL0029D C ON S.CUST_ID = C.CUST_ID LEFT JOIN ORG1001V V ON V.MEM_ID = G.SRV_CODY_ID WHERE 1=1
AND S.SALES_ORD_ID = #{ordId}
AND S.SALES_ORD_NO = #{ordNo}
AND S.SALES_DT BETWEEN TO_DATE(#{ordStartDt}, 'YYYY-MM-DD') AND TO_DATE(#{ordEndDt}||' 23:59:59', 'YYYY-MM-DD HH24:MI:SS')
AND S.SALES_DT BETWEEN TO_DATE(#{ordDt}, 'DD/MM/YYYY') AND TO_DATE(#{ordDt}||' 23:59:59', 'DD/MM/YYYY HH24:MI:SS')
AND (V.MEM_ID = #{memId} OR C.TYPE_ID = 965)
AND (V.DEPT_CODE = #{deptCode} OR C.TYPE_ID = 965)
AND (V.GRP_CODE = #{grpCode} OR C.TYPE_ID = 965)
AND (V.ORG_CODE = #{orgCode} OR C.TYPE_ID = 965)
) SVC ON V1.ORD_ID = SVC.SALES_ORD_ID
JOIN( SELECT DISTINCT S.CUST_ID , S.SALES_ORD_ID FROM SAL0001D S LEFT JOIN ORG1001V V ON V.MEM_ID = S.MEM_ID WHERE 1=1
AND (V.MEM_ID = #{memId})
AND (V.DEPT_CODE = #{deptCode})
AND (V.GRP_CODE = #{grpCode})
AND (V.ORG_CODE = #{orgCode})
) SVC ON V1.ORD_ID = SVC.SALES_ORD_ID
WHERE 1=1
AND V1.SALES_DT BETWEEN TO_DATE(#{ordStartDt}, 'YYYY-MM-DD') AND TO_DATE(#{ordEndDt}||' 23:59:59', 'YYYY-MM-DD HH24:MI:SS')
AND V1.SALES_DT BETWEEN TO_DATE(#{ordDt}, 'DD/MM/YYYY') AND TO_DATE(#{ordDt}||' 23:59:59', 'DD/MM/YYYY HH24:MI:SS')
AND V1.ORD_NO = #{ordNo}
AND V1.APP_TYPE_ID IN
#{item}
AND V1.ORD_STUS_ID IN
#{item}
AND V1.KEYIN_BRNCH_ID IN
#{item}
AND V1.DSC_BRNCH_ID IN
#{item}
AND V1.CUST_ID = #{custId}
AND V1.CUST_NAME LIKE '%'||UPPER(#{custName})||'%'
AND V1.CUST_IC LIKE '%'||UPPER(#{custIc})||'%'
AND V1.PRODUCT_ID IN
#{item}
AND V1.SALESMAN_CODE = UPPER(#{salesmanCode})
AND V1.RENT_STUS IN
#{item}
AND V1.REF_NO = UPPER(#{refNo})
AND V1.PO_NO = UPPER(#{poNo})
AND #{contactNo} IN (V1.MAIL_TEL_MOB, V1.MAIL_TEL_OFF, V1.MAIL_TEL_RES)
AND V1.CUST_VA_NO = UPPER(#{vaNo})
AND V1.CRT_USER_ID = UPPER(#{crtUserId})
AND V1.PROMO_CODE = UPPER(#{promoCode})
AND V1.RELATED_NO = UPPER(#{relatedNo})
