) T ORDER BY T.ORD_NO ASC

/* com.coway.trust.biz.homecare.sales.order.impl.HcOrderListMapper.selectHcOrderInfo */ SELECT A.ORD_SEQ_NO , A.CUST_ID , A.SALES_DT , A.SALES_DT_STR , A.MAT_ORD_NO , A.FRA_ORD_NO , A.MAT_PRE_ORD_ID , A.FRA_PRE_ORD_ID , A.ORD_ID , A.ANO_ORD_NO , A.SRV_ORD_ID , A.PRODUCT_CODE , A.BNDL_NO , (SELECT SAL1.SALES_ORD_ID FROM SAL0001D SAL1 WHERE SAL1.SALES_ORD_NO = A.ANO_ORD_NO) AS ANO_ORD_ID , (SELECT SYS2.CODE FROM SYS0026M SYS1, SYS0013M SYS2 WHERE SYS1.STK_CTGRY_ID = SYS2.CODE_ID AND SYS1.STK_ID = A.PRODUCT_CODE) AS ORD_CTGRY_CD , B.ANO_ORD_APP_TYPE , b.ANO_ORD_CTGRY_CD FROM ( SELECT H1.ORD_SEQ_NO , H1.CUST_ID , H1.SALES_DT , TO_CHAR(H1.SALES_DT, 'MM/dd/YYYY') AS SALES_DT_STR , H1.MAT_ORD_NO , H1.FRA_ORD_NO , H1.MAT_PRE_ORD_ID , H1.FRA_PRE_ORD_ID , S1.SALES_ORD_ID AS ORD_ID , S2.ITM_STK_ID AS PRODUCT_CODE
, H1.FRA_ORD_NO AS ANO_ORD_NO
, DECODE(#{ordNo}, H1.FRA_ORD_NO, H1.MAT_ORD_NO, H1.MAT_ORD_NO, H1.FRA_ORD_NO) AS ANO_ORD_NO
, H1.SRV_ORD_ID , H1.BNDL_NO
FROM HMC0011D H1 , SAL0001D S1 , SAL0002D S2
WHERE H1.CUST_ID = S1.CUST_ID AND S1.SALES_ORD_ID = S2.SALES_ORD_ID
AND S1.SALES_ORD_ID = H1.SRV_ORD_ID AND H1.SRV_ORD_ID = #{srvOrdId}
AND S1.SALES_ORD_NO = #{ordNo} AND (H1.MAT_ORD_NO = #{ordNo} OR H1.FRA_ORD_NO = #{ordNo})
) A LEFT JOIN ( SELECT S.SALES_ORD_ID ANO_ORD_ID, S.SALES_ORD_NO ANO_ORD_NO, S.APP_TYPE_ID ANO_ORD_APP_TYPE, SC.CODE ANO_ORD_CTGRY_CD FROM SAL0001D S JOIN SAL0002D D ON D.SALES_ORD_ID = S.SALES_ORD_ID JOIN SYS0026M STK ON STK.STK_ID = D.ITM_STK_ID JOIN SYS0013M SC ON SC.CODE_ID = STK.STK_CTGRY_ID ) B ON B.ANO_ORD_NO = A.ANO_ORD_NO
