mapper.insert:
/* com.coway.trust.biz.homecare.sales.order.impl.HcOrderRegisterMapper.insertHcRegisterOrder */
INSERT INTO HMC0011D (
       ORD_SEQ_NO
     , CUST_ID
     , SALES_DT
     , MAT_ORD_NO
     , FRA_ORD_NO
     , CRT_DT
     , CRT_USER_ID
     , UPD_DT
     , UPD_USER_ID
     , MAT_PRE_ORD_ID      /* eKeyin Mat ord_id */
     , FRA_PRE_ORD_ID       /* eKeyin Fra Ord_id */
     , STUS_ID
     , BNDL_NO
     , SRV_ORD_ID

) VALUES (
       #{ordSeqNo}
     , #{custId}
     , SYSDATE
     , #{matOrdNo}
     , #{fraOrdNo}
     , SYSDATE
     , #{crtUserId}
     , SYSDATE
     , #{updUserId}
     , #{matPreOrdId}
     , #{fraPreOrdId}
     , #{stusId}
     , #{bndlNo}
     , #{srvOrdId}
)

mapper.update:
/* com.coway.trust.biz.homecare.sales.order.impl.HcOrderRegisterMapper.updateHcPreOrder */
UPDATE HMC0011D SET
              UPD_DT                  = SYSDATE
            , UPD_USER_ID           = #{updUserId}

            , CUST_ID                  = #{custId}


            , MAT_ORD_NO          = #{matOrdNo}       /* Mat ord_no */


            , FRA_ORD_NO           = #{fraOrdNo}         /* Fra Ord_no */


            , MAT_PRE_ORD_ID     = #{matPreOrdId}    /* eKeyin Mat ord_id */


            , FRA_PRE_ORD_ID      = #{fraPreOrdId}      /* eKeyin Fra Ord_id */


            , STUS_ID                  = #{stusId}


            , SRV_ORD_ID            = #{srvOrdId}

   WHERE ORD_SEQ_NO          = #{ordSeqNo}

mapper.select:
SELECT COUNT(*) FROM ( SELECT PROMO_CAN_ID FROM SAL0252M WHERE DISB = 0 AND SYSDATE BETWEEN EFF_DT AND EXP_DT
UNION
SELECT PROMO_CAN_ID FROM SAL0253D WHERE DISB = 0 AND SYSDATE BETWEEN EFF_DT AND EXP_DT ) WHERE PROMO_CAN_ID = #{promoNo}

SELECT COUNT(*)
FROM SAL0252M
WHERE PROD_CDE = #{prod}
  AND PROMO_ID = #{promoNo}
  AND DISB = 0
  AND SYSDATE BETWEEN EFF_DT AND EXP_DT

SELECT COUNT(*)
FROM SAL0253D
WHERE SUB_PROD_ID = #{prod}
  AND SUB_PROMO_ID = #{promoNo}
  AND DISB = 0
  AND SYSDATE BETWEEN EFF_DT AND EXP_DT

SELECT COUNT(*) FROM SAL0349D A LEFT JOIN ( SELECT SALES_ORD_ID, PCKAGE_BINDING_NO, CUST_ID, PROMO_ID, STUS_CODE_ID FROM SAL0001D WHERE PCKAGE_BINDING_NO IS NOT NULL AND PROMO_ID IN ( SELECT PROMO_ID FROM SAL0252M WHERE CONF_ID IN ( SELECT CONF_ID FROM SAL0252M WHERE DISB = 0 AND PROD_CDE = #{prod} AND PROMO_ID = #{promoNo}
UNION
SELECT CONF_ID FROM SAL0253D WHERE DISB = 0 AND SUB_PROD_ID = #{prod} AND SUB_PROMO_ID = #{promoNo} ) ) ) B ON B.SALES_ORD_ID = A.MAIN_ORD_ID AND B.CUST_ID = #{custId} WHERE B.CUST_ID = #{custId} AND B.STUS_CODE_ID IN (1, 4)

SELECT DISTINCT B.ORD_NO, B.ORD_ID, B.REF_NO, B.ORD_DT, B.APP_TYPE, B.PROD, B.PROMO_CDE, B.CUST_ID FROM SAL0349D A LEFT JOIN ( SELECT Z.SALES_ORD_NO AS ORD_NO, Z.SALES_ORD_ID AS ORD_ID, Z.PCKAGE_BINDING_NO, F.NAME AS CUST_ID, F.CUST_ID AS CID, Z.PROMO_ID, Z.STUS_CODE_ID, Z.REF_NO, TO_CHAR(Z.SALES_DT, 'DD/MM/YYYY') AS ORD_DT, G.CODE AS APP_TYPE, D.STK_DESC AS PROD, E.PROMO_CODE AS PROMO_CDE FROM SAL0001D Z JOIN SAL0002D C ON Z.SALES_ORD_ID = C.SALES_ORD_ID JOIN SYS0026M D ON C.ITM_STK_ID = D.STK_ID JOIN SAL0017D E ON Z.PROMO_ID = E.PROMO_ID JOIN SAL0029D F ON Z.CUST_ID = F.CUST_ID JOIN SYS0013M G ON G.CODE_MASTER_ID = '10' AND Z.APP_TYPE_ID = G.CODE_ID WHERE Z.PCKAGE_BINDING_NO IS NOT NULL AND Z.PROMO_ID IN ( SELECT PROMO_ID FROM SAL0252M WHERE CONF_ID IN ( SELECT CONF_ID FROM SAL0252M WHERE DISB = 0 AND PROD_CDE = #{prod} AND PROMO_ID = #{promoNo}
UNION
SELECT CONF_ID FROM SAL0253D WHERE DISB = 0 AND SUB_PROD_ID = #{prod} AND SUB_PROMO_ID = #{promoNo} ) ) ) B ON B.ORD_ID = A.MAIN_ORD_ID AND B.CID = #{custId} WHERE B.CID = #{custId} AND B.STUS_CODE_ID IN (1, 4)

SELECT COUNT(*) FROM SAL0349D A LEFT JOIN SAL0001D B ON B.SALES_ORD_ID = A.SUB_ORD_ID AND B.CUST_ID = #{custId} LEFT JOIN SAL0002D C ON C.SALES_ORD_ID = B.SALES_ORD_ID WHERE A.COMBO_GRP IN ( SELECT * FROM ( SELECT CASE WHEN (SELECT SUM(MAX_QTY) FROM SAL0253D WHERE SUB_PROD_ID = #{prod}) = PROD_MAX_LINK THEN 1 ELSE 0 END AS COMBO_GRP FROM SAL0252M WHERE DISB = 0 AND PROD_CAT = 'ACI' AND PROD_CDE = #{prod} AND PROMO_ID = #{promoNo}
UNION
SELECT COMBO_GRP FROM SAL0253D WHERE SUB_PROMO_ID = #{promoNo} AND SUB_PROD_ID = #{prod} AND SUB_PROD_CAT = 'ACI' AND DISB = 0 ) ) AND DISB = 0
AND A.MAIN_ORD_ID = #{ordId}

SELECT A.SALES_ORD_ID, A.SALES_ORD_NO, A.REF_NO REF_NO, A.APP_TYPE_ID, C.CODE_NAME APP_TYPE, D.STK_CODE, D.STK_DESC, A.STUS_CODE_ID, E.NAME STATUS, TO_CHAR(MAX(F.SRV_START_DT), 'DD/MM/YYYY') SRV_START_DT, TO_CHAR(MAX(F.SRV_EXPR_DT), 'DD/MM/YYYY') SRV_EXPR_DT FROM SAL0001D A LEFT JOIN SAL0002D B ON B.SALES_ORD_ID = A.SALES_ORD_ID LEFT JOIN SYS0013M C ON C.CODE_ID = A.APP_TYPE_ID LEFT JOIN SYS0026M D ON D.STK_ID = B.ITM_STK_ID LEFT JOIN SYS0038M E ON E.STUS_CODE_ID = A.STUS_CODE_ID LEFT JOIN SAL0095D F ON F.SRV_SALES_ORD_ID = A.SALES_ORD_ID AND F.SRV_STUS_CODE_ID = 4 WHERE A.CUST_ID = #{custId}
AND A.SALES_ORD_NO = #{salesOrdNo}
GROUP BY A.SALES_ORD_ID, A.SALES_ORD_NO, A.APP_TYPE_ID, C.CODE_NAME, D.STK_CODE, D.STK_DESC, A.STUS_CODE_ID, E.NAME, A.REF_NO

/* com.coway.trust.biz.homecare.sales.order.impl.HcOrderRegisterMapper.selectPromotionByFrame */ SELECT DISTINCT T2.PROMO_ID CODE , T2.PROMO_CODE || '-' || T2.PROMO_DESC CODE_NAME
FROM SAL0018D T1 JOIN SAL0017D T2 ON T2.PROMO_ID = T1.PROMO_ID
WHERE T2.PROMO_ID in (select code from sys0013m where code_master_id = 587 )

SELECT MAX(SUB_ORD_ID) AS ORDER_ID FROM SAL0349D WHERE DISB = 0
AND MAIN_ORD_ID = #{ordId}
GROUP BY MAIN_ORD_ID

SELECT CASE WHEN MAX(COMBO_GRP) <= 2 THEN MAX(COMBO_GRP) + 1 ELSE MAX(COMBO_GRP) END AS NEXT_COMBO_GRP FROM SAL0349D WHERE DISB = 0
AND MAIN_ORD_ID = #{ordId}
GROUP BY MAIN_ORD_ID

SELECT COMBO_GRP
FROM (
               SELECT CASE WHEN (SELECT SUM(MAX_QTY) FROM SAL0253D WHERE SUB_PROD_ID = #{prod}) = PROD_MAX_LINK THEN 1
               ELSE 0 END AS COMBO_GRP
               FROM SAL0252M
               WHERE DISB = 0
               AND PROD_CAT =  'ACI'
               AND PROD_CDE = #{prod}
               AND PROMO_ID = #{promoNo}

              UNION

              SELECT COMBO_GRP
              FROM SAL0253D
              WHERE SUB_PROMO_ID = #{promoNo}
              AND SUB_PROD_ID = #{prod}
              AND SUB_PROD_CAT = 'ACI'
)
