mapper.select:
SELECT * FROM ( SELECT T1.PRE_BOOK_ID , T1.PRE_BOOK_NO , TO_CHAR(T1.PRE_BOOK_DT, 'DD/MM/YYYY') AS PRE_BOOK_DT , T1.CUST_VERIFY_STUS , T1.MEM_CODE , T1.CUST_ID , T2.NAME AS CUST_NAME , T1.SALES_ORD_ID_OLD , T3.STK_DESC , T4.SALES_ORD_NO , T5.ITM_STK_ID, T6.STK_DESC AS PREV_STK_DESC , T7.CODE AS STUS_CODE , T1.DISC_WAIVE , T4.APP_TYPE_ID ,CASE WHEN T1.CUST_VERIFY_STUS = 'Y' THEN (TO_CHAR(ADD_MONTHS(T1.PRE_BOOK_DT, T1.DISC_WAIVE), 'MM/YYYY')) ELSE '' END AS CONVERT_MTH FROM SAL0404M T1 LEFT JOIN SAL0029D T2 ON T1.CUST_ID = T2.CUST_ID JOIN SYS0026M T3 ON T1.STK_ID = T3.STK_ID LEFT JOIN SAL0001D T4 ON T1.SALES_ORD_ID_OLD = T4.SALES_ORD_ID LEFT JOIN SAL0002D T5 ON T4.SALES_ORD_ID = T5.SALES_ORD_ID LEFT JOIN SYS0026M T6 ON T5.ITM_STK_ID = T6.STK_ID LEFT JOIN SYS0038M T7 ON T1.STUS_ID = T7.STUS_CODE_ID LEFT JOIN ORG0001D T8 ON T1.MEM_CODE = T8.MEM_CODE LEFT JOIN ORG1001V T9 ON T8.MEM_ID = T9.MEM_ID
WHERE T3.STK_CTGRY_ID IN (SELECT AA.CODE_ID FROM SYS0013M AA, SYS0094M BB WHERE AA.CODE = BB.CODE AND AA.CODE_MASTER_ID = 11 AND BB.IND = 'HOMECARE')
AND T1.PRE_BOOK_NO = #{_ordNo}
AND T1.MEM_CODE = #{_memCode}
AND T1.DISC_WAIVE IN
#{item, jdbcType=VARCHAR}
AND T1.CUST_VERIFY_STUS = #{verifyStatus}
AND T1.PRE_BOOK_DT BETWEEN TO_DATE(#{_reqstStartDt} || #{_reqstStartTime}, 'dd/mm/yyyy hh24:mi') AND TO_DATE(#{_reqstEndDt} || #{_reqstEndTime}, 'dd/mm/yyyy hh24:mi')
AND T2.NRIC = #{_nric}
AND T2.NAME LIKE #{_name}||'%'
AND T7.CODE IN
#{item, jdbcType=VARCHAR}
AND T4.APP_TYPE_ID IN
#{item}
AND T9.ORG_CODE = #{orgCode}
AND T9.GRP_CODE = #{grpCode}
AND T9.DEPT_CODE = #{deptCode}
AND T4.SALES_ORD_NO = #{salesOrdNo}
ORDER BY T1.PRE_BOOK_ID ASC )
WHERE CONVERT_MTH = #{eliConvertMth}

