/* com.coway.trust.biz.homecare.sales.order.impl.HcPreOrderMapper.selectHcPreOrderInfo */ SELECT H1.ORD_SEQ_NO , H1.CUST_ID , H1.SALES_DT , TO_CHAR(H1.SALES_DT, 'MM/dd/YYYY') AS SALES_DT_STR , H1.MAT_PRE_ORD_ID , H1.FRA_PRE_ORD_ID , DECODE(#{preOrdId}, H1.FRA_PRE_ORD_ID, H1.MAT_PRE_ORD_ID, H1.MAT_PRE_ORD_ID, H1.FRA_PRE_ORD_ID) AS ANO_PRE_ORD_ID , (SELECT SYS2.CODE FROM SYS0026M SYS1, SYS0013M SYS2 WHERE SYS1.STK_CTGRY_ID = SYS2.CODE_ID AND SYS1.STK_ID = T1.ITM_STK_ID) AS ORD_CTGRY_CD
FROM HMC0011D H1 , SAL0213M T1
WHERE T1.PRE_ORD_ID = #{preOrdId} AND (H1.MAT_PRE_ORD_ID = #{preOrdId} OR H1.FRA_PRE_ORD_ID = #{preOrdId})

