mapper.select:
SELECT MAX(MEM_ACC_ID) AS MEM_ACC_ID
  FROM SYS0047M
 WHERE USER_ID = #{sUserId}

/* [com.coway.trust.biz.homecare.po.impl.HcConfirmPoMapper.selectHcConfirmPoMainListCnt] - KR JIN */
    SELECT COUNT(1) AS CNT
      FROM HMC0004M A
     WHERE 1 = 1
       AND A.USE_YN = 'Y'
       AND A.PO_STS_CD IN ( SELECT CODE_ID FROM SYS0013M
                             WHERE CODE_MASTER_ID = '429'
                               AND CODE IN ('30')
                          )
   
       AND A.CDC = #{sCdc}
   
   
       AND A.APPR_DT >= TO_DATE(#{sPoDtFrom}, 'YYYYMMDD')
   
   
       AND A.APPR_DT <= TO_DATE(#{sPoDtTo}, 'YYYYMMDD') + 1
   
   
      AND A.MEM_ACC_ID = #{sMemAccId}
   
   
       AND 1 = 2
   
   
      AND A.PO_TY_CD = #{sPoTyCd}
   
   
      AND A.PO_STS_CD = #{sPoStsCd}
   
   
      AND A.SUPP_STS_CD = #{sSuppStsCd}
   
   
      AND A.PO_NO = #{sPoNo}

/* [com.coway.trust.biz.homecare.po.impl.HcConfirmPoMapper.selectHcConfirmPoMainList] - KR JIN */ SELECT * FROM ( SELECT ROWNUM AS RNUM, T1.* FROM (
SELECT A.PO_NO , A.PO_TY_CD , (SELECT CODE_NAME FROM SYS0013M WHERE CODE_MASTER_ID = '428' AND CODE_ID = A.PO_TY_CD) AS PO_TY_NAME , A.CDC , B.CDC_TEXT , A.PO_STS_CD , (SELECT CODE FROM SYS0013M WHERE CODE_MASTER_ID = '429' AND CODE_ID = A.PO_STS_CD) AS PO_STS_CODE , (SELECT CODE_NAME FROM SYS0013M WHERE CODE_MASTER_ID = '429' AND CODE_ID = A.PO_STS_CD) AS PO_STS_NAME , A.SUPP_STS_CD , (SELECT CODE FROM SYS0013M WHERE CODE_MASTER_ID = '438' AND CODE_ID = A.SUPP_STS_CD) AS SUPP_STS_CODE , (SELECT CODE_NAME FROM SYS0013M WHERE CODE_MASTER_ID = '438' AND CODE_ID = A.SUPP_STS_CD) AS SUPP_STS_NAME , A.MEM_ACC_ID , (SELECT MEM_ACC_NAME FROM FCM0006M WHERE MEM_ACC_ID = A.MEM_ACC_ID) AS MEM_ACC_NAME , A.APPR_DT AS APPR_DT , TO_DATE(A.CDC_DUE_DT, 'YYYYMMDD') AS CDC_DUE_DT , B.ADDRESS AS ADDRESS , B.TEL_NO AS TEL , A.USE_YN , A.RM , A.CRT_DT , A.CRT_USER_ID FROM HMC0004M A , ( SELECT A.WH_LOC_ID AS CDC , A.WH_LOC_DESC AS CDC_TEXT , B.STATE ||' '|| B.CITY ||' '|| B.AREA ||' '|| A.STREET ||' '|| A.ADDR_DTL AS ADDRESS , NVL(A.WH_LOC_TEL1, A.WH_LOC_TEL2 ) AS TEL_NO FROM SYS0028M A , SYS0064M B WHERE A.AREA_ID = B.AREA_ID(+) AND A.WH_LOC_CODE IN ('3010', '3020', '3030') ) B -- CDC MASTER WHERE 1 = 1 AND A.CDC = B.CDC (+) AND A.USE_YN = 'Y' AND A.PO_STS_CD IN ( SELECT CODE_ID FROM SYS0013M WHERE CODE_MASTER_ID = '429' AND CODE IN ('30') )
AND A.CDC = #{sCdc}
AND A.APPR_DT >= TO_DATE(#{sPoDtFrom}, 'YYYYMMDD')
AND A.APPR_DT <= TO_DATE(#{sPoDtTo}, 'YYYYMMDD') + 1
AND 1 = 2
AND A.MEM_ACC_ID = #{sMemAccId}
AND A.PO_TY_CD = #{sPoTyCd}
AND A.PO_STS_CD = #{sPoStsCd}
AND A.SUPP_STS_CD = #{sSuppStsCd}
AND A.PO_NO = #{sPoNo}
ORDER BY
A.PO_NO ASCDESC A.PO_TY_CD ASCDESC PO_TY_NAME ASCDESC A.CDC ASCDESC B.CDC_TEXT ASCDESC A.PO_STS_CD ASCDESC PO_STS_CODE ASCDESC PO_STS_NAME ASCDESC A.MEM_ACC_ID ASCDESC MEM_ACC_NAME ASCDESC APPR_DT ASCDESC A.CDC_DUE_DT ASCDESC ADDRESS ASCDESC B.TEL_NO ASCDESC A.USE_YN ASCDESC A.RM ASCDESC A.CRT_DT ASCDESC A.CRT_USER_ID ASCDESC
A.UPD_DT DESC
A.UPD_DT DESC
) T1
ROWNUM <= #{lastIndex}
)
RNUM > #{firstIndex}

mapper.update:
/* [com.coway.trust.biz.homecare.po.impl.HcConfirmPoMapper.updateConfirmPoStat] - KR JIN */
    UPDATE HMC0004M A
       SET A.SUPP_STS_CD = #{suppStsCd}
         , A.rm = (SELECT Z.RM || #{sRm} FROM HMC0004M Z  WHERE Z.PO_NO = A.PO_NO)
         , A.UPD_USER_ID = #{updUserId}
         , A.UPD_DT  = SYSDATE
     WHERE A.PO_NO = #{poNo}

/* [com.coway.trust.biz.homecare.po.impl.HcConfirmPoMapper.updateConfirmPoDetail] - KR JIN */
    UPDATE HMC0005D
       SET CONFIRM_QTY = PO_QTY
         , UPD_DT = SYSDATE
         , UPD_USER_ID = #{updUserId}
     WHERE PO_NO = #{poNo}
