/* [com.coway.trust.biz.homecare.po.impl.HcCreateDeliveryMapper.selectPoList] - KR JIN */ SELECT * FROM ( SELECT ROWNUM AS RNUM, T1.* FROM ( SELECT A.CDC , b.CDC_TEXT AS CDC_NAME , A.PO_NO , A.PO_TY_CD , (SELECT CODE_NAME FROM SYS0013M WHERE CODE_MASTER_ID = '428' AND CODE_ID = A.PO_TY_CD) AS PO_TY_NAME , A.PO_STS_CD , (SELECT CODE FROM SYS0013M WHERE CODE_MASTER_ID = '429' AND CODE_ID = A.PO_STS_CD) AS PO_STS_CODE , (SELECT CODE_NAME FROM SYS0013M WHERE CODE_MASTER_ID = '429' AND CODE_ID = A.PO_STS_CD) AS PO_STS_NAME , A.SUPP_STS_CD , (SELECT CODE FROM SYS0013M WHERE CODE_MASTER_ID = '438' AND CODE_ID = A.SUPP_STS_CD) AS SUPP_STS_CODE , (SELECT CODE_NAME FROM SYS0013M WHERE CODE_MASTER_ID = '438' AND CODE_ID = A.SUPP_STS_CD) AS SUPP_STS_NAME , A.MEM_ACC_ID , (SELECT MEM_ACC_NAME FROM FCM0006M WHERE MEM_ACC_ID = A.MEM_ACC_ID) AS MEM_ACC_NAME , A.APPR_DT AS PO_DT , TO_DATE(A.CDC_DUE_DT, 'YYYYMMDD') AS CDC_DUE_DT , B.ADDRESS AS ADDRESS , B.TEL , A.RM FROM HMC0004M A , ( SELECT A.WH_LOC_ID AS CDC , A.WH_LOC_DESC AS CDC_TEXT , B.STATE ||' '|| B.CITY ||' '|| B.AREA ||' '|| A.STREET ||' '|| A.ADDR_DTL AS ADDRESS , NVL(A.WH_LOC_TEL1, A.WH_LOC_TEL2 ) AS TEL FROM SYS0028M A , SYS0064M B WHERE A.AREA_ID = B.AREA_ID(+) AND A.WH_LOC_CODE IN ('3010', '3020', '3030') ) B -- CDC MASTER WHERE A.CDC = B.CDC (+) AND A.PO_STS_CD = (SELECT CODE_ID FROM SYS0013M WHERE CODE_MASTER_ID = '429' AND CODE = '30') AND A.SUPP_STS_CD = (SELECT CODE_ID FROM SYS0013M WHERE CODE_MASTER_ID = '438' AND CODE = '20') AND A.PO_TY_CD = (SELECT CODE_ID FROM SYS0013M WHERE CODE_MASTER_ID = '428' AND CODE = 'PO') AND A.USE_YN = 'Y'
AND A.CDC = #{sCdc}
AND A.APPR_DT >= TO_DATE(#{sPoDtFrom}, 'YYYYMMDD')
AND A.APPR_DT <= TO_DATE(#{sPoDtTo}, 'YYYYMMDD') + 1
AND A.MEM_ACC_ID = #{sMemAccId}
AND 1 = 2
AND A.PO_NO = #{sPoNo}
ORDER BY
CDC ASCDESC CDC_NAME ASCDESC A.PO_NO ASCDESC A.PO_TY_CD ASCDESC PO_TY_NAME ASCDESC A.PO_STS_CD ASCDESC PO_STS_CODE ASCDESC PO_STS_NAME ASCDESC A.SUPP_STS_CD ASCDESC SUPP_STS_CODE ASCDESC SUPP_STS_NAME ASCDESC MEM_ACC_ID ASCDESC MEM_ACC_NAME ASCDESC PO_DT ASCDESC CDC_DUE_DT ASCDESC ADDRESS ASCDESC B.TEL ASCDESC A.RM ASCDESC
PO_NO DESC
PO_NO DESC
) T1
ROWNUM <= #{lastIndex}
)
RNUM > #{firstIndex}

