mapper.select:
/* [com.coway.trust.biz.homecare.po.impl.HcDeliveryGrSearchPopMapper.selectDeliveryGrSearchPop] - KR JIN */ SELECT B.CDC , A.HMC_DELVRY_NO , (SELECT SUM(X.DELVRY_QTY) FROM HMC0008D X WHERE X.HMC_DELVRY_NO = A.HMC_DELVRY_NO GROUP BY X.HMC_DELVRY_NO ) AS DELVRY_QTY , A.DELVRY_GI_DT , A.PO_NO from HMC0007M A , HMC0004M B WHERE A.PO_NO = B.PO_NO AND DELVRY_STATUS = (SELECT CODE_ID FROM SYS0013M WHERE CODE_MASTER_ID = '432' AND DISAB = '0' AND CODE = '20') AND A.USE_YN = 'Y' AND B.USE_YN = 'Y'
AND B.CDC = #{pCdc}
AND B.MEM_ACC_ID = #{sMemAccId}
AND A.DELVRY_GI_DT >= TO_DATE(#{sDlvGiDtFrom}, 'YYYYMMDD')
AND A.DELVRY_GI_DT <= TO_DATE(#{sDlvGiDtTo}, 'YYYYMMDD') + 1
AND A.HMC_DELVRY_NO = #{sHmcDelvryNo}
AND A.PO_NO = #{sPoNo}
