mapper.select:
/* [com.coway.trust.biz.homecare.po.impl.HcDeliveryGrMapper.selectDeliveryGrMainCnt] - KR JIN */
    SELECT COUNT(1) AS CNT
      FROM HMC0009M A       -- GR
         , HMC0010D B
         , HMC0007M C
     WHERE A.HMC_GR_NO = B.HMC_GR_NO
       AND A.PO_NO = B.PO_NO
       AND A.HMC_DELVRY_NO = B.HMC_DELVRY_NO
       AND A.PO_NO = C.PO_NO
       AND A.HMC_DELVRY_NO = C.HMC_DELVRY_NO
       AND A.USE_YN = 'Y'
       AND C.DELVRY_STATUS = '5652'
       AND C.USE_YN = 'Y'
     
       AND A.CDC = #{sCdc}
     
     
       AND C.DELVRY_GI_DT >= #{sDlvGiDtFrom}
     
     
       AND C.DELVRY_GI_DT <= to_char(TO_DATE(#{sDlvGiDtTo}, 'YYYYMMDD') + 1, 'YYYYMMDD')
     
     
       AND C.MEM_ACC_ID = #{sMemAccId}
     
     
         AND 1 = 2
     
     
       AND A.PO_NO = #{sPoNo}
     
     
       AND A.HMC_DELVRY_NO = #{sHmcDelvryNo}
     
     
       AND A.GR_YN = #{sGrYn}

/* [com.coway.trust.biz.homecare.po.impl.HcDeliveryGrMapper.selectDeliveryGrMain] - KR JIN */ SELECT * FROM ( SELECT ROWNUM AS RNUM, T1.* FROM (
SELECT B.HMC_GR_NO , B.HMC_GR_NO_DTL_NO , B.HMC_DELVRY_NO , B.HMC_DELVRY_NO_DTL_NO , B.PO_NO , B.PO_DTL_NO , B.STOCK_ID , B.STOCK_CODE , (SELECT STK_DESC FROM SYS0026M WHERE STK_ID = B.STOCK_ID AND STK_CODE = B.STOCK_CODE) AS STOCK_NAME , NVL(A.GR_YN, 'N') AS GR_YN , CASE WHEN NVL(A.GR_YN, 'N') = 'N' THEN ( SELECT COUNT(1) FROM ( SELECT ROW_NUMBER() OVER (PARTITION BY SERIAL_NO ORDER BY SCAN_NO DESC ) AS RN , REQST_NO , REQST_NO_ITM , ITM_CODE FROM LOG0099D ) Z WHERE Z.REQST_NO = B.HMC_GR_NO AND Z.REQST_NO_ITM = B.HMC_GR_NO_DTL_NO AND Z.ITM_CODE = B.STOCK_CODE AND Z.RN = 1 ) ELSE B.RCIPT_QTY END AS RCIPT_QTY , B.QC_FAIL_QTY , A.GR_DT FROM HMC0009M A -- GR , HMC0010D B , HMC0007M C WHERE A.HMC_GR_NO = B.HMC_GR_NO AND A.PO_NO = B.PO_NO AND A.HMC_DELVRY_NO = B.HMC_DELVRY_NO AND A.PO_NO = C.PO_NO AND A.HMC_DELVRY_NO = C.HMC_DELVRY_NO AND A.USE_YN = 'Y' AND C.DELVRY_STATUS = '5652' AND C.USE_YN = 'Y'
AND A.CDC = #{sCdc}
AND C.DELVRY_GI_DT >= #{sDlvGiDtFrom}
AND C.DELVRY_GI_DT <= TO_CHAR(TO_DATE(#{sDlvGiDtTo}, 'YYYYMMDD') + 1, 'YYYYMMDD')
AND C.MEM_ACC_ID = #{sMemAccId}
AND 1 = 2
AND A.PO_NO = #{sPoNo}
AND A.HMC_DELVRY_NO = #{sHmcDelvryNo}
AND A.GR_YN = #{sGrYn}
ORDER BY
B.HMC_GR_NO ASCDESC
B.HMC_GR_NO DESC
B.HMC_GR_NO DESC
) T1
ROWNUM <= #{lastIndex}
)
RNUM > #{firstIndex}

mapper.insert:
/* [com.coway.trust.biz.homecare.po.impl.HcDeliveryGrMapper.insertDeliveryGrHeader] - KR JIN */
SELECT 'HGR'||TO_CHAR(SYSDATE, 'YYMMDD')|| LPAD(NVL(MAX(TO_NUMBER(SUBSTR(HMC_GR_NO, 10))), 0)+1, 5, '0') AS HMC_GR_NO FROM HMC0009M WHERE HMC_GR_NO LIKE 'HGR'||TO_CHAR(SYSDATE, 'YYMMDD') || '%'
INSERT INTO HMC0009M ( HMC_GR_NO --HomeCare GR Number , PO_NO --PO No , HMC_DELVRY_NO --Delivery Number , GR_YN --SAVE : N, GR : Y , GR_DT --GR Date , CDC --CDC , RM --Remark , PO_SETTL_STATUS --PO Settlement Status 443 , USE_YN --USE_YN , CRT_DT --Create Date , CRT_USER_ID --Create User Id , UPD_DT --Update Date , UPD_USER_ID --Update User Id )VALUES( #{hmcGrNo} --HomeCare GR Number , #{poNo} --PO No , #{hmcDelvryNo} --Delivery Number , 'N' , NULL , #{cdc} --CDC , NULL , NULL , 'Y' , SYSDATE , #{crtUserId} --Create User Id , SYSDATE , #{updUserId} --Update User Id )

mapper.select:
/* [com.coway.trust.biz.homecare.po.impl.HcDeliveryGrMapper.selectDeliveryConfirm] - KR JIN */ SELECT CASE WHEN Z.SERIAL_CHK = 'N' THEN 0 ELSE ( SELECT COUNT(1) FROM ( SELECT ROW_NUMBER() OVER (PARTITION BY SERIAL_NO ORDER BY SCAN_NO DESC ) AS RN , REQST_NO , ITM_CODE FROM LOG0099D ) Z1 WHERE Z1.REQST_NO = Z.ING_GR_NO AND Z1.ITM_CODE = Z.STOCK_CODE AND Z1.RN = 1 ) END AS RCIPT_TM_QTY , Z.* FROM ( SELECT COUNT(1) OVER () AS ITEM_COUNT , B.STOCK_ID , B.STOCK_CODE , D.STK_DESC AS STOCK_NAME , DECODE(D.SERIAL_CHK, 'Y', 'Y', 'N') AS SERIAL_CHK , D.STK_TYPE_ID , B.DELVRY_QTY , B.PO_NO , B.PO_DTL_NO , B.HMC_DELVRY_NO , B.HMC_DELVRY_NO_DTL_NO , C.CDC , NVL(C.RCIPT_GR_QTY, 0) AS RCIPT_GR_QTY , NVL(C.FAIL_GR_QTY, 0) AS FAIL_GR_QTY
, NVL(C.FAIL_TM_QTY, 0) AS FAIL_TM_QTY , CASE WHEN C.ING_GR_NO IS NULL THEN (SELECT MAX(Z.HMC_GR_NO) FROM HMC0009M Z WHERE Z.HMC_DELVRY_NO = A.HMC_DELVRY_NO AND Z.PO_NO = A.PO_NO AND Z.USE_YN = 'Y' AND Z.GR_YN = 'N' ) ELSE C.ING_GR_NO END AS ING_GR_NO
FROM HMC0007M A , HMC0008D B , ( SELECT A.HMC_DELVRY_NO , B.HMC_DELVRY_NO_DTL_NO , A.PO_NO , B.PO_DTL_NO , B.STOCK_ID , B.STOCK_CODE , MAX(A.CDC) AS CDC , SUM(CASE WHEN A.GR_YN = 'Y' THEN B.RCIPT_QTY ELSE 0 END) AS RCIPT_GR_QTY , SUM(CASE WHEN A.GR_YN = 'Y' THEN B.QC_FAIL_QTY ELSE 0 END) AS FAIL_GR_QTY , SUM(CASE WHEN A.GR_YN = 'N' THEN B.RCIPT_QTY ELSE 0 END) AS RCIPT_TM_QTY , SUM(CASE WHEN A.GR_YN = 'N' THEN B.QC_FAIL_QTY ELSE 0 END) AS FAIL_TM_QTY , (SELECT MAX(Z.HMC_GR_NO) FROM HMC0009M Z WHERE Z.HMC_DELVRY_NO = A.HMC_DELVRY_NO AND Z.USE_YN = 'Y' AND Z.GR_YN = 'N' ) AS ING_GR_NO FROM HMC0009M A , HMC0010D B WHERE A.HMC_GR_NO = B.HMC_GR_NO (+) AND A.PO_NO = B.PO_NO (+) AND A.HMC_DELVRY_NO = B.HMC_DELVRY_NO (+) AND A.USE_YN = 'Y' AND B.USE_YN (+) = 'Y' AND A.HMC_DELVRY_NO = #{hmcDelvryNo} GROUP BY A.PO_NO, B.PO_DTL_NO, A.HMC_DELVRY_NO, B.HMC_DELVRY_NO_DTL_NO, B.STOCK_ID, B.STOCK_CODE ) C , SYS0026M D WHERE A.HMC_DELVRY_NO = B.HMC_DELVRY_NO AND A.PO_NO = B.PO_NO AND B.PO_NO = C.PO_NO (+) AND B.PO_DTL_NO = C.PO_DTL_NO (+) AND B.HMC_DELVRY_NO = C.HMC_DELVRY_NO (+) AND B.HMC_DELVRY_NO_DTL_NO = C.HMC_DELVRY_NO_DTL_NO (+) AND B.STOCK_ID = C.STOCK_ID (+) AND B.STOCK_CODE = C.STOCK_CODE (+) AND B.STOCK_ID = D.STK_ID (+) AND B.STOCK_CODE = D.STK_CODE (+) AND A.HMC_DELVRY_NO = #{hmcDelvryNo} AND A.USE_YN = 'Y' AND B.USE_YN = 'Y' ) Z

/* [com.coway.trust.biz.homecare.po.impl.HcDeliveryGrMapper.selectGrHeaderCnt] - KR JIN */
