/* [com.coway.trust.biz.homecare.po.impl.HcDeliveryGrMapper.selectItemSerialChkYn] - KR JIN */
    SELECT DECODE(MAX(SERIAL_CHK), 'Y', 'Y', 'N') AS SERIAL_CHK
         , MAX(STK_TYPE_ID) AS STK_TYPE_ID
      FROM SYS0026M
     WHERE 1 = 1
       AND STK_ID = #{stockId}
       AND STK_CODE = #{stockCode}

