mapper.select:
    /* [com.coway.trust.biz.homecare.po.impl.HcDeliveryPlanMapper.selectHcDeliveryPlanMainListCnt] - KR JIN */
	    SELECT NVL(SUM(1), 0) AS CNT
	      FROM (
	            SELECT (SELECT DECODE(MAX(Z.PO_DTL_PLAN_NO), NULL, 'N', 'Y') FROM HMC0006D Z WHERE Z.PO_NO = A.PO_NO) AS PLAN_YN
	              FROM HMC0004M A
	             WHERE 1 = 1
	               AND A.SUPP_STS_CD = (SELECT CODE_ID FROM SYS0013M WHERE CODE_MASTER_ID = '438' AND CODE = '20')
	               AND A.PO_TY_CD  = (SELECT CODE_ID FROM SYS0013M WHERE CODE_MASTER_ID = '428' AND CODE = 'PO')
	               AND A.USE_YN = 'Y'
	             
	               AND A.CDC = #{sCdc}
	             
	             
	               AND A.APPR_DT >= TO_DATE(#{sPoDtFrom}, 'YYYYMMDD')
	             
	             
	               AND A.APPR_DT <= TO_DATE(#{sPoDtTo}, 'YYYYMMDD') + 1
	             
	             
	               AND A.MEM_ACC_ID = #{sMemAccId}
	             
	             
	               AND A.PO_NO = #{sPoNo}
	             
	          ) X
	    WHERE 1 = 1
	    
	      AND X.PLAN_YN = #{sPlanYn}

/* [com.coway.trust.biz.homecare.po.impl.HcDeliveryPlanMapper.selectHcDeliveryPlanMainList] - KR JIN */ SELECT * FROM ( SELECT ROWNUM AS RNUM, T1.* FROM (
SELECT * FROM ( SELECT A.CDC , B.CDC_TEXT AS CDC_NAME , A.PO_NO , A.PO_TY_CD , (SELECT CODE_NAME FROM SYS0013M WHERE CODE_MASTER_ID = '428' AND CODE_ID = A.PO_TY_CD) AS PO_TY_NAME , A.PO_STS_CD , (SELECT CODE FROM SYS0013M WHERE CODE_MASTER_ID = '429' AND CODE_ID = A.PO_STS_CD) AS PO_STS_CODE , (SELECT CODE_NAME FROM SYS0013M WHERE CODE_MASTER_ID = '429' AND CODE_ID = A.PO_STS_CD) AS PO_STS_NAME , A.SUPP_STS_CD , (SELECT CODE FROM SYS0013M WHERE CODE_MASTER_ID = '438' AND CODE_ID = A.SUPP_STS_CD) AS SUPP_STS_CODE , (SELECT CODE_NAME FROM SYS0013M WHERE CODE_MASTER_ID = '438' AND CODE_ID = A.SUPP_STS_CD) AS SUPP_STS_NAME , A.MEM_ACC_ID , (SELECT MEM_ACC_NAME FROM FCM0006M WHERE MEM_ACC_ID = A.MEM_ACC_ID) AS MEM_ACC_NAME , A.APPR_DT AS PO_DT , TO_DATE(A.CDC_DUE_DT, 'YYYYMMDD') AS CDC_DUE_DT , B.ADDRESS AS ADDRESS , B.TEL , A.RM , (SELECT DECODE(MAX(Z.PO_DTL_PLAN_NO), NULL, 'N', 'Y') FROM HMC0006D Z WHERE Z.PO_NO = A.PO_NO) AS PLAN_YN FROM HMC0004M A , ( SELECT A.WH_LOC_ID AS CDC , A.WH_LOC_DESC AS CDC_TEXT , B.STATE ||' '|| B.CITY ||' '|| B.AREA ||' '|| A.STREET ||' '|| A.ADDR_DTL AS ADDRESS , NVL(A.WH_LOC_TEL1, A.WH_LOC_TEL2 ) AS TEL FROM SYS0028M A , SYS0064M B WHERE A.AREA_ID = B.AREA_ID(+) AND A.WH_LOC_CODE IN ('3010', '3020', '3030') ) B -- CDC MASTER WHERE A.CDC = B.CDC (+) AND A.SUPP_STS_CD = (SELECT CODE_ID FROM SYS0013M WHERE CODE_MASTER_ID = '438' AND CODE = '20') AND A.PO_TY_CD = (SELECT CODE_ID FROM SYS0013M WHERE CODE_MASTER_ID = '428' AND CODE = 'PO') AND A.USE_YN = 'Y'
AND A.CDC = #{sCdc}
AND A.APPR_DT >= TO_DATE(#{sPoDtFrom}, 'YYYYMMDD')
AND A.APPR_DT <= TO_DATE(#{sPoDtTo}, 'YYYYMMDD') + 1
AND A.MEM_ACC_ID = #{sMemAccId}
AND A.PO_NO = #{sPoNo}
) X WHERE 1 = 1
AND X.PLAN_YN = #{sPlanYn}
ORDER BY
CDC ASCDESC CDC_NAME ASCDESC X.PO_NO ASCDESC X.PO_TY_CD ASCDESC PO_TY_NAME ASCDESC X.PO_STS_CD ASCDESC PO_STS_CODE ASCDESC PO_STS_NAME ASCDESC X.SUPP_STS_CD ASCDESC SUPP_STS_CODE ASCDESC SUPP_STS_NAME ASCDESC MEM_ACC_ID ASCDESC MEM_ACC_NAME ASCDESC PO_DT ASCDESC CDC_DUE_DT ASCDESC ADDRESS ASCDESC B.TEL ASCDESC X.RM ASCDESC PLAN_YN ASCDESC
PO_NO DESC
PO_NO DESC
) T1
ROWNUM <= #{lastIndex}
)
RNUM > #{firstIndex}

mapper.delete:
/* [com.coway.trust.biz.homecare.po.impl.HcDeliveryPlanMapper.deleteHcPoPlan] - KR JIN */
    DELETE FROM HMC0006D
     WHERE PO_NO = #{poNo}

mapper.insert:
    /* [com.coway.trust.biz.homecare.po.impl.HcDeliveryPlanMapper.insertHcPoPlan] - KR JIN */
       INSERT
         INTO HMC0006D
       (
			  PO_NO
			, PO_DTL_NO
			, PO_DTL_PLAN_NO
			, PO_PLAN_QTY
			, PO_PLAN_DT
			, ACTUAL_QTY
			, CRT_DT
			, CRT_USER_ID
			, UPD_DT
			, UPD_USER_ID
       )VALUES(
              #{poNo}
            , #{poDtlNo}
            , (SELECT NVL(MAX(PO_DTL_PLAN_NO), 0)+1 FROM HMC0006D WHERE PO_NO = #{poNo} AND PO_DTL_NO = #{poDtlNo})
            , #{poPlanQty}
            , TO_CHAR(TO_DATE(#{poPlanDt}, 'YYYY/MM/DD'), 'YYYYMMDD')
            , #{actualQty}
            , SYSDATE
            , #{crtUserId}
            , SYSDATE
            , #{updUserId}
       )
