mapper.select:
/* [com.coway.trust.biz.homecare.po.impl.HcPoIssueMapper.selectHcPoIssueMainListCnt] - KR JIN */
    SELECT COUNT(1) AS CNT
      FROM HMC0004M A
     WHERE 1 = 1
       AND A.USE_YN = 'Y'
   
       AND A.CDC = #{sCdc}
   
   
       AND A.PO_DT >= #{sPoDtFrom}
   
   
       AND A.PO_DT <= #{sPoDtTo}
   
   
      AND A.MEM_ACC_ID = #{sMemAccId}
   
   
      AND A.PO_TY_CD = #{sPoTyCd}
   
   
      AND A.PO_STS_CD = #{sPoStsCd}
   
   
      AND A.PO_NO = #{sPoNo}

/* [com.coway.trust.biz.homecare.po.impl.HcPoIssueMapper.selectHcPoIssueMainList] - KR JIN */ SELECT * FROM ( SELECT ROWNUM AS RNUM, T1.* FROM (
SELECT A.PO_NO , A.PO_TY_CD , (SELECT CODE_NAME FROM SYS0013M WHERE CODE_MASTER_ID = '428' AND CODE_ID = A.PO_TY_CD) AS PO_TY_NAME , A.CDC , B.CODE_CD AS CDC_CD , A.CDC AS CDC_TEXT , A.PO_STS_CD , (SELECT CODE FROM SYS0013M WHERE CODE_MASTER_ID = '429' AND CODE_ID = A.PO_STS_CD) AS PO_STS_CODE , (SELECT CODE_NAME FROM SYS0013M WHERE CODE_MASTER_ID = '429' AND CODE_ID = A.PO_STS_CD) AS PO_STS_NAME , A.SUPP_STS_CD , (SELECT CODE FROM SYS0013M WHERE CODE_MASTER_ID = '438' AND CODE_ID = A.SUPP_STS_CD) AS SUPP_STS_CODE , (SELECT CODE_NAME FROM SYS0013M WHERE CODE_MASTER_ID = '438' AND CODE_ID = A.SUPP_STS_CD) AS SUPP_STS_NAME , A.MEM_ACC_ID
, A.MEM_ACC_ID AS MEM_ACC_NAME , TO_DATE(A.PO_DT, 'YYYYMMDD') AS PO_DT , TO_CHAR(TO_DATE(A.CDC_DUE_DT, 'YYYYMMDD'), 'YYYY/MM/DD') AS CDC_DUE_DT , B.ADDRESS AS ADDRESS , B.TEL_NO AS TEL_NO , A.USE_YN , A.RM , A.BASE_EXR , A.CUR , A.ISSUE_USR_ID , (SELECT USER_NAME FROM SYS0047M WHERE USER_ID = A.ISSUE_USR_ID) AS ISSUE_USR_NAME , A.ISSUE_DT , A.ISSUE_RSN , A.APPR_USR_ID , (SELECT USER_NAME FROM SYS0047M WHERE USER_ID = A.APPR_USR_ID) AS APPR_USR_NAME , A.APPR_DT , A.APPR_RSN , A.CRT_DT , A.CRT_USER_ID FROM HMC0004M A , ( SELECT A.WH_LOC_ID AS CODE_ID , A.WH_LOC_CODE AS CODE_CD , A.WH_LOC_DESC AS CODE_NAME , B.STATE ||' '|| B.CITY ||' '|| B.AREA ||' '|| A.STREET ||' '|| A.ADDR_DTL AS ADDRESS , NVL(A.WH_LOC_TEL1, A.WH_LOC_TEL2 ) AS TEL_NO FROM SYS0028M A , SYS0064M B WHERE A.AREA_ID = B.AREA_ID(+) AND A.WH_LOC_CODE IN ('3010', '3020', '3030') ) B -- CDC MASTER WHERE 1 = 1 AND A.CDC = B.CODE_ID (+) AND A.USE_YN = 'Y'
AND A.CDC = #{sCdc}
AND A.PO_DT >= #{sPoDtFrom}
AND A.PO_DT <= #{sPoDtTo}
AND A.MEM_ACC_ID = #{sMemAccId}
AND A.PO_TY_CD = #{sPoTyCd}
AND A.PO_STS_CD = #{sPoStsCd}
AND A.PO_NO = #{sPoNo}
ORDER BY
A.PO_NO ASCDESC A.PO_TY_CD ASCDESC PO_TY_NAME ASCDESC A.CDC ASCDESC B.CODE_CD ASCDESC CDC_TEXT ASCDESC A.PO_STS_CD ASCDESC PO_STS_CODE ASCDESC PO_STS_NAME ASCDESC A.MEM_ACC_ID ASCDESC MEM_ACC_NAME ASCDESC A.PO_DT ASCDESC A.CDC_DUE_DT ASCDESC ADDRESS ASCDESC B.TEL ASCDESC A.USE_YN ASCDESC A.RM ASCDESC A.BASE_EXR ASCDESC A.CUR ASCDESC A.ISSUE_USR_ID ASCDESC ISSUE_USR_NAME ASCDESC A.ISSUE_DT ASCDESC A.ISSUE_RSN ASCDESC A.APPR_USR_ID ASCDESC APPR_USR_NAME ASCDESC A.APPR_DT ASCDESC A.APPR_RSN ASCDESC A.CRT_DT ASCDESC A.CRT_USER_ID ASCDESC
A.PO_NO DESC
A.PO_NO DESC
) T1
ROWNUM <= #{lastIndex}
)
RNUM > #{firstIndex}

mapper.insert:
/* [com.coway.trust.biz.homecare.po.impl.HcPoIssueMapper.insertHcPoIssueMain] - KR JIN */
SELECT 'CM'|| #{code} || TO_CHAR(SYSDATE, 'DDMMYY') || DECODE(TO_NUMBER(SUBSTR(MAX(PO_NO), 12)), 999, CHR(ASCII(SUBSTR(MAX(PO_NO), 11, 1))+1), NVL(SUBSTR(MAX(PO_NO), 11, 1), 'A') ) || LPAD(DECODE( NVL(TO_NUMBER(SUBSTR(MAX(PO_NO), 12)), 0)+1, 1000, 1, NVL(TO_NUMBER(SUBSTR(MAX(PO_NO), 12)), 0)+1), 3, '0') AS poNo FROM ( SELECT PO_NO FROM HMC0004M WHERE PO_NO LIKE 'CM'|| #{code} || TO_CHAR(SYSDATE, 'DDMMYY') ||'%' ORDER BY PO_NO DESC ) WHERE ROWNUM = 1
INSERT INTO HMC0004M --HomeCare PO Master ( PO_NO --PO No , PO_TY_CD --PO TYPE CD(428) , CDC --CDC , MEM_ACC_ID --vendor , PO_DT --PO DATE , CDC_DUE_DT --Receiving Due Date , PO_STS_CD --PO Status Code(429) , USE_YN --USE_YN , BASE_EXR --Base Exchange Rate , CUR --currency , RM --Remark , ISSUE_USR_ID --PO Issue User Id , ISSUE_DT --PO Issue Date , ISSUE_RSN --PO Issue Reasons , APPR_USR_ID --Approval User Id , APPR_DT --Approval Date , APPR_RSN --Reasons for Approval , CRT_DT --Create Date , CRT_USER_ID --Create User Id , UPD_DT --Update Date , UPD_USER_ID --Update User Id ) VALUES ( #{poNo} --PO No , #{poTyCd} --PO TYPE CD(428) , #{cdc} --CDC , #{memAccId} --vendor , TO_CHAR(SYSDATE, 'YYYYMMDD') --PO DATE , TO_CHAR(TO_DATE(#{cdcDueDt}, 'YYYY/MM/DD'), 'YYYYMMDD') --Receiving Due Date , (SELECT MAX(CODE_ID) FROM SYS0013M WHERE CODE_MASTER_ID = '429' AND DISAB = 0 AND CODE = #{poStsCd}) , 'Y' --USE_YN , #{baseExr} --Base Exchange Rate , #{cur} --currency , #{rm} --Remark , '' --PO Issue User Id , null --PO Issue Date , '' --PO Issue Reasons , '' --Approval User Id , null --Approval Date , '' --Reasons for Approval , SYSDATE , #{crtUserId} --Create User Id , SYSDATE , #{updUserId} --Update User Id )

mapper.update:
    /* [com.coway.trust.biz.homecare.po.impl.HcPoIssueMapper.updateHcPoIssueSub] - KR JIN */
        UPDATE HMC0005D
           SET STOCK_ID      = #{stockId}
			 , STOCK_CODE    = #{stockCode}
			 , SUPLY_ITEM_CD = #{suplyItemCd}
			 , PO_QTY        = #{poQty}
			 , UOM           = #{uom}
			 , CUR           = #{cur}
			 , PO_UPRC       = #{poUprc}
			 , SUPLY_PRC     = #{suplyPrc}
			 , TAX           = #{tax}
			 , TAX_CD        = #{taxCd}
			 , LOCAL_AMT     = #{localAmt}
			 , FREX_AMT      = #{frexAmt}
			 , PO_DTL_STS_CD = #{poDtlStsCd}
			 , RM            = #{rm}
			 , USE_YN        = 'Y'
			 , UPD_DT        = SYSDATE
			 , UPD_USER_ID   = #{updUserId}
	     WHERE PO_NO = #{poNo}
           AND PO_DTL_NO = #{poDtlNo}

mapper.delete:
/* [com.coway.trust.biz.homecare.po.impl.HcPoIssueMapper.deleteHcPoIssueSub] - KR JIN */
    DELETE FROM HMC0005D
     WHERE PO_NO = #{poNo}
       AND PO_DTL_NO = #{poDtlNo}

mapper.update:
/* [com.coway.trust.biz.homecare.po.impl.HcPoIssueMapper.updateIssueHcPoIssue] - KR JIN */
    UPDATE HMC0004M     --HomeCare PO Master
       SET PO_STS_CD    = (SELECT MAX(CODE_ID) FROM SYS0013M WHERE CODE_MASTER_ID = '429' AND DISAB = 0 AND CODE = #{poStsCd})
         , ISSUE_USR_ID = #{updUserId}
         , ISSUE_DT     = SYSDATE
         , ISSUE_RSN    = #{issueRsn}
         , UPD_DT       = SYSDATE
         , UPD_USER_ID  = #{updUserId}          --Update User Id
     WHERE 1 = 1
       AND PO_NO        = #{poNo}               --PO No

/* [com.coway.trust.biz.homecare.po.impl.HcPoIssueMapper.updateApprovalHcPoIssue] - KR JIN */ UPDATE HMC0004M --HomeCare PO Master SET PO_STS_CD = (SELECT MAX(CODE_ID) FROM SYS0013M WHERE CODE_MASTER_ID = '429' AND DISAB = 0 AND CODE = #{poStsCd})
, SUPP_STS_CD = (SELECT MAX(CODE_ID) FROM SYS0013M WHERE CODE_MASTER_ID = '438' AND DISAB = 0 AND CODE = #{suppStsCd})
, APPR_USR_ID = #{updUserId} , APPR_DT = SYSDATE , APPR_RSN = #{issueRsn} , UPD_DT = SYSDATE , UPD_USER_ID = #{updUserId} --Update User Id WHERE 1 = 1 AND PO_NO = #{poNo} --PO No

/* [com.coway.trust.biz.homecare.po.impl.HcPoIssueMapper.deleteHcPoIssuePoSub] - KR JIN */
    UPDATE HMC0005D
       SET USE_YN = 'N'
         , UPD_DT = SYSDATE
         , UPD_USER_ID = #{updUserId}
     WHERE PO_NO = #{poNo}

/* [com.coway.trust.biz.homecare.po.impl.HcPoIssueMapper.deleteHcPoIssuePoMain] - KR JIN */
    UPDATE HMC0004M
       SET USE_YN = 'N'
         , UPD_DT = SYSDATE
         , UPD_USER_ID = #{updUserId}
     WHERE PO_NO = #{poNo}

    /* [com.coway.trust.biz.homecare.po.impl.HcPoIssueMapper.updateHCPoDetailKeySort] - KR JIN */
        UPDATE HMC0005D A
		   SET A.PO_DTL_NO = (
		                     SELECT T.PO_ROW
		                       FROM (
		                             SELECT ROW_NUMBER() OVER (PARTITION BY PO_NO ORDER BY STOCK_CODE ASC) AS PO_ROW
		                                  , PO_NO
		                                  , PO_DTL_NO
		                                  , STOCK_CODE
		                               FROM HMC0005D
		                              WHERE PO_NO = #{sPoNo}
		                            ) T
		                       WHERE A.PO_NO = T.PO_NO
		                         AND A.PO_DTL_NO = T.PO_DTL_NO
		                   )
		 WHERE A.PO_NO = #{sPoNo}
