/* [com.coway.trust.biz.homecare.po.impl.HcSettlementMapper.selectHcSettlementMainCnt] - KR JIN */ SELECT COUNT(1) AS CNT FROM HMC0009M A -- GR HEADER , ( SELECT A.PO_NO , A.HMC_DELVRY_NO , A.HMC_GR_NO , SUM(A.RCIPT_QTY ) AS RCIPT_QTY , SUM(A.RCIPT_QTY * B.PO_UPRC) AS PO_UPRC , SUM(A.RCIPT_QTY *(B.TAX/B.PO_QTY) ) AS TAX , SUM((A.RCIPT_QTY * B.PO_UPRC) + (A.RCIPT_QTY *(B.TAX/B.PO_QTY)) ) AS TOTAL , MAX(B.CUR) AS CUR_ID FROM HMC0010D A , HMC0005D B WHERE A.PO_NO = B.PO_NO AND A.PO_DTL_NO = B.PO_DTL_NO AND A.RCIPT_QTY > 0 AND A.USE_YN = 'Y' AND B.USE_YN = 'Y' GROUP BY A.HMC_GR_NO, A.PO_NO, A.HMC_DELVRY_NO ) B -- GR DETAIL , ( SELECT A.SETTL_NO , A.SETTL_STATUS , A.AMOUNT , A.RQST_DT , A.CNFM_DT , A.SETTL_DT , B.HMC_GR_NO , B.HMC_DELVRY_NO , B.PO_NO FROM HMC0012M A , ( SELECT SETTL_NO, HMC_GR_NO, HMC_DELVRY_NO, PO_NO FROM HMC0013D GROUP BY SETTL_NO, HMC_GR_NO, HMC_DELVRY_NO, PO_NO ) B WHERE A.SETTL_NO = B.SETTL_NO ) C -- SETTLEMENT HEADER , HMC0004M D WHERE A.HMC_GR_NO = B.HMC_GR_NO AND A.PO_NO = B.PO_NO AND A.HMC_DELVRY_NO = B.HMC_DELVRY_NO
AND A.PO_NO = C.PO_NO (+) AND A.HMC_DELVRY_NO = C.HMC_DELVRY_NO (+) AND A.HMC_GR_NO = C.HMC_GR_NO (+)
AND A.PO_NO = C.PO_NO AND A.HMC_DELVRY_NO = C.HMC_DELVRY_NO AND A.HMC_GR_NO = C.HMC_GR_NO
AND A.PO_NO = D.PO_NO AND A.GR_YN = 'Y' AND A.USE_YN = 'Y' AND D.USE_YN = 'Y'
AND A.CDC = #{sCdc}
AND A.GR_DT >= #{sGrDtFrom}
AND A.GR_DT <= TO_CHAR(TO_DATE(#{sGrDtTo}, 'YYYYMMDD') + 1, 'YYYYMMDD')
AND D.MEM_ACC_ID = #{sMemAccId}
AND A.HMC_GR_NO LIKE '%'||#{sHmcGrNo}||'%'
AND C.SETTL_NO LIKE '%'||#{sSettlNo}||'%'

/* [com.coway.trust.biz.homecare.po.impl.HcSettlementMapper.selectHcSettlementMain] - KR JIN */ SELECT * FROM ( SELECT ROWNUM AS RNUM, T1.* FROM ( SELECT A.HMC_GR_NO , A.HMC_DELVRY_NO , A.PO_NO , C.SETTL_DT , C.SETTL_STATUS AS PO_SETTL_STATUS , NVL((SELECT CODE AS CODE FROM SYS0013M WHERE CODE_MASTER_ID = '443' AND CODE_ID = C.SETTL_STATUS ), '-1') AS PO_SETTL_STATUS_CD , (SELECT CODE_NAME FROM SYS0013M WHERE CODE_MASTER_ID = '443' AND CODE_ID = C.SETTL_STATUS ) AS PO_SETTL_STATUS_NM , C.SETTL_NO , B.TOTAL AS AMOUNT , C.AMOUNT AS SETTLEMENT_AMOUNT , B.CUR_ID , (SELECT CODE_NAME FROM SYS0013M WHERE CODE_MASTER_ID = '94' AND CODE_ID = B.CUR_ID ) AS CUR_NM , D.MEM_ACC_ID , (SELECT MEM_ACC_NAME FROM FCM0006M WHERE MEM_ACC_ID = D.MEM_ACC_ID) AS MEM_ACC_NAME FROM HMC0009M A -- GR HEADER , ( SELECT A.PO_NO , A.HMC_DELVRY_NO , A.HMC_GR_NO , SUM(A.RCIPT_QTY ) AS RCIPT_QTY , SUM(A.RCIPT_QTY * B.PO_UPRC) AS PO_UPRC , SUM(A.RCIPT_QTY *(B.TAX/B.PO_QTY) ) AS TAX , SUM((A.RCIPT_QTY * B.PO_UPRC) + (A.RCIPT_QTY *(B.TAX/B.PO_QTY)) ) AS TOTAL , MAX(B.CUR) AS CUR_ID FROM HMC0010D A , HMC0005D B WHERE A.PO_NO = B.PO_NO AND A.PO_DTL_NO = B.PO_DTL_NO AND A.RCIPT_QTY > 0 AND A.USE_YN = 'Y' AND B.USE_YN = 'Y' GROUP BY A.HMC_GR_NO, A.PO_NO, A.HMC_DELVRY_NO ) B -- GR DETAIL , ( SELECT A.SETTL_NO , A.SETTL_STATUS , A.AMOUNT , A.RQST_DT , A.CNFM_DT , TO_CHAR(TO_DATE(A.SETTL_DT, 'YYYYMMDD'), 'YYYY/MM/DD') AS SETTL_DT , B.HMC_GR_NO , B.HMC_DELVRY_NO , B.PO_NO FROM HMC0012M A , ( SELECT SETTL_NO, HMC_GR_NO, HMC_DELVRY_NO, PO_NO FROM HMC0013D GROUP BY SETTL_NO, HMC_GR_NO, HMC_DELVRY_NO, PO_NO ) B WHERE A.SETTL_NO = B.SETTL_NO ) C -- SETTLEMENT HEADER , HMC0004M D WHERE A.HMC_GR_NO = B.HMC_GR_NO AND A.PO_NO = B.PO_NO AND A.HMC_DELVRY_NO = B.HMC_DELVRY_NO
AND A.PO_NO = C.PO_NO (+) AND A.HMC_DELVRY_NO = C.HMC_DELVRY_NO (+) AND A.HMC_GR_NO = C.HMC_GR_NO (+)
AND A.PO_NO = C.PO_NO AND A.HMC_DELVRY_NO = C.HMC_DELVRY_NO AND A.HMC_GR_NO = C.HMC_GR_NO
AND A.PO_NO = D.PO_NO AND A.USE_YN = 'Y' AND D.USE_YN = 'Y' AND A.GR_YN = 'Y'
AND A.CDC = #{sCdc}
AND A.GR_DT >= #{sGrDtFrom}
AND A.GR_DT <= TO_CHAR(TO_DATE(#{sGrDtTo}, 'YYYYMMDD') + 1, 'YYYYMMDD')
AND D.MEM_ACC_ID = #{sMemAccId}
AND A.HMC_GR_NO LIKE '%'||#{sHmcGrNo}||'%'
AND C.SETTL_NO LIKE '%'||#{sSettlNo}||'%'
ORDER BY
A.HMC_GR_NO ASCDESC A.HMC_DELVRY_NO ASCDESC A.PO_NO ASCDESC C.SETTL_DT ASCDESC PO_SETTL_STATUS_NM ASCDESC C.SETTL_NO ASCDESC AMOUNT ASCDESC SETTLEMENT_AMOUNT ASCDESC B.CUR_ID ASCDESC CUR_NM ASCDESC D.MEM_ACC_ID ASCDESC MEM_ACC_NAME ASCDESC
C.SETTL_NO DESC, A.HMC_GR_NO DESC
C.SETTL_NO DESC, A.HMC_GR_NO DESC
) T1
ROWNUM <= #{lastIndex}
)
RNUM > #{firstIndex}

