/* [com.coway.trust.biz.homecare.po.impl.HcSettlementMapper.insertSettlementMain] - KR JIN */
SELECT 'HSTL'||TO_CHAR(SYSDATE, 'YYMMDD')|| LPAD(NVL(MAX(TO_NUMBER(SUBSTR(SETTL_NO, 11))), 0)+1, 5, '0') AS SETTL_NO FROM HMC0012M WHERE SETTL_NO LIKE 'HSTL'||TO_CHAR(SYSDATE, 'YYMMDD') || '%'
INSERT INTO HMC0012M ( SETTL_NO --Settlement No , SETTL_STATUS --Settlement Status 443 , AMOUNT --Amount , RQST_DT --Requested Date , CNFM_DT --Supplier's Approved/Rejected Date , SETTL_DT --Settlement Date , CRT_DT --Create Date , CRT_USER_ID --Create User Id , UPD_DT --Update Date , UPD_USER_ID --Update User Id )VALUES( #{settlNo} --Settlement No , (SELECT MAX(CODE_ID) FROM SYS0013M WHERE CODE_MASTER_ID = '443' AND DISAB = 0 AND CODE = '10') , #{amount} --Amount , TO_CHAR(SYSDATE, 'YYYYMMDD') , NULL , NULL , SYSDATE , #{crtUserId} --Create User Id , SYSDATE , #{updUserId} --Update User Id )

