FROM SVC0117M A JOIN SYS0100M B ON A.DEFECT_TYP = B.DEFECT_TYP AND A.DEFECT_ID = B.DEFECT_ID AND B.DEFECT_STATUS = 1 AND SYSDATE BETWEEN B.EFF_DT AND B.EXP_DT
JOIN SYS0094M C ON C.CODE = A.SEQ AND C.IND = 'AS_PRESET' AND DISB = 0 AND C.CODE_NAME = #{prodCat}
WHERE SYSDATE BETWEEN A.EFF_DT AND A.EXP_DT AND A.STATUS = 1 AND A.ERR_CDE = #{errCd} AND A.ERR_DESC = #{errDesc}
AND B.DEFECT_TYP in ('DP', 'HDD', 'HDC', 'HDT', 'HSC') ORDER BY CASE B.DEFECT_TYP WHEN 'DP' THEN 1 WHEN 'HDD' THEN 2 WHEN 'HDC' THEN 3 WHEN 'HDT' THEN 4 ELSE 5 END
