, CASE WHEN ( Extent1.APP_TYPE_ID IS NOT NULL ) THEN Extent1.APP_TYPE_ID ELSE 0 END C1 , CASE WHEN ( Extent1.CUST_ID IS NOT NULL ) THEN Extent1.CUST_ID ELSE 0 END C2 , CASE WHEN ( Extent1.SALES_ORD_DT IS NOT NULL ) THEN Extent1.SALES_ORD_DT ELSE TO_DATE('1900-01-01','YYYY-MM-DD') END C3 , CASE WHEN ( Extent1.PRODUCT_ID IS NOT NULL ) THEN Extent1.PRODUCT_ID ELSE 0 END C4 FROM ( SELECT vCallLogOrderCall.CALL_ENTRY_ID CALL_ENTRY_ID , vCallLogOrderCall.CALLTYPEID CALL_TYPE_ID , vCallLogOrderCall.CALL_TYPE_CODE CALL_TYPE_CODE , vCallLogOrderCall.SALES_ORD_ID SALES_ORD_ID , vCallLogOrderCall.SALES_ORD_NO SALES_ORD_NO , vCallLogOrderCall.APP_TYPE_ID APP_TYPE_ID , vCallLogOrderCall.APPTYPENAME APP_TYPE_NAME , vCallLogOrderCall.SALES_ORD_DT SALES_ORD_DT , vCallLogOrderCall.CALL_STUS_ID CALL_STUS_ID , vCallLogOrderCall.CALL_STUS_CODE CALL_STUS_CODE , vCallLogOrderCall.PRODUCT_ID PRODUCT_ID , vCallLogOrderCall.PRODUCT_CODE || ' - ' || vCallLogOrderCall.PRODUCT_NAME PRODUCT_CODE , vCallLogOrderCall.PRODUCT_NAME PRODUCT_NAME , vCallLogOrderCall.CUST_ID CUST_ID , vCallLogOrderCall.CUST_NAME CUST_NAME , vCallLogOrderCall.CUST_IC CUST_IC , vCallLogOrderCall.INSTALL_AREA_ID INSTALL_AREA_ID , vCallLogOrderCall.INSTALL_AREA_NAME INSTALL_AREA_NAME , vCallLogOrderCall.CALL_LOG_DT CALL_LOG_DT , vCallLogOrderCall.DSC_BRNCH_ID DSC_BRNCH_ID , vCallLogOrderCall.PO_NO PO_NO , vCallLogOrderCall.INSTALL_TEL_M INSTALL_TEL_M , vCallLogOrderCall.INSTALL_TEL_O INSTALL_TEL_O , vCallLogOrderCall.INSTALL_TEL_R INSTALL_TEL_R , vCallLogOrderCall.INSTALL_TEL_F INSTALL_TEL_F , vCallLogOrderCall.DOC_ID DOC_ID , vCallLogOrderCall.IS_WAIT_CANCL IS_WAIT_CANCL , vCallLogOrderCall.CRT_USER_ID CRT_USER_ID_ID , vCallLogOrderCall.CRT_USER_ID CRT_USER_ID , vCallLogOrderCall.CRT_DT CRT_DT , vCallLogOrderCall.CALL_TYPE_NAME CALL_TYPE_NAME , vCallLogOrderCall.CALL_FDBCK_ID CALL_FDBCK_ID , vCallLogOrderCall.CALL_FDBCK_CODE CALL_FDBCK_CODE , vCallLogOrderCall.CALL_FDBCK_DESC CALL_FDBCK_DESC , vCallLogOrderCall.RCD_TMS RCD_TMS , vCallLogOrderCall.UPD_DT UPD_DT , AREA.STATE , AREA.AREA , AREA.POSTCODE , DSC.CODE DSC_CODE FROM CCR1002V vCallLogOrderCall JOIN SYS0064M AREA ON AREA.AREA_ID = vcalllogordercall.INSTALL_AREA_ID JOIN SYS0005M DSC ON DSC.BRNCH_ID = vcalllogordercall.DSC_BRNCH_ID
AND dsc.TYPE_ID = #{branchTypeId}
) Extent1 WHERE Extent1.SALES_ORD_NO = #{orderNo}
AND Extent1.CALL_STUS_ID = #{callStusId}
