mapper.select:
/* com.coway.trust.biz.homecare.services.plan.impl.HcDTSubGroupListMapper.selectDTSubAreaGroupList */ SELECT A.AREA_ID, B.DT_BRNCH_CODE, B.LT_BRNCH_CODE, B.AREA, B.CITY, B.POSTCODE, B.STATE, A.REQST_NO, TO_CHAR(A.PRIOD_FROM,'YYYY-MM-DD') PRIOD_FROM, TO_CHAR(A.PRIOD_TO,'YYYY-MM-DD') PRIOD_TO, CASE WHEN (A.LOC_TYPE='L') THEN 'Local' WHEN (A.LOC_TYPE='O') THEN 'OutStation' ELSE '' END LOC_TYPE, A.SVC_WEEK, A.DT_SUB_GRP AS CT_SUB_GRP, A.LT_SUB_GRP AS LT_SUB_GRP, (SELECT COUNT(AREA_ID) FROM ORG0028M WHERE AREA_ID = A.AREA_ID AND TYPE = 6666) AS NO_SVC_CNT
FROM ORG0019M A INNER JOIN SYS0064M B ON A.AREA_ID = B.AREA_ID AND A.AVAIL_FLAG = 'Y' WHERE A.BRNCH_TYPE = #{branchTypeId}
AND B.DT_BRNCH_CODE = #{dscCode}
AND B.AREA_ID = #{areaId}
AND B.AREA like '%' || #{area} || '%'
AND B.CITY = #{city}
AND B.POSTCODE = #{postCode}
AND B.STATE = #{state}
AND B.DT_SUB_GRP = #{dtSubGrp}
ORDER BY CT_SUB_GRP, LT_SUB_GRP

/* com.coway.trust.biz.homecare.services.plan.impl.HcDTSubGroupListMapper.selectDTSubGrp */
SELECT DISTINCT NVL(AC_SUB_GRP,DT_SUB_GRP) CODE_ID
         , NVL(AC_SUB_GRP,DT_SUB_GRP)  CODE_NAME
 FROM SYS0064M
WHERE DT_SUB_GRP IS NOT NULL

    AND (DT_BRNCH_CODE = #{groupCode}
    or AC_BRNCH_CODE = #{groupCode}
    )

ORDER BY NVL(AC_SUB_GRP,DT_SUB_GRP)
