/* com.coway.trust.biz.homecare.services.plan.impl.HcHolidayMapper.selectDTAssignList */ SELECT DISTINCT B.HOLIDAY_TYPE, B.STATE, TO_CHAR(B.HOLIDAY,'YYYY-MM-DD') HOLIDAY, B.HOLIDAY_DESC, B.HOLIDAY_SEQ, B.DT_BRNCH_CODE, B.BRNCH_ID, CASE WHEN COUNT (CASE WHEN C.MEM_TYPE = 5758 AND C.BRNCH_CODE = B.DT_BRNCH_CODE THEN 1 END) > 0 THEN 'Complete' WHEN COUNT (CASE WHEN C.MEM_TYPE != 5758 AND C.BRNCH_CODE = B.DT_BRNCH_CODE THEN 0 END) >= 0 THEN 'Active' END ASSIGN_STATUS, COUNT (CASE WHEN C.MEM_TYPE = 5758 AND C.BRNCH_CODE = B.DT_BRNCH_CODE THEN 1 END) AS REPLACEMENT_CT_PAX, B.APPL_CODE FROM( SELECT CASE WHEN A.HOLIDAY_TYPE = 'P' THEN 'Public Holiday' ELSE 'State Holiday' END HOLIDAY_TYPE, D.STATE, A.HOLIDAY, A.HOLIDAY_DESC, A.HOLIDAY_SEQ, C.CODE DT_BRNCH_CODE, C.BRNCH_ID, CASE WHEN E.APPL_CODE IS NULL THEN 'Holiday' ELSE 'Working' END APPL_CODE FROM( SELECT A.HOLIDAY_TYPE, A.HOLIDAY, A.HOLIDAY_DESC, A.HOLIDAY_SEQ, B.CODE BRNCH_CODE FROM sys0101m A , (SELECT CODE FROM SYS0005M WHERE TYPE_ID = '5754' GROUP BY CODE) B )A JOIN sys0005m C on A.BRNCH_CODE = C.CODE JOIN SYS0064M D ON C.AREA_ID = D.AREA_ID LEFT JOIN SVC0055M E on A.HOLIDAY = E.HOLIDAY AND A.HOLIDAY_SEQ = E.HOLIDAY_SEQ
AND A.HOLIDAY_TYPE = #{type1}
AND A.HOLIDAY_TYPE = #{type}
AND A.HOLIDAY = TO_CHAR(TO_DATE (#{holidayDt}, 'DD/MM/YYYY'),'YYYY-MM-DD')
AND A.HOLIDAY = TO_DATE(#{holiday1},'YYYY-MM-DD')
AND A.HOLIDAY_DESC =#{holidayDesc1}
AND A.HOLIDAY_SEQ =#{holidaySeq1}
)B JOIN org0020d C ON B.HOLIDAY_SEQ = C.HOLIDAY_SEQ and B.HOLIDAY = C.HOLIDAY GROUP BY B.HOLIDAY_TYPE,B.STATE,B.HOLIDAY,B.HOLIDAY_DESC,B.HOLIDAY_SEQ,B.DT_BRNCH_CODE,B.BRNCH_ID,B.APPL_CODE
ORDER BY DT_BRNCH_CODE

/* com.coway.trust.biz.homecare.services.plan.impl.HcHolidayMapper.selectLTAssignList */ SELECT DISTINCT B.HOLIDAY_TYPE, B.STATE, TO_CHAR(B.HOLIDAY,'YYYY-MM-DD') HOLIDAY, B.HOLIDAY_DESC, B.HOLIDAY_SEQ, B.LT_BRNCH_CODE, B.BRNCH_ID, CASE WHEN COUNT (CASE WHEN C.MEM_TYPE = 6672 AND C.BRNCH_CODE = B.LT_BRNCH_CODE THEN 1 END) > 0 THEN 'Complete' WHEN COUNT (CASE WHEN C.MEM_TYPE != 6672 AND C.BRNCH_CODE = B.LT_BRNCH_CODE THEN 0 END) >= 0 THEN 'Active' END ASSIGN_STATUS, COUNT (CASE WHEN C.MEM_TYPE = 6672 AND C.BRNCH_CODE = B.LT_BRNCH_CODE THEN 1 END) AS REPLACEMENT_CT_PAX, B.APPL_CODE FROM( SELECT CASE WHEN A.HOLIDAY_TYPE = 'P' THEN 'Public Holiday' ELSE 'State Holiday' END HOLIDAY_TYPE, D.STATE, A.HOLIDAY, A.HOLIDAY_DESC, A.HOLIDAY_SEQ, C.CODE LT_BRNCH_CODE, C.BRNCH_ID, CASE WHEN E.APPL_CODE IS NULL THEN 'Holiday' ELSE 'Working' END APPL_CODE FROM( SELECT A.HOLIDAY_TYPE, A.HOLIDAY, A.HOLIDAY_DESC, A.HOLIDAY_SEQ, B.CODE BRNCH_CODE FROM sys0101m A , (SELECT CODE FROM SYS0005M WHERE TYPE_ID = '5754' GROUP BY CODE) B )A JOIN sys0005m C on A.BRNCH_CODE = C.CODE JOIN SYS0064M D ON C.AREA_ID = D.AREA_ID LEFT JOIN SVC0055M E on A.HOLIDAY = E.HOLIDAY AND A.HOLIDAY_SEQ = E.HOLIDAY_SEQ
AND A.HOLIDAY_TYPE = #{type1}
AND A.HOLIDAY_TYPE = #{type}
AND A.HOLIDAY = TO_CHAR(TO_DATE (#{holidayDt}, 'DD/MM/YYYY'),'YYYY-MM-DD')
AND A.HOLIDAY = TO_DATE(#{holiday1},'YYYY-MM-DD')
AND A.HOLIDAY_DESC =#{holidayDesc1}
AND A.HOLIDAY_SEQ =#{holidaySeq1}
)B JOIN org0020d C ON B.HOLIDAY_SEQ = C.HOLIDAY_SEQ and B.HOLIDAY = C.HOLIDAY GROUP BY B.HOLIDAY_TYPE,B.STATE,B.HOLIDAY,B.HOLIDAY_DESC,B.HOLIDAY_SEQ,B.LT_BRNCH_CODE,B.BRNCH_ID,B.APPL_CODE
ORDER BY LT_BRNCH_CODE

/* com.coway.trust.biz.homecare.services.plan.impl.HcHolidayMapper.selectDTAssignList */ SELECT CASE WHEN B.HOLIDAY_TYPE = 'P' THEN 'Public Holiday' ELSE 'State Holiday' END HOLIDAY_TYPE, B.STATE, B.HOLIDAY, B.HOLIDAY_DESC, B.HOLIDAY_SEQ, B.DT_BRNCH_CODE, B.BRNCH_ID, B.ASSIGN_STATUS, B.REPLACEMENT_CT_PAX, CASE WHEN B.APPL_CODE IS NULL THEN 'Holiday' ELSE DECODE (B.APPL_CODE, 'W', 'Working','Holiday') END APPL_CODE
FROM (SELECT A.HOLIDAY_TYPE, A.STATE, TO_CHAR(A.HOLIDAY,'YYYY-MM-DD') HOLIDAY, A.HOLIDAY_DESC, A.HOLIDAY_SEQ, A.DT_BRNCH_CODE, F.BRNCH_ID, CASE WHEN COUNT (D.CT_MEM_ID) = 0 THEN 'Active' WHEN COUNT (D.CT_MEM_ID) > 0 THEN 'Complete' END ASSIGN_STATUS, COUNT(CASE WHEN D.CT_MEM_ID IS NOT NULL THEN 1 END) AS REPLACEMENT_CT_PAX, C.APPL_CODE
FROM( SELECT A.HOLIDAY_TYPE, CASE WHEN A.HOLIDAY_TYPE = 'S' THEN A.STATE ELSE F.STATE END STATE, A.HOLIDAY, A.HOLIDAY_DESC, A.HOLIDAY_SEQ, A.DT_BRNCH_CODE FROM (SELECT DISTINCT A.HOLIDAY_TYPE, A.STATE, A.HOLIDAY, A.HOLIDAY_DESC, A.HOLIDAY_SEQ, CASE WHEN A.HOLIDAY_TYPE = 'S' AND A.STATE = B.STATE THEN B.DT_BRNCH_CODE ELSE E.CODE END DT_BRNCH_CODE FROM SYS0101M A , (SELECT STATE, DT_BRNCH_CODE FROM SYS0064M GROUP BY STATE, DT_BRNCH_CODE) B , (SELECT CODE FROM SYS0005M WHERE TYPE_ID = #{hdcBranchType} GROUP BY CODE) E WHERE A.STATE = B.STATE(+) ) A , (SELECT A.AREA_ID, A.CODE, B.STATE FROM (SELECT AREA_ID , CODE FROM SYS0005M WHERE TYPE_ID = #{hdcBranchType}) A , SYS0064M B WHERE A.AREA_ID = B.AREA_ID (+) ) F WHERE F.CODE(+) = A.DT_BRNCH_CODE ) A , SVC0055M C , ORG0020D D , SYS0005M F WHERE A.HOLIDAY = D.HOLIDAY(+) AND A.HOLIDAY_TYPE = D.HOLIDAY_TYPE(+) AND A.HOLIDAY_SEQ = D.HOLIDAY_SEQ(+) AND A.DT_BRNCH_CODE = D.BRNCH_CODE(+) AND A.HOLIDAY = C.HOLIDAY(+) AND A.HOLIDAY_TYPE = C.HOLIDAY_TYPE(+) AND A.HOLIDAY_SEQ = C.HOLIDAY_SEQ(+) AND A.DT_BRNCH_CODE = C.BRNCH_CODE(+) AND A.DT_BRNCH_CODE = F.CODE
GROUP BY A.HOLIDAY_TYPE, A.STATE, A.HOLIDAY, A.HOLIDAY_DESC, A.HOLIDAY_SEQ, A.DT_BRNCH_CODE, F.BRNCH_ID, C.APPL_CODE ORDER BY DT_BRNCH_CODE, HOLIDAY_TYPE, STATE, HOLIDAY, HOLIDAY_DESC ) B
AND B.ASSIGN_STATUS IN
#{item}
AND B.STATE = #{state1}
AND B.STATE IN
#{item}
AND B.HOLIDAY_TYPE = #{type1}
AND B.HOLIDAY_TYPE = #{type}
AND B.HOLIDAY = TO_CHAR(TO_DATE (#{holidayDt}, 'DD/MM/YYYY'),'YYYY-MM-DD')
AND B.HOLIDAY = #{holiday1}
AND B.DT_BRNCH_CODE IN
#{item}
AND B.HOLIDAY_DESC =#{holidayDesc1}
AND B.HOLIDAY_SEQ =#{holidaySeq1}

