mapper.select:
/* com.coway.trust.biz.homecare.services.plan.impl.HcTerritoryMgtMapper.selectHcTerritoryDetailList */ SELECT A.AREA_ID , A.AREA , A.CITY , A.POSTCODE , A.STATE , A.DT_BRNCH_CODE , A.DT_SUB_GRP , A.LT_BRNCH_CODE , A.LT_SUB_GRP
FROM SYS0064M A JOIN SYS0005M B ON A.AREA_ID = B.AREA_ID
WHERE B.TYPE_ID = #{brnchType} AND A.AREA_ID IN(SELECT AREA_ID FROM ORG0019M WHERE REQST_NO=#{reqstNo})

/* com.coway.trust.biz.homecare.services.plan.impl.HcTerritoryMgtMapper.selectCurrentHcTerritory */ SELECT A.AREA_ID , A.AREA , A.CITY , A.POSTCODE , A.STATE , A.DT_BRNCH_CODE , A.DT_SUB_GRP , A.LT_BRNCH_CODE , A.LT_SUB_GRP , CASE WHEN A.AREA_ID IN (SELECT AREA_ID FROM SYS0064M WHERE DT_BRNCH_CODE IS NULL ) THEN 'Active' ELSE 'Complete' END AS STATUS
FROM SYS0064M A
AND UPPER(A.AREA) LIKE UPPER('%'||#{area}||'%')
AND REPLACE(A.AREA_ID, '-', '') = REPLACE(#{areaID}, '-', '')
AND UPPER(A.CITY) LIKE UPPER('%'||#{city}||'%')
AND A.STATE = #{state}
AND A.POSTCODE = #{postcode}
AND A.DT_BRNCH_CODE = #{hdcBranch}
AND (CODY_BRNCH_CODE IS NULL OR CT_BRNCH_CODE IS NULL)
AND (CODY_BRNCH_CODE IS NOT NULL AND CT_BRNCH_CODE IS NOT NULL)
AND (DT_BRNCH_CODE IS NOT NULL OR DT_SUB_GRP IS NULL)
AND (LT_BRNCH_CODE IS NOT NULL AND LT_SUB_GRP IS NOT NULL)

mapper.insert:
/* com.coway.trust.biz.homecare.services.plan.impl.HcTerritoryMgtMapper.insertHDC */
INSERT INTO ORG0019M (
           REQST_NO
         , AREA_ID
         , REQST_DT
         , REQST_USER_ID
         , DT_BRNCH_CODE        /* DT Branch Code(HDC) */
         , DT_SUB_GRP              /* Dt Sub Group */
         , BRNCH_TYPE
         , CNFM_STUS
)

    SELECT #{item.reqstNo}
             , #{item.areaId}
             , SYSDATE
             , #{item.requester}
             , #{item.branch}
             , #{item.extBranch}
             , #{item.brnchId}
             , '1'
     FROM DUAL

/* com.coway.trust.biz.homecare.services.plan.impl.HcTerritoryMgtMapper.insertHDC */
INSERT INTO ORG0019M (
           REQST_NO
         , AREA_ID
         , REQST_DT
         , REQST_USER_ID
         , LT_BRNCH_CODE        /* DT Branch Code(HDC) */
         , LT_SUB_GRP              /* Dt Sub Group */
         , BRNCH_TYPE
         , CNFM_STUS
)

    SELECT #{item.reqstNo}
             , #{item.areaId}
             , SYSDATE
             , #{item.requester}
             , #{item.branch}
             , #{item.extBranch}
             , #{item.brnchId}
             , '1'
     FROM DUAL

mapper.update:
/* com.coway.trust.biz.homecare.services.plan.impl.HcTerritoryMgtMapper.updateSYS0064M */
UPDATE SYS0064M SET
            DT_BRNCH_CODE   =  #{dtBrnchCode},
            DT_SUB_GRP         =  #{dtSubGrp}
 WHERE AREA_ID                = #{areaId}

 /* com.coway.trust.biz.homecare.services.plan.impl.HcTerritoryMgtMapper.updateORG0019MFlag */
UPDATE ORG0019M SET
            AVAIL_FLAG    = 'N'
 WHERE AREA_ID         = #{areaId}
    AND REQST_NO      = #{reqstNo}
    AND BRNCH_TYPE   = #{brnchType}

/* com.coway.trust.biz.homecare.services.plan.impl.HcTerritoryMgtMapper.updateORG0019M */
UPDATE ORG0019M SET
            AVAIL_FLAG    = 'Y',
            CNFM_STUS    = '4'
 WHERE AREA_ID          = #{areaId}
    AND REQST_NO       = #{reqstNo}
    AND BRNCH_TYPE    = #{brnchType}
