mapper.update:
UPDATE SYS0047M
SET USER_PASSWD = #{newPasswordConfirmTxt}
,USER_PASSWD_LAST_UPD_DT = SYSDATE


        WHERE USER_ID = #{newUserIdTxt}


        WHERE USER_ID = #{searchLoginId}

UPDATE SYS0047M
   SET USER_EMAIL  =  #{eMail}
      ,USER_UPD_DT =  SYSDATE
      ,USER_NRIC    = #{nricNo}
      ,USER_WORK_NO = #{userWorkNo}
      ,USER_MOBILE_NO = #{mobileNo}
      ,USER_EXT_NO =  #{exNo}
 WHERE USER_ID = #{updUserId}

mapper.select:
select BYPASS_AREA, BYPASS_MAIN_DO, BYPASS_DO_LIST from SYS0088m
where BYPASS_MAIN_DO = #{mainDo}
where BYPASS_MEM_TYPE = #{memTyp}

SELECT A.POP_NEW_FL_NM, A.POP_FLG, A.POP_TYPE, A.POP_REJECT_FLG, A.POP_ACK1, A.POP_ACK2, A.POP_ACK3, A.CRT_DT, NVL(B.POP_EXCEPTION_MEMROLE_CNT, 0) POP_EXCEPTION_MEMROLE_CNT, NVL(C.POP_EXCEPTION_USER_CNT, 0) POP_EXCEPTION_USER_CNT FROM SYS0091M A LEFT JOIN ( SELECT POP_ID, COUNT(*) POP_EXCEPTION_MEMROLE_CNT FROM SYS0091M WHERE POP_EXCEPTION_MEMROLE LIKE '%' || #{roleType} || '%' GROUP BY POP_ID ) B ON A.POP_ID = B.POP_ID LEFT JOIN ( SELECT POP_ID, COUNT(*) POP_EXCEPTION_USER_CNT FROM SYS0091M WHERE POP_EXCEPTION_USER LIKE '%' || #{userId} || '%' GROUP BY POP_ID ) C ON A.POP_ID = C.POP_ID WHERE SUBSTR(A.POP_MEM_TYPE, 1, 4) = #{userTypeId}
AND SUBSTR(A.POP_MEM_TYPE, 6, 3) = #{roleType}
AND POP_TYPE = #{popType}
AND POP_FLG = 'A'
