mapper.select:
SELECT itemid , itemname , itemcode ,cateid, catename, statusname
, description
, typename,typeid , uom , serialchk
from (
SELECT ITM_ID itemid , ITM_NAME itemname, ITM_CODE itemcode, CTGRY_ID cateid, STUS_CODE_ID stateid, DBMS_LOB.SUBSTR (ITM_DESC, 4000) description, (SELECT code_name FROM SYS0013M WHERE code_master_id = 63 and code_id = CTGRY_ID) catename, (SELECT SYS0038M.NAME FROM SYS0038M WHERE SYS0038M.STUS_CODE_ID = MSC0039D.STUS_CODE_ID) statusname FROM MSC0039D where 1 = 1
SELECT STK_ID itemid , STK_CODE itemcode , STK_DESC itemname , STK_CTGRY_ID cateid , STK_TYPE_ID typeid , STUS_CODE_ID stateid , UOM uom , SERIAL_CHK serialchk ,(SELECT CODE_NAME FROM SYS0013M WHERE CODE_ID = SYS0026M.STK_CTGRY_ID) catename, (SELECT CODE_NAME FROM SYS0013M WHERE CODE_ID = SYS0026M.STK_TYPE_ID) typename, (SELECT SYS0038M.NAME FROM SYS0038M WHERE SYS0038M.STUS_CODE_ID = SYS0026M.STUS_CODE_ID) statusname FROM SYS0026M WHERE 1 = 1
) where 1 = 1
and itemcode like #{scode}||'%'
and itemname like '%'|| #{sname} ||'%'
and cateid = #{cateid}
and typeid = #{typeid}
AND cateid in
#{item}
AND typeid in
#{item}
and stateid = #{stateid}
AND stateid in
#{item}

SELECT T1.ITM_ID, T1.ITM_CODE, T1.ITM_NAME, T1.ATTACH_IMG_LOC, T1.CTGRY_ID, T1.SEQ, T1.PRC, T1.PRC_REM, T1.ITM_DESC, T1.CRT_DT, T1.CRT_USER_ID, T1.UPD_DT, T1.UPD_USER_ID, T1.STUS_CODE_ID, T1.IS_ATTACH_IMG, T1.IS_HOT_ITM, T1.IS_NW_ITM, T1.IS_PROMO_ITM, T1.PROMO_NORMAL_PRC, T1.CURRENCY, S94.CODE_DESC CURRENCYNM, T1.UOM, S42.CODE_DESC UOMNAME, T1.ITEM_TYPE, T1.OLD_STK_ID, S63.CODE_ID, S63.CODE_NAME FROM (SELECT M39D.ITM_ID, M39D.ITM_CODE, M39D.ITM_NAME, M39D.ATTACH_IMG_LOC, M39D.CTGRY_ID, M39D.SEQ, M39D.PRC, M39D.PRC_REM, M39D.ITM_DESC, M39D.CRT_DT, M39D.CRT_USER_ID, M39D.UPD_DT, M39D.UPD_USER_ID, M39D.STUS_CODE_ID, M39D.IS_ATTACH_IMG, M39D.IS_HOT_ITM, M39D.IS_NW_ITM, M39D.IS_PROMO_ITM, M39D.PROMO_NORMAL_PRC, M39D.CURRENCY, M39D.UOM, M39D.ITEM_TYPE, M39D.OLD_STK_ID FROM MSC0039D M39D WHERE 1 = 1
AND ITM_CODE = #{svalue}
AND CTGRY_ID = #{spgroup}
AND STUS_CODE_ID = #{sused}
) T1, (SELECT * FROM SYS0013M WHERE CODE_MASTER_ID = 63) S63, (SELECT * FROM SYS0013M WHERE CODE_MASTER_ID = 42) S42, (SELECT * FROM SYS0013M WHERE CODE_MASTER_ID = 94) S94 WHERE T1.CTGRY_ID = S63.CODE_ID(+) AND T1.UOM = S42.CODE_ID(+) AND T1.CURRENCY = S94.CODE_ID(+)

mapper.update:
UPDATE MSC0039D
   SET ITM_NAME     = #{itmName}
     , PRC          = #{prc}
     , ITM_DESC     = #{itmDesc}
     , STUS_CODE_ID = #{stusCodeId}
     , CTGRY_ID     = #{ctgryId}
     , OLD_STK_ID   = #{oldStkId}
     , CURRENCY     = #{currency}
     , UOM          = #{uom}
     , ITEM_TYPE    = #{itemType}
     , UPD_DT       = SYSDATE
     , UPD_USER_ID  = #{updUserId}
 WHERE ITM_ID       = #{itmId}
