     , IS_CODY_MEMO  = decode(nvl(#{codymemo}  , '0') , '0' , 0 , 1)
     , IS_HP_MEMO    = decode(nvl(#{hpmemo}    , '0') , '0' , 0 , 1)
     , IS_HT_MEMO    = decode(nvl(#{htmemo}    , '0') , '0' , 0 , 1)
     , UPD_USER_ID   = #{userid}
     , UPD_DT        = SYSDATE
 WHERE MEMO_ID = #{memoid}

