mapper.select:
SELECT WH_LOC_ID locid, WH_LOC_CODE loccd, WH_LOC_DESC locdesc, ADDR_DTL locdtl , s28m.AREA_ID areaid, s28m.STREET street, WH_LOC_TEL1 loctel1, WH_LOC_TEL2 loctel2, WH_LOC_BRNCH_ID locbranch1, WH_LOC_BRNCH_ID2 locbranch2, WH_LOC_BRNCH_ID3 locbranch3, WH_LOC_TYPE_ID loctype, WH_LOC_STK_GRAD locgrad, WH_LOC_STUS_ID locstus, WH_LOC_UPD_USER_ID locuserid, WH_LOC_UPD_DT locupddt, CODE2 code2, DESC2 desc2, s64m.area areanm, s64m.city locdt2, s64m.postcode postcd, s64m.state, s64m.country countrynm, s05m.code branchcd, s05m.name branchnm, s05m.dcode, s05m.descr, s28m.WH_LOC_GB whlocgb, crdc.CODE_NAME whlocgbnm, s28m.SERIAL_FT_CHK serialftchk, s28m.SERIAL_PT_CHK serialptchk, s28m.SERIAL_PD_CHK serialpdchk, s28m.CDC_CODE cdccode, s28m.RDC_CODE rdccode, s28m.PLANT plant, s28m.SL_PLANT slplant, s28m.WH_LOC_LANE whloclane, (SELECT code_name FROM SYS0013M WHERE code_id = s28m.WH_LOC_TYPE_ID) codenm, s38m.name statnm, s47m.user_name, s28m.REM, s28m.SERIAL_REQUIRE_CHK_YN FROM SYS0028M s28m, SYS0064M s64m, (SELECT s05m.brnch_id, s05m.code, s05m.name, s13m.code dcode, s13m.CODE_DESC descr FROM SYS0005M s05m, SYS0013M s13m WHERE s05m.type_id = s13m.code_id(+)) s05m, SYS0038M s38m, SYS0047M s47m, (SELECT * FROM SYS0013M WHERE code_master_id = 339) crdc
WHERE s28m.AREA_ID = s64m.AREA_ID(+) AND s28m.WH_LOC_BRNCH_ID = s05m.BRNCH_ID(+) AND s28m.WH_LOC_STUS_ID = s38m.STUS_CODE_ID(+) AND s28m.WH_LOC_UPD_USER_ID = s47m.USER_ID(+) AND s28m.WH_LOC_GB = crdc.code(+)
AND (s28m.WH_LOC_CODE like #{loccd}||'%' or s28m.WH_LOC_DESC like '%'||#{loccd}||'%')
AND s28m.WH_LOC_DESC like '%'||#{locdesc}||'%'
AND s28m.WH_LOC_BRNCH_ID = #{branch}
AND s28m.WH_LOC_STUS_ID = #{status}
AND s28m.WH_LOC_ID = #{warelocid}
AND WH_LOC_GB IN
#{item}
AND s28m.WH_LOC_STK_GRAD = #{locgrad}
ORDER BY WH_LOC_CODE

mapper.update:
UPDATE SYS0028M
   SET WH_LOC_CODE           = #{loccd}
     , WH_LOC_DESC           = #{locdesc}
