MERGE INTO LOG0054M A
     USING DUAL
        ON (DELVRY_NO = #{delno}) 
      WHEN MATCHED THEN
             UPDATE SET
                        A.DOC_HDER_TXT= #{headtxt}
                      , A.UPD_USER_ID = #{userId}
                      , A.UPD_DT      = SYSDATE
      WHEN NOT MATCHED THEN              
             INSERT (
                        DELVRY_NO
                      , TRNSC_TYPE
                      , TRNSC_TYPE_DTL
                      , DELVRY_DT
                      , CRT_USER_ID
                      , CRT_DT
                    ) VALUES(
                        #{delno}
                      , #{ttype}
                      , #{mtype}
                      , to_char(sysdate , 'yyyymmdd')
                      , #{userId}
                      , SYSDATE
                    )

