mapper.select:
SELECT A.MEM_CODE memCode, H.CODE_NAME memType, D.MEM_ORG_DESC orgDesc, A.FULL_NAME name, A.NRIC nric, NVL(B.LAST_DEPT_CODE, ' ') deptCode, NVL(B.LAST_GRP_CODE, ' ') grpCode, NVL(B.LAST_ORG_CODE, ' ') orgCode, F.NAME MEMBERSTUS, G.NAME USERSTUS, CASE WHEN C.CNFM = 1 THEN 'Signed' ELSE 'Unsigned' END CNFMSTUS, TO_CHAR(C.CNFM_DT, 'DD/MM/YYYY') CNFMDT, TO_CHAR(A.JOIN_DT, 'DD/MM/YYYY') JOINDT, CASE WHEN J.PROMO_TYPE_ID in ('747','748') THEN TO_CHAR(A.PROMO_DT, 'DD/MM/YYYY') ELSE '-' END PROMODT, I.VERSION_ID VERSIONID, I.FILE_NAME RPTFLNM FROM ORG0001D A JOIN ORG0005D B ON B.MEM_ID = A.MEM_ID JOIN ORG0003D C ON A.APLICNT_ID = C.APLCTN_ID JOIN ORG0012D D ON A.MEM_TYPE = D.MEM_TYPE_ID AND B.MEM_LVL = D.MEM_ORG_LVL JOIN SYS0047M E ON A.MEM_CODE = E.HR_CODE /* Member Status */ JOIN SYS0038M F ON A.STUS = F.STUS_CODE_ID /* User Status */ JOIN SYS0038M G ON E.USER_STUS_ID = G.STUS_CODE_ID /* Member Type Description */ JOIN SYS0013M H ON A.MEM_TYPE = H.CODE_ID AND H.CODE_MASTER_ID = 1 /* Agreement Version */ JOIN SYS0104M I ON A.MEM_TYPE = I.MEM_TYPE AND B.MEM_LVL = I.MEM_LVL
AND TO_DATE(TO_CHAR(C.CNFM_DT, 'YYYYMMDD'), 'YYYYMMDD') >= I.VALID_START_DT AND TO_DATE(TO_CHAR(C.CNFM_DT, 'YYYYMMDD'), 'YYYYMMDD') <= I.VALID_END_DT
LEFT JOIN ( SELECT * FROM ORG0007D WHERE PROMO_ID IN ( SELECT MAX(PROMO_ID) PROMO_ID FROM ORG0007D WHERE PROMO_TYPE_ID in ('747','748') GROUP BY MEM_ID ) ) J ON A.MEM_ID = J.MEM_ID WHERE A.MEM_TYPE = #{memTypeCom}
AND A.MEM_CODE = #{code}
AND A.NRIC = #{icNum}
AND A.STUS = #{memStusCmb}
AND A.FULL_NAME LIKE '%' || #{name} || '%'
AND B.LAST_DEPT_CODE = #{deptCode}
AND B.LAST_GRP_CODE = #{grpCode}
AND B.LAST_ORG_CODE = #{orgCode}
AND A.BRNCH = #{selectBranch}
AND B.MEM_LVL = #{memLevelCom}

SELECT A.MEM_CODE, A.NAME, A.NRIC, A.MEM_TYPE, D.ROLE_ID, TO_CHAR(E.CNFM_DT, 'DD-MON-YYYY') CNFM_DT, F.VERSION_ID, F.FILE_NAME FROM ORG0001D A JOIN ORG0005D B ON A.MEM_ID = B.MEM_ID JOIN SYS0047M C ON A.MEM_CODE = C.HR_CODE JOIN SYS0045M D ON C.USER_ID = D.USER_ID JOIN ( SELECT E1.APLICNT_ID, NVL(E3.CNFM, E2.CNFM) CNFM, NVL(E3.CNFM_DT, E2.CNFM_DT) CNFM_DT, CASE WHEN E1.MEM_TYPE = 1 THEN NVL(E3.CNTRCT_TYPE, E2.CNTRCT_TYPE) ELSE ' ' END CNTRCT_TYPE FROM ORG0001D E1 JOIN ORG0003D E2 ON E1.APLICNT_ID = E2.APLCTN_ID LEFT JOIN ( SELECT E3A.APLCTN_ID, CNFM, CNFM_DT, CNTRCT_TYPE FROM ORG0027D E3A JOIN ( SELECT MIN(HIST_APLCTN_ID) HIST_APLCTN_ID, APLCTN_ID FROM ORG0027D WHERE CNFM = 1
AND TO_CHAR(CNFM_DT, 'YYYYMMDD') >= #{startDt} AND TO_CHAR(CNFM_DT, 'YYYYMMDD') <= #{endDt}
GROUP BY APLCTN_ID ) E3B ON E3A.HIST_APLCTN_ID = E3B.HIST_APLCTN_ID GROUP BY E3A.APLCTN_ID, CNFM, CNFM_DT, CNTRCT_TYPE ) E3 ON E1.APLICNT_ID = E3.APLCTN_ID ) E ON A.APLICNT_ID = E.APLICNT_ID /* AND (CASE WHEN A.MEM_TYPE = 1 THEN TO_CHAR(D.ROLE_ID) ELSE ' ' END) = E.CNTRCT_TYPE */
AND TO_CHAR(E.CNFM_DT, 'YYYYMMDD') >= #{startDt} AND TO_CHAR(E.CNFM_DT, 'YYYYMMDD') <= #{endDt} JOIN SYS0104M F ON A.MEM_TYPE = F.MEM_TYPE AND B.MEM_LVL = F.MEM_LVL AND TO_DATE(TO_CHAR(E.CNFM_DT, 'YYYYMMDD'), 'YYYYMMDD') >= F.VALID_START_DT AND TO_DATE(TO_CHAR(E.CNFM_DT, 'YYYYMMDD'), 'YYYYMMDD') <= F.VALID_END_DT
WHERE A.MEM_CODE = #{memCode}

SELECT B.BILL_AMT VERSION FROM ORG0001D A INNER JOIN PAY0007D B ON A.MEM_ID = B.BILL_MEM_ID WHERE A.MEM_CODE = #{memID}
AND A.MEM_TYPE = #{memType}
AND B.BILL_TYPE_ID = '222' ORDER BY A.MEM_ID DESC

SELECT MEM_ORG_LVL CODE_ID, MEM_ORG_LVL CODE, MEM_ORG_DESC_CODE || ' - ' || MEM_ORG_DESC CODE_NAME FROM ORG0012D
WHERE MEM_TYPE_ID = #{groupCode}
WHERE MEM_TYPE_ID = #{userTypeId}
ORDER BY CODE
