mapper.select:
SELECT A.MEM_CODE memCode, A.MEM_TYPE memType, A.FULL_NAME name, A.NRIC nric, B.DEPTCODE deptCode, B.GRPCODE grpCode, B.ORGCODE orgCode, E.ROLE_DESC userRole, F.NAME status FROM ORG0001D A INNER JOIN ( SELECT B1.MEM_ID, CASE WHEN B1.DEPT_CODE = '' THEN B1.LAST_DEPT_CODE ELSE B1.DEPT_CODE END AS DEPTCODE, B1.LAST_GRP_CODE GRPCODE, B1.LAST_ORG_CODE ORGCODE, B1.MEM_LVL MEMLVL FROM ORG0005D B1 ) B ON B.MEM_ID = A.MEM_ID INNER JOIN SYS0047M C ON C.USER_NAME = A.MEM_CODE INNER JOIN SYS0045M D ON D.USER_ID = C.USER_ID INNER JOIN SYS0044M E ON E.ROLE_ID = D.ROLE_ID INNER JOIN SYS0038M F ON F.STUS_CODE_ID = C.USER_STUS_ID WHERE A.MEM_TYPE = #{memTypeCom}
AND A.MEM_CODE LIKE '%' || #{code} || '%'
AND A.FULL_NAME LIKE '%' || #{name} || '%'
AND A.NRIC LIKE '%' || #{icNum} || '%'
AND B.DEPTCODE LIKE '%' || #{deptCode} || '%'
AND B.GRPCODE LIKE '%' || #{grpCode} || '%'
AND B.ORGCODE LIKE '%' || #{orgCode} || '%'
AND C.USER_STUS_ID = #{memStusCmb}
AND B.MEMLVL = #{memLevelCom}
AND A.BRNCH = #{selectBranch}
ORDER BY B.ORGCODE, B.GRPCODE, B.DEPTCODE, B.MEMLVL, C.USER_STUS_ID, A.MEM_CODE

SELECT B.BILL_AMT VERSION FROM ORG0001D A INNER JOIN PAY0007D B ON A.MEM_ID = B.BILL_MEM_ID WHERE A.MEM_CODE = #{memID}
AND A.MEM_TYPE = #{memType}
ORDER BY A.MEM_ID DESC

SELECT MEM_ORG_LVL CODE_ID, MEM_ORG_LVL CODE, MEM_ORG_DESC_CODE || ' - ' || MEM_ORG_DESC CODE_NAME FROM ORG0012D
WHERE MEM_TYPE_ID = #{groupCode}
WHERE MEM_TYPE_ID = #{userTypeId}
ORDER BY CODE

SELECT APLICNT_AGMTDT SIGNDT
FROM ORG0003D
WHERE MEM_CODE = #{memID}
