mapper.select:
select E1.ASSET_ID assetId, E1.BRAND_ID brandId, l49.name, E1.CTGRY_ID ctgryId, s13c.code_name codeName, E1.COLOR_ID colorId, s13r.code_name codeName1, TO_CHAR(E1.CRT_DT, 'DD/MM/YYYY') crtDt, E1.CRT_USER_ID crtUserId, (select USER_NAME from SYS0047M where user_id = e1.CRT_USER_ID) userName , E1.CURR_BRNCH_ID currBrnchId, E1.CURR_DEPT_ID CURR_DEPT_ID, E1.CURR_USER_ID CURR_USER_ID, E1.IMEI_NO imeiNo, E1.MAC_ADDR macAddr, E1.NAME NAME1, E1.PURCHS_AMT purchsAmt, TO_CHAR(E1.PURCHS_DT, 'DD/MM/YYYY') purchsDt, E1.REF_NO refNo, E1.ASSET_REM assetRem, E1.SERIAL_NO serialNo, E1.STUS_ID stusId, s38.name name2, E1.TYPE_ID typeId, s13t.code_name codeName2, TO_CHAR(E1.UPD_DT, 'DD/MM/YYYY') updDt, E1.UPD_USER_ID updUserId, (select USER_NAME from SYS0047M where user_id = e1.UPD_USER_ID) userName1 , E1.WRANTY_NO wrantyNo, E1.DEALER_ID dealerId, s30.DEALER_NAME dealerName, E1.INVC_NO invcNo, CASE WHEN (s05m.BRNCH_ID IS NOT NULL) THEN s05m.CODE ELSE NULL END branch , CASE WHEN (s40.DEPT_ID IS NOT NULL) THEN s40.DEPT_NAME ELSE NULL END department, CASE WHEN (s47.USER_ID IS NOT NULL) THEN s47.USER_NAME ELSE NULL END userName2 FROM LOG0050M E1 , SYS0038M s38 , LOG0049M l49 , (select * from sys0013m where code_master_id = 108) s13c , (select * from sys0013m where code_master_id = 111) s13t , (select * from sys0013m where code_master_id = 112) s13r , SYS0005M s05m , SAL0030D s30 , SYS0040M s40 , SYS0047M s47
WHERE 1 = 1 and e1.stus_id = s38.STUS_CODE_ID and e1.brand_id = l49.brand_id and e1.ctgry_id = s13c.code_id and e1.type_id = s13t.code_id and e1.COLOR_ID = s13r.code_id and E1.CURR_BRNCH_ID = s05m.BRNCH_ID(+) and E1.DEALER_ID = s30.DEALER_ID(+) and E1.CURR_DEPT_ID = s40.DEPT_ID(+) and E1.CURR_USER_ID = s47.USER_ID(+)
AND E1.ASSET_ID = #{searchassetid}
AND E1.STUS_ID in
#{item}
AND E1.TYPE_ID in
#{item}
AND E1.CURR_DEPT_ID in
#{item}
AND l49.name = #{searchbrand}
AND E1.COLOR_ID = #{searchcolor}
AND E1.NAME = #{searchmodelname}
AND E1.PURCHS_DT >= TO_DATE(#{searchpurchasedate1},'DD/MM/YYYY') AND E1.PURCHS_DT <= TO_DATE(#{searchpurchasedate2},'DD/MM/YYYY')
AND E1.REF_NO = #{searchrefno}
AND E1.CURR_BRNCH_ID = #{searchbranchid}
AND E1.CURR_DEPT_ID = #{searchdeptid}
AND E1.INVC_NO = #{searchinvoiceno}
AND E1.DEALER_ID = #{searchdealer}
AND E1.SERIAL_NO = #{searchserialno}
AND E1.WRANTY_NO = #{searchwarrantyno}
AND E1.IMEI_NO = #{searchimeino}
AND E1.MAC_ADDR = #{searchmacaddress}
AND E1.CURR_USER_ID = #{searchcreator}
AND E1.CRT_DT >= TO_CHAR(TO_DATE(#{searchcreatedate1},'DD/MM/YYYY'),'YYYY-MM-DD') AND E1.CRT_DT < TO_CHAR(TO_DATE(#{searchcreatedate2},'DD/MM/YYYY'),'YYYY-MM-DD')
order by asset_id desc

