SELECT  /* [com.coway.trust.biz.logistics.barcodeRegister.impl.BarcodeRegisterApiMapper.selectBarcodeRegisterList] KR_HAN */
			A.SCAN_NO       SCAN_NO
			,A.SERIAL_NO    SERIAL_NO
			, A.ITM_CODE    ITM_CODE
FROM    LOG0099D A

			AND (A.SCAN_STUS_CODE = 'C' OR A.SCAN_STUS_CODE = 'D')

			AND A.DELVRY_NO = #{delvryNo}


			AND A.REQST_NO = #{reqstNo}


			AND A.ITM_CODE = #{itmCode}


			AND A.IO_TYPE = #{ioType}
