mapper.select:
SELECT l75.reqst_no AS reqstno , l75.seq , trand.code_name AS stkactivity , TO_CHAR(l75.reqst_crt_dt, 'YYYY/MM/DD') AS reqstdate , DBMS_LOB.SUBSTR (s28c.wh_loc_desc, 4000) AS frmloc , DBMS_LOB.SUBSTR (s28q.wh_loc_desc, 4000) AS toloc , l48.itm_code AS itmcode , l48.itm_name AS itmname , NVL(l75.reqst_qty, 0) AS reqstqty , NVL(l75.final_cmplt, 'N') AS status FROM log0075m l75 , log0047m l47 , log0048d l48 , (SELECT * FROM sys0013m WHERE code_master_id = 308) trand , sys0028m s28c , sys0028m s28q WHERE 1 = 1 AND l75.trnsc_type_dtl = trand.code AND l47.rciv_cdc_rdc = s28c.wh_loc_id AND l47.reqst_cdc_rdc = s28q.wh_loc_id AND l47.reqst_no = l75.reqst_no AND l48.reqst_no = l75.reqst_no AND l48.reqst_no_itm = l75.seq
AND l75.itm_code = #{itmCode}
AND l47.trnsc_type_dtl IN
#{item}
AND TO_CHAR(l75.reqst_crt_dt, 'YYYYMMDD') BETWEEN TO_CHAR(TO_DATE(#{srchcrtdtfrom}, 'DD/MM/YYYY'), 'YYYYMMDD') AND TO_CHAR(TO_DATE(#{srchcrtdtto}, 'DD/MM/YYYY'), 'YYYYMMDD')
AND l75.final_cmplt = #{status}
AND l47.rciv_cdc_rdc = #{flocation}
AND l47.reqst_cdc_rdc = #{tlocation}
ORDER BY l75.reqst_no
