SELECT Project1.FILE_UPLOAD_ID FILE_UPLOAD_ID ,
TO_CHAR(Project1.CRT_DT, 'DD/MM/YYYY') CRT_DT, Project1.CRT_USER_ID CRT_USER_ID , Project1.C1 C1 , Project1.FILE_ET FILE_ET , Project1.FILE_NAME FILE_NAME , Project1.FILE_URL FILE_URL , Project1.IS_CODY IS_CODY , Project1.IS_HP IS_HP , Project1.IS_STAFF IS_STAFF , Project1.FILE_TYPE_LBL FILE_TYPE_LBL , Project1.STUS_CODE_ID STUS_CODE_ID , Project1.FILE_TYPE_ID FILE_TYPE_ID , Project1.CODE_NAME CODE_NAME , Project1.UPD_DT UPD_DT , Project1.UPD_USER_ID UPD_USER_ID , Project1.C2 C2 FROM ( SELECT Project1.FILE_UPLOAD_ID FILE_UPLOAD_ID , Project1.FILE_TYPE_ID FILE_TYPE_ID , Project1.FILE_NAME FILE_NAME , Project1.STUS_CODE_ID STUS_CODE_ID , Project1.FILE_URL FILE_URL , Project1.CRT_DT CRT_DT , Project1.CRT_USER_ID CRT_USER_ID , Project1.UPD_DT UPD_DT , Project1.UPD_USER_ID UPD_USER_ID , Project1.IS_HP IS_HP , Project1.IS_CODY IS_CODY , Project1.FILE_TYPE_LBL FILE_TYPE_LBL , Project1.IS_STAFF IS_STAFF , Project1.FILE_ET FILE_ET , Project1.CODE_NAME CODE_NAME , Project1.C1 C1 , Project1.C2 C2 , ROW_NUMBER() OVER ( ORDER BY Project1.FILE_NAME ASC ) ROW_NUMBER FROM ( SELECT Extent1.FILE_UPLOAD_ID FILE_UPLOAD_ID , Extent1.FILE_TYPE_ID FILE_TYPE_ID , Extent1.FILE_NAME FILE_NAME , Extent1.STUS_CODE_ID STUS_CODE_ID , Extent1.FILE_URL FILE_URL , Extent1.CRT_DT CRT_DT , Extent1.CRT_USER_ID CRT_USER_ID , Extent1.UPD_DT UPD_DT , Extent1.UPD_USER_ID UPD_USER_ID , Extent1.IS_HP IS_HP , Extent1.IS_CODY IS_CODY , Extent1.FILE_TYPE_LBL FILE_TYPE_LBL , Extent1.IS_STAFF IS_STAFF , Extent1.FILE_ET FILE_ET , Extent2.CODE_NAME CODE_NAME , CASE WHEN ( Extent3.USER_ID IS NOT NULL ) THEN Extent3.USER_NAME ELSE '' END C1 , CASE WHEN ( Extent4.USER_ID IS NOT NULL ) THEN Extent4.USER_NAME ELSE '' END C2 FROM PAY0061D Extent1 JOIN SYS0013M Extent2 ON Extent2.CODE_ID = Extent1.FILE_TYPE_ID LEFT JOIN SYS0047M Extent3 ON Extent3.USER_ID = Extent1.CRT_USER_ID LEFT JOIN SYS0047M Extent4 ON Extent4.USER_ID = Extent1.UPD_USER_ID WHERE ( 1 = Extent1.STUS_CODE_ID )
AND Extent1.FILE_TYPE_LBL = #{searchTypeLabel}
AND Extent1.FILE_NAME = #{searchFilename}
AND Extent1.FILE_TYPE_ID IN
#{item}
AND ( Extent1.IS_STAFF = 1 OR Extent1.CRT_USER_ID = 52366 ) ) Project1 ) Project1 WHERE Project1.ROW_NUMBER > 0 ORDER BY Project1.FILE_UPLOAD_ID DESC

