SELECT ROWNUM RNUM ,S28.WH_LOC_ID ,S28.WH_LOC_CODE ,S28.WH_LOC_DESC ,L83.PLANT ,L83.BL_NO ,L84.ITM_SEQ ,L84.MATRL_NO ,S26.STK_TYPE_ID , (SELECT CODE_NAME FROM SYS0013M WHERE CODE_ID = S26.STK_TYPE_ID) TYPENAME ,S26.STK_CTGRY_ID , (SELECT CODE_NAME FROM SYS0013M WHERE CODE_ID = S26.STK_CTGRY_ID) CTGRYNAME ,S26.STK_DESC STKDESC ,S26.STK_ID STKID ,L84.QTY ,NVL (REQ.REQST_QTY, 0) REQED_QTY , (L84.QTY - NVL (REQ.REQST_QTY, 0) ) AVRQTY ,L84.UOM , (SELECT CODE_NAME FROM SYS0013M WHERE CODE_ID = L84.UOM) UOMNM , L83.SHIP_DT , L83.GR_DT , L83.AP_CMPLT , L83.GR_CMPLT , L84.PUR_DOC_NO , L83.ACC_NO , L84.FREE_ITM , L83.INV_CRT_DT , L83.DEL_FLAG FROM LOG0083M L83 ,LOG0084D L84 ,SYS0026M S26 ,SYS0028M S28 , (SELECT L48.ITM_CODE ,SUM (L48.REQST_QTY) REQST_QTY ,L48.BL_NO ,L48.BL_ITM_SEQ FROM LOG0047M L47, LOG0048D L48 WHERE L47.REQST_NO = L48.REQST_NO AND NVL (L47.REQST_DEL, 'N') <> 'Y' AND L48.BL_NO IS NOT NULL AND L48.BL_ITM_SEQ IS NOT NULL GROUP BY L48.ITM_CODE, L48.BL_NO, L48.BL_ITM_SEQ) REQ WHERE L83.AP_CMPLT = 'X' AND L83.GR_CMPLT = 'X' AND NVL (L83.DEL_FLAG, 'Y') <> 'X'
AND S28.WH_LOC_GB = '06' AND L83.BL_NO = L84.BL_NO AND L83.INV_NO = L84.INV_NO AND L84.MATRL_NO = S26.STK_CODE AND L83.PLANT = S28.PLANT AND L84.BL_NO = REQ.BL_NO(+) AND L84.ITM_SEQ = REQ.BL_ITM_SEQ(+) AND L84.MATRL_NO = REQ.ITM_CODE(+)
AND L83.INV_NO LIKE #{invno}||'%'
AND L83.BL_NO LIKE #{blno}||'%'
AND S28.WH_LOC_ID = #{location}
AND L83.GR_DT BETWEEN TO_CHAR(TO_DATE(#{grsdt},'DD/MM/YYYY'),'YYYYMMDD') AND TO_CHAR(TO_DATE(#{gredt},'DD/MM/YYYY'),'YYYYMMDD')
AND L83.SHIP_DT BETWEEN TO_CHAR(TO_DATE(#{blsdt},'DD/MM/YYYY'),'YYYYMMDD') AND TO_CHAR(TO_DATE(#{bledt},'DD/MM/YYYY'),'YYYYMMDD')
AND L84.PUR_DOC_NO = #{pono}
AND L84.MATRL_ID/CODE = #{materialcode}
and s26.STK_TYPE_ID in
#{item}
and s26.STK_CTGRY_ID in
#{item}
ORDER BY L83.BL_NO, L84.ITM_SEQ
