SELECT l82.STK_ID parts_Id,s26.STK_CODE parts_Code,s26.STK_DESC parts_Name,s29.NAME cust_Name,l82.QTY used_Qty,(SELECT CODE_NAME FROM SYS0013M WHERE CODE_ID=s26.UOM) currency,l82.OLD_SERIAL serial_No,l82.NEW_SERIAL FROM log0082m l82, SVC0008D s08, org0001d o01,sys0026m s26, sal0029d s29 WHERE 1 = 1 AND s08.cody_id = o01.mem_id AND l82.STK_ID=s26.STK_ID AND o01.mem_code = #{userId} AND l82.svc_ord_id = s08.no AND l82.CUST_ID=s29.CUST_ID
and nvl(l82.CMPLT_YN , 'N') <> 'N'
and nvl(l82.CMPLT_YN , 'N') <> 'Y'
AND TO_CHAR (SVC_DT, 'YYYYMMDD') BETWEEN #{searchFromDate}
AND #{searchToDate}

SELECT L43M.MATRL_NO,n26m.STK_ID parts_Id,L44D.BOM_COMPNT,n26m.STK_CODE parts_Code,n26m.STK_DESC parts_Name,l56.qty stock_Qty,l56.serial_no serial_No,s28m.WH_LOC_CODE location_Code,s28m.WH_LOC_DESC location_Name,s28m.WH_LOC_BRNCH_ID branch_Code, (select WH_LOC_ID from sys0028m where WH_LOC_CODE= s28m.rdc_code) rdc_code FROM LOG0043M L43M,LOG0044D L44D,sys0026m s26m,sys0026m n26m,sys0028m s28m, (SELECT log0056m.*, log0063d.serial_no FROM log0056m, log0063d WHERE 1 = 1 AND log0056m.stk_code = log0063d.MATNR(+) AND log0056m.loc_id = (select wh_loc_id from sys0028m where wh_loc_code = #{userId})) l56 WHERE 1=1 AND n26m.STK_CODE=l56.stk_code(+) AND l56.LOC_ID=s28m.WH_LOC_ID(+) AND L43M.BOM=L44D.BOM AND L43M.MATRL_NO=s26m.STK_CODE AND L44D.BOM_COMPNT=n26m.STK_CODE
AND MATRL_NO=#{productCode}
AND n26m.STK_CODE =#{searchKeyword}
AND LTRIM(RTRIM(UPPER(n26m.STK_DESC))) LIKE UPPER(#{searchKeyword}) || '%'
AND s26m.STK_ID=#{productId}
group by L43M.MATRL_NO,n26m.STK_ID,L44D.BOM_COMPNT,n26m.STK_CODE,n26m.STK_DESC,l56.qty,l56.serial_no,s28m.rdc_code,s28m.WH_LOC_CODE,s28m.WH_LOC_DESC,s28m.WH_LOC_BRNCH_ID

SELECT l55d.DELVRY_NO, l55d.ITM_CODE PARTS_CODE, l55d.ITM_NAME PARTS_NAME, l61d.SERIAL_NO SERIAL_NO, l55d.DELVRY_QTY REQUEST_QTY, l55d.DELVRY_NO_ITM SMO_NO_ITEM, l61d.PDELVRY_NO_ITM,
s26m.STK_ID PARTS_ID FROM LOG0055D l55d, LOG0061D l61d,sys0026m s26m WHERE l55d.DELVRY_NO = l61d.DELVRY_NO AND l55d.DELVRY_NO_ITM = l61d.PDELVRY_NO_ITM AND l55d.ITM_CODE=s26m.STK_CODE AND l61d.DELVRY_NO = #{smoNo}
ORDER BY l61d.PDELVRY_NO_ITM, l61d.SERIAL_NO

SELECT s28m.WH_LOC_STK_GRAD STOCK_GRADE, l56.STK_CODE PARTS_CODE, l56.STK_DESC PARTS_NAME, l56.STK_TYPE_ID, l56.STK_CTGRY_ID, l56.QTY STOCK_QTY, S26.STK_ID PARTS_ID, L63.SERIAL_NO, (select WH_LOC_ID from sys0028m where WH_LOC_CODE= s28m.rdc_code) rdc_code FROM sys0028m s28m, LOG0056M L56 , SYS0026M S26 , LOG0063D L63 WHERE 1 =1 AND L56.STK_CODE = L63.MATNR(+) AND L56.LOC_ID = L63.LOC_ID(+) AND L56.STK_CODE = S26.STK_CODE AND L56.STK_TYPE_ID = 61 AND L56.QTY > 0 AND L56.LOC_ID=s28m.WH_LOC_ID
AND l56.STK_CODE=#{searchKeyword}
AND UPPER(l56.STK_DESC) LIKE UPPER(#{searchKeyword}) || '%'
AND s28m.WH_LOC_CODE= #{userId}

select L56.STK_CODE parts_Code, L56.STK_DESC parts_Name, L56.QTY qty, S26.STK_ID parts_Id, s.WH_LOC_DESC cust_Name, s.WH_LOC_ID from log0056m l56 , sys0028m s , sys0026m s26 , ( select * from SYS0013M where CODE_MASTER_ID = '11' ) type where 1 = 1 and loc_id = s.wh_loc_id and l56.stk_code = s26.stk_code and s26.stk_ctgry_id = type.code_id and s26.STK_TYPE_ID = '2687'
AND S26.STK_CODE= #{searchKeyword}
AND UPPER(S26.STK_DESC) LIKE UPPER(#{searchKeyword}) || '%'
AND type.CODE_ID=#{searchItem}
and s.wh_loc_code = ( select nvl(rdc_code , wh_loc_code) from sys0028m s where 1= 1 and s.wh_loc_code = #{searchLocation} )

select S26T.STK_ID parts_Id,l44.BOM_COMPNT parts_Code,S26T.STK_DESC parts_Name,S26T.STK_CTGRY_ID,S26T.STK_TYPE_ID,1 price_Unit,s16.AMT parts_Price, (select CODE_NAME from sys0013m where CODE_MASTER_ID ='94' AND CODE_ID='1150') currency from log0043m l43, log0044d l44,SAL0016M s16 , SYS0026M s26t where 1=1 AND S26T.STK_ID = s16.STK_ID AND l44.BOM_COMPNT = S26T.stk_code(+) AND l43.bom=l44.bom AND l43.MATRL_NO=#{productCode}
AND s26t.STK_TYPE_ID =#{searchKinds}
AND S26T.STK_CODE=#{searchKeyword}
AND (LTRIM(RTRIM(S26T.STK_DESC))) LIKE #{searchKeyword}||'%'
group by S26T.STK_ID,l44.BOM_COMPNT,S26T.STK_DESC,S26T.STK_CTGRY_ID,S26T.STK_TYPE_ID,1,s16.AMT

