USER_NAME = #{userId}) SVC, LOG0056M BAL WHERE 1 = 1 AND SVC.STK_CODE = BAL.STK_CODE(+) AND SVC.WH_LOC_ID = BAL.LOC_ID(+) AND S87.SRV_FILTER_STK_ID = BOM.FILTERID AND S87.SRV_FILTER_STK_ID = SVC.BS_RESULT_PART_ID AND S02.ITM_STK_ID = BOM.STK_ID AND S01.SALES_ORD_ID = SVC.SALES_ORD_ID AND S01.SALES_ORD_ID = S02.SALES_ORD_ID AND S01.SALES_ORD_ID = S90.SRV_SO_ID AND S90.SRV_CONFIG_ID = S87.SRV_CONFIG_ID AND S87.SRV_FILTER_STUS_ID = '1'
AND BOM.BOM_COMPNT=#{partsCode}
AND BOM.FILTERID=#{partsId}
AND TO_CHAR (ADD_MONTHS (S87.SRV_FILTER_PRV_CHG_DT, S87.SRV_FILTER_PRIOD), 'yyyymmdd') BETWEEN #{searchFromDate}
AND #{searchToDate}
) T GROUP BY BOM, FILTERID, BS_RESULT_PART_ID, BOM_COMPNT, FILTERNM, COMPNT_QTY, ALTERNATIVEYN, WH_LOC_ID, NAME, MEM_CODE
)

