mapper.select:
/* [com.coway.trust.biz.logistics.prefixConversion.impl.prefixConversionMapper.searchPrefixConfigList] - TOMMY */ SELECT A.PREFIX_CONFIG_ID, A.PREFIX_STK_ID, B.STK_CODE AS PREFIX_STK_CODE, B.STK_DESC AS PREFIX_STK_DESC, A.PREFIX_CONV_STK_ID, C.STK_CODE AS PREFIX_CONV_STK_CODE, C.STK_DESC AS PREFIX_CONV_STK_DESC, A.USE_YN, A.CRT_USER_ID, D.USER_NAME AS CRT_USER, TO_CHAR(A.CRT_DT,'DD/MM/YYYY') CRT_DT FROM LOG0205M A LEFT JOIN SYS0026M B ON A.PREFIX_STK_ID = B.STK_ID LEFT JOIN SYS0026M C ON A.PREFIX_CONV_STK_ID = C.STK_ID LEFT JOIN SYS0047M D ON A.CRT_USER_ID = D.USER_ID WHERE 1=1
AND B.STK_ID = #{prefixProdId}
AND C.STK_ID = #{prefixConvProdId}
ORDER BY A.PREFIX_CONFIG_ID DESC

/*
[com.coway.trust.biz.logistics.prefixConversion.impl.prefixConversionMapper.selectPrefixConversionByProdId]
- TOMMY */
SELECT
A.PREFIX_CONFIG_ID
FROM LOG0205M A
WHERE 1=1

	AND A.PREFIX_STK_ID = #{prefixProdId}


	AND A.PREFIX_CONV_STK_ID = #{prefixConvProdId}

ORDER BY A.PREFIX_CONFIG_ID DESC
