mapper.select:
SELECT ROWNUM rnum , S62.PST_SALES_ORD_ID psoid, S62.PST_REF_NO psono, S62.PST_STUS_ID pststus, S62.CRT_DT crtdt , TO_CHAR(S62.CRT_DT , 'YYYYMMDDhh24miss') crtdt1 , S62.PST_DEALER_ID dealerid , S62.PST_TYPE psttypeid , PTYPE.CODE_NAME psttype , S62.PST_CUST_PO pstpo , S62.PIC pic , S38.CODE pststuscd , S38.NAME pststusnm , S30.DEALER_NAME dealernm , S30.DEALER_NRIC nric, S30.DEALER_TYPE dtype , S62.PST_LOC_ID pstlocid , S28.WH_LOC_ID locid , S28.WH_LOC_CODE loccode , S28.WH_LOC_DESC locnm , S63.PST_ITM_STK_ID itmid , S26.STK_CODE itmcd , s26.STK_DESC itmnm , S26.UOM uom , UOM.CODE uomcd , S63.PST_ITM_REQ_QTY qty , S63.PST_ITM_DO_QTY doqty, S63.PST_ITM_BAL_QTY balqty , S63.PST_ITM_PRC itmprc , 0 reqqty , S62.CRT_USER_ID userid , S26.SERIAL_CHK serialchk , S62.PST_CUR_RATE pcr , S62.PST_CUR_TYPE_ID pcti, CUR.CODE pctcd , S32.CNT_NAME cntname FROM SAL0062D S62 , SAL0063D S63 , SYS0038M S38 , SAL0030D S30 , SAL0032D S32 , SYS0028M S28 , SYS0026M S26 , (SELECT * FROM SYS0013M WHERE CODE_MASTER_ID = 358) PTYPE , (SELECT * FROM SYS0013M WHERE CODE_MASTER_ID = 42) UOM , (SELECT * FROM SYS0013M WHERE CODE_MASTER_ID = 94) CUR WHERE 1 = 1 AND S62.PST_LOC_ID = S28.WH_LOC_ID AND S26.UOM = UOM.CODE_ID AND S63.PST_ITM_STK_ID = S26.STK_ID AND S62.PST_TYPE = PTYPE.CODE_ID AND S62.PST_CUR_TYPE_ID = CUR.CODE_ID AND S30.DEALER_ID = S32.DEALER_ID AND S62.PST_DEALER_ID = S30.DEALER_ID AND S62.PST_STUS_ID = S38.STUS_CODE_ID AND S62.PST_SALES_ORD_ID = S63.PST_SALES_ORD_ID
AND S62.PST_REF_NO = #{pstno}
AND S62.PST_STUS_ID in
#{item}
AND S62.CRT_DT between to_date(#{crtdtfr}||'000000' , 'dd/mm/yyyyhh24miss') and to_date(#{crtdtto}||'235959' , 'dd/mm/yyyyhh24miss')
AND S62.PST_DEALER_ID = #{dealerid}
AND UPPER(S30.DEALER_NAME) LIKE '%'||UPPER(#{dealernm})||'%'
AND S30.DEALER_NRIC = #{nricno}
AND S62.PST_TYPE IN
#{item}
AND S62.PST_CUST_PO = #{custno}
AND S62.PIC = #{pic}
order by psoid desc

mapper.update:
UPDATE LOG0056M
   SET QTY      =
    
   QTY + #{REQQTY}
   
    
   QTY - #{REQQTY}
   
 WHERE LOC_ID   = #{FRLOCID}
   AND STK_CODE = #{ITMCD}

mapper.insert:
    INSERT INTO LOG0063D (SERIAL_NO, MATNR, LOC_ID, GLTRI, LWEDT, SWAOK, CRT_DT, CRT_USER_ID)
     SELECT SERIAL_NO , #{ITMCD} , #{FRLOCID} , to_char(sysdate,'yyyymmdd') , to_char(sysdate,'yyyymmdd') , 'Y' , sysdate , #{userid} 
       FROM LOG0061D L61, LOG0048D L48
	  WHERE L61.DELVRY_NO      = L48.REQST_NO
	    AND L61.PDELVRY_NO_ITM = L48.REQST_NO_ITM
	    AND L48.ITM_CODE       = #{ITMCD} 
	    AND L61.DELVRY_NO      = #{REQNO}

mapper.update:
    update SAL0063D
	   set PST_ITM_DO_QTY   = #{reqqty}
	     , PST_ITM_BAL_QTY  = PST_ITM_BAL_QTY - #{reqqty} 
	 where PST_SALES_ORD_ID = #{psoid}
	   and PST_ITM_STK_ID   = #{itmid}

    UPDATE SAL0062D 
	   SET PST_STUS_ID      = #{pststatusid}
	 WHERE PST_SALES_ORD_ID = #{psoid}

mapper.insert:
INSERT INTO SAL0059D (PST_DO_ID, PST_DO_REF_NO, PST_SALES_ORD_ID, PST_DO_STK_TRANS_LOC_FROM_ID, PST_DO_TOT_AMT, PST_DO_REM, PST_DO_STUS_ID, PST_DO_TYPE_ID, CRT_DT, CRT_USER_ID, INVC_NO)
             VALUES (#{pstdoid}
                    , #{reqstno}, #{psoid}, #{locid}, #{totalprice}, #{htext}, #{dostatusid}, #{psttype}, SYSDATE, #{userid}, 'PST'||LPAD((select max(PST_DO_ID)+1 from SAL0059D) , 7 , 0))
