mapper.select:
/* [com.coway.trust.biz.logistics.report.impl.CCDReportMapper.selectIssuerBankReportJsonList] KR_HAN */ WITH DAY_LIST AS ( SELECT LEVEL DD FROM dual CONNECT BY LEVEL <= TO_CHAR(LAST_DAY(TO_DATE(#{planYearMonth}, 'MM/YYYY')),'DD') -- PARAMETER ) SELECT DECODE(A.MODE_ID, -1, 'REG', -2, 'DD', C.CODE) AS MODE_CODE, NVL(D.CODE_NAME, C.CODE_NAME) AS MODE_DESC, CASE WHEN A.MODE_ID = -1 THEN '' WHEN A.BANK_ID = -1 THEN 'Others (combined)' ELSE E.CODE END AS BANK_CODE, A.*, C.CODE_NAME, D.CODE FROM (
SELECT * FROM ( SELECT X.DD, MODE_ID, CARD_TYPE_ID, BANK_ID, CNT FROM DAY_LIST X LEFT OUTER JOIN ( SELECT CASE WHEN MODE_ID IN ('108') THEN -2 WHEN MODE_ID IN ('105','106') THEN -1 ELSE MODE_ID END AS MODE_ID, CARD_TYPE_ID, CASE WHEN PAY_ITM_ISSU_BANK_ID IN (3, 21, 7, 6, 9, 5) THEN PAY_ITM_ISSU_BANK_ID ELSE -1 END AS BANK_ID, DD, SUM(NVL(CNT,0)) AS CNT FROM ( -- CRC: '107', REG: '105','106', DD: '108'-PAY64.OR_NO LIKE 'AER%' -- AUTO DEBIT SELECT PAY65.PAY_ITM_MODE_ID AS MODE_ID, NVL(PAY65.PAY_ITM_CARD_TYPE_ID, 0) CARD_TYPE_ID, PAY65.PAY_ITM_ISSU_BANK_ID, TO_CHAR(PAY64.PAY_DATA,'DD') AS DD, COUNT(DISTINCT SOM.SALES_ORD_ID) AS CNT FROM PAY0064D pay64 INNER JOIN PAY0065D PAY65 ON PAY64.PAY_ID = PAY65.PAY_ID INNER JOIN SAL0001D SOM ON pay64.SALES_ORD_ID = SOM.SALES_ORD_ID INNER JOIN SAL0071D RS ON RS.SALES_ORD_ID = SOM.SALES_ORD_ID LEFT OUTER JOIN SAL0003D SAL3D ON SAL3D.CNTRCT_SALES_ORD_ID = SOM.SALES_ORD_ID
WHERE 1 = 1 AND SOM.APP_TYPE_ID = '66' -- rental AND SOM.STUS_CODE_ID = '4' -- Order Status = COM AND RS.STUS_CODE_ID IN ('REG','INV') -- Open Rental Status = REG , INV AND SOM.PV_YEAR <> TO_CHAR(SYSDATE, 'YYYY') AND SOM.PV_MONTH <> TO_CHAR(SYSDATE, 'MM') --exclude current month Net Sales AND NVL(SAL3D.CNTRCT_OBLIGT_PRIOD,0) BETWEEN 1 AND 60 -- Billing month = 1-60
AND PAY64.PAY_DATA BETWEEN TO_DATE(TO_CHAR(TRUNC(to_date( #{planYearMonth}, 'MM/YYYY'),'MM'),'DD/MM/YYYY'), 'DD/MM/YYYY') AND TO_DATE(to_char( LAST_DAY(to_date( #{planYearMonth}, 'MM/YYYY')), 'DD/MM/YYYY'), 'DD/MM/YYYY') +1 -- Search condition : PARAMETER
AND PAY65.PAY_ITM_MODE_ID IN ('105','106', '107','108') -- Cash, Cheque, CRC AND NOT ( PAY65.PAY_ITM_MODE_ID = '108' -- Online Payment AND PAY64.OR_NO NOT LIKE 'AER%' -- AUTO DEBIT )
GROUP BY PAY65.PAY_ITM_MODE_ID, NVL(PAY65.PAY_ITM_CARD_TYPE_ID, 0), PAY65.PAY_ITM_ISSU_BANK_ID, TO_CHAR(PAY64.PAY_DATA,'DD')
) GROUP BY CASE WHEN MODE_ID IN ('108') THEN -2 WHEN MODE_ID IN ('105','106') THEN -1 ELSE MODE_ID END, CARD_TYPE_ID, CASE WHEN PAY_ITM_ISSU_BANK_ID IN (3, 21, 7, 6, 9, 5) THEN PAY_ITM_ISSU_BANK_ID ELSE -1 END, DD
) Y ON X.DD = Y.DD
) PIVOT ( SUM(CNT) FOR DD IN( 1 D1, 2 D2, 3 D3, 4 D4, 5 D5, 6 D6, 7 D7, 8 D8, 9 D9, 10 D10, 11 D11, 12 D12, 13 D13, 14 D14, 15 D15, 16 D16, 17 D17, 18 D18, 19 D19, 20 D20, 21 D21, 22 D22, 23 D23, 24 D24, 25 D25, 26 D26, 27 D27, 28 D28, 29 D29, 30 D30, 31 D31) ) ) A LEFT OUTER JOIN SYS0013M C ON A.MODE_ID = C.CODE_ID LEFT OUTER JOIN SYS0013M D ON A.CARD_TYPE_ID = D.CODE_ID LEFT OUTER JOIN SYS0004M E ON A.BANK_ID = E.BANK_ID ORDER BY DECODE(A.MODE_ID, -1, 'REG', -2, 'DD', C.CODE), CARD_TYPE_ID, E.CODE
