mapper.select:
SELECT ROWNUM rnum, l82.SEQ, l82.SVC_ORD_ID service_order, s01.SALES_ORD_NO order_no, l82.CUST_ID customer, s29.NAME customer_name,
TO_CHAR(s06.SETL_DT,'YYYY/MM/DD') service_date,
(select STK_CODE from sys0026m where STK_ID=l82.STK_ID) material_code, s26.STK_CODE material_code_actual, s26.STK_DESC material_name, l82.STK_ID,
l82.OLD_SERIAL serial_number, l82.QTY qty, l82.COMMENTS text, l82.CMPLT_YN return_complete,
TO_CHAR(l82.CMPLT_DT,'YYYY/MM/DD') return_complete_date,
l82.RESN_CODE no_parts_return, l82.CRT_DT, l82.CRT_USER_ID, l82.UPD_DT, l82.UPD_USER_ID, S26.SERIAL_CHK, S26.STK_TYPE_ID, l82.STK_ID_NEW, o01.BRNCH, o01.MEM_CODE, o01.NAME, (select CODE from SYS0005M where BRNCH_ID = o01.BRNCH ) code, (select NAME from SYS0005M where BRNCH_ID = o01.BRNCH ) brnch_name ,s13.code_id unmatch_id ,s13.code_name unmatch_reason ,s38.NAME pend_scan_name ,case when l82.pend_scan = '44' then 'Y' else '' end pend_scan from LOG0082M l82 join sal0029d s29 on l82.CUST_ID=s29.CUST_ID join sys0026m s26 on l82.STK_ID=s26.STK_ID join org0001d o01 on l82.MEM_ID = o01.MEM_ID join SVC0008D s08 on l82.SVC_ORD_ID=s08.NO join SAL0001D s01 on s08.SALES_ORD_ID=s01.SALES_ORD_ID join SVC0006D s06 on s06.RESULT_ID=l82.RESULT_ID join SVC0007D s07 on s07.bs_result_id = s06.RESULT_ID and s07.BS_RESULT_PART_ID = l82.STK_ID left join sys0013m s13 on s13.code = to_char(s07.FILTER_SRL_UNMATCH_RSN) and s13.code_master_id='511' left join sys0038m s38 on s38.stus_code_id = l82.pend_scan
JOIN LOG0082M SM ON SM.RESULT_ID = (SELECT MAX(aa.RESULT_ID) from LOG0082M aa where aa.SVC_ORD_ID = SM.SVC_ORD_ID) AND SM.SEQ = l82.SEQ left join ORG1001V o01v on o01v.MEM_ID = l82.MEM_ID
where 1=1 AND EXISTS ( select WW.RESULT_ID, sum(WW.qty) from LOG0082M WW WHERE RESULT_ID = SM.RESULT_ID group by WW.RESULT_ID having sum(WW.qty) <> 0 )
and o01.BRNCH = #{searchBranch}
and o01v.DEPT_CODE = (select dept_code from org0005d where mem_id = #{searchCM})
and o01.MEM_id = #{searchLoc}
and NVL(trim(s26.SERIAL_CHK),'N') = #{searchSerialChk}
and l82.pend_scan = #{searchSerialSts}
AND l82.SVC_DT >= to_date(#{servicesdt}|| '00:00:00', 'DD/MM/YYYY HH24:MI:SS')
AND l82.SVC_DT <= to_date(#{serviceedt}|| '23:59:59', 'DD/MM/YYYY HH24:MI:SS')
AND l82.SVC_ORD_ID = #{searchOder}
AND l82.CMPLT_YN= #{searchComplete}
AND l82.CMPLT_YN is null
AND s26.STK_CODE = #{searchMaterialCode}
AND l82.OLD_SERIAL = #{searchUsedSerial}
AND s13.code = #{sUnmatchReason}

mapper.update:
UPDATE LOG0082M SET
OLD_SERIAL=#{serialNumber}, QTY =#{qty}, RESN_CODE=#{noPartsReturn}, COMMENTS=#{text}, STK_ID=(select STK_ID from sys0026m where STK_CODE=#{materialCode}),
CMPLT_YN ='Y', CMPLT_DT=SYSDATE, UPD_DT = SYSDATE, UPD_USER_ID=#{userId}, CRT_DT = SYSDATE, CRT_USER_ID=#{userId} WHERE SEQ =#{seq}

mapper.select:
select BRNCH_ID code,code || '-' || NAME CODE_NAME
 from sys0005m where TYPE_ID in (42 , 43)

 and BRNCH_ID = #{userBranchId}

 ORDER BY CODE_NAME

mapper.update:
UPDATE LOG0082M
SET
    PEND_SCAN = #{pendSts},
    UPD_DT = SYSDATE,
    UPD_USER_ID=#{userId}
WHERE SEQ =#{seq}

mapper.select:
SELECT A.SEQ ,A.STK_ID ,B.STK_CODE ,B.STK_DESC FROM LOG0082M A LEFT JOIN SYS0026M B ON B.STK_ID = A.STK_ID JOIN SVC0006D S06 ON S06.RESULT_ID=A.RESULT_ID JOIN SVC0007D S07 ON S07.BS_RESULT_ID = S06.RESULT_ID AND S07.BS_RESULT_PART_ID = A.STK_ID WHERE A.PEND_SCAN = '44' AND NVL(S07.FILTER_SRL_UNMATCH_RSN,0) = 0 AND A.OLD_SERIAL = #{barcode} and MEM_ID = #{codyId}
AND A.SVC_DT >= to_date(#{servicesdt}|| '00:00:00', 'DD/MM/YYYY HH24:MI:SS')
AND A.SVC_DT <= to_date(#{serviceedt}|| '23:59:59', 'DD/MM/YYYY HH24:MI:SS')

mapper.update:
UPDATE LOG0082M
SET
    scan_no = #{scanNo},
    UPD_DT = SYSDATE,
    UPD_USER_ID=#{updUserId}
WHERE SEQ = #{seq}

UPDATE LOG0082M
SET
    scan_no = null,
    UPD_DT = SYSDATE,
    UPD_USER_ID=#{updUserId}
WHERE SEQ in (SELECT seq FROM LOG0082M
WHERE scan_no = #{scanNo}
)

UPDATE LOG0082M
SET
    CMPLT_YN ='Y',
    CMPLT_DT=SYSDATE,
    pend_scan = #{pendScan},
    UPD_DT = SYSDATE,
    UPD_USER_ID=#{updUserId}
WHERE SEQ in (SELECT seq FROM LOG0082M
WHERE scan_no = #{scanNo}
)
