mapper.select:
SELECT ROWNUM rnum, l82.SEQ, l82.SVC_ORD_ID service_order, l82.CUST_ID customer, s29.NAME customer_name,
TO_CHAR(l82.SVC_DT,'YYYY/MM/DD') service_date, s26.STK_CODE material_code, s26.STK_DESC material_name, l82.STK_ID, l82.OLD_SERIAL serial_number, l82.NEW_SERIAL serial, l82.QTY qty, l82.COMMENTS text, l82.CMPLT_YN return_complete,
TO_CHAR(l82.CMPLT_DT,'YYYY/MM/DD') return_complete_date,
l82.RESN_CODE no_parts_return, l82.CRT_DT, l82.CRT_USER_ID, l82.UPD_DT, l82.UPD_USER_ID, S26.SERIAL_CHK, S26.STK_TYPE_ID from LOG0082M l82,sal0029d s29,sys0026m s26 where 1=1 AND l82.CUST_ID=s29.CUST_ID AND l82.STK_ID=s26.STK_ID
AND l82.SVC_ORD_ID = #{searchOder}
AND s29.NAME = #{searchCustomer}
AND s26.STK_CODE = #{searchMaterialCode}
AND S26.STK_TYPE_ID = #{searchMaterialType}
AND l82.CMPLT_YN= #{searchComplete}
and to_char(l82.SVC_DT , 'yyyymmdd') >= to_char(to_date(#{servicesdt},'dd/mm/yyyy'),'yyyymmdd')
and to_char(l82.SVC_DT , 'yyyymmdd') <= to_char(to_date(#{serviceedt},'dd/mm/yyyy'),'yyyymmdd')
and to_char(l82.CMPLT_DT , 'yyyymmdd') >= to_char(to_date(#{returnsdt},'dd/mm/yyyy'),'yyyymmdd')
and to_char(l82.CMPLT_DT , 'yyyymmdd') <= to_char(to_date(#{returnedt},'dd/mm/yyyy'),'yyyymmdd')
