/* [com.coway.trust.biz.logistics.serialmgmt.SerialMgmtNewMapper.insertSerialInfo] - KR JIN */
SELECT FN_GET_LOG0099D_SCAN_NO() FROM DUAL
INSERT INTO LOG0099D -- Serial Info for Scan ( SCAN_NO --scan number , SERIAL_NO --serial number , SERIAL_SCAN --scan s/n > raw input after scan , BOX_NO --box number , ITM_CODE --Material code , SCAN_STUS_CODE --status of scan , REQST_NO --request number , REQST_NO_ITM --request item , REQST_DT --request date , FROM_LOC_ID --from location , TO_LOC_ID --to location , TRNSC_TYPE --Transaction Type , IO_TYPE --In/Out , UOM --Unit of Measure , CRT_USER_ID --name of person who created the object , CRT_DT --date on which record was created , UPD_USER_ID --update User id , UPD_DT --update date )VALUES( #{scanNo} , #{barcode} --serial number , #{barcode} --scan s/n > raw input after scan , #{barcode} --box number , #{stockCode} --Material code , 'D'--'C' , #{docNo} --request number , #{docNoItm} --request item , TO_CHAR(SYSDATE, 'YYYYMMDD') , #{fromLocCode} --from location , #{toLocCode} --to location , #{transactionType} --Transaction Type , #{ioType} --In/Out , #{uom} --Unit of Measure , #{crtUserId} --name of person who created the object , SYSDATE , #{updUserId} --update User id , SYSDATE )

mapper.select:
    /* [com.coway.trust.biz.logistics.serialmgmt.SerialMgmtNewMapper.selectHPDelStsCheck] - KR JIN */
