mapper.select:
SELECT Project1.SIRIM_TYPE_ID SIRIM_TYPE_ID , Project1.SIRIM_NO SIRIM_NO , Project1.SIRIM_TYPE_CODE SIRIM_TYPE_CODE , Project1.SIRIM_TYPE_NAME SIRIM_TYPE_NAME , Project1.SIRIM_LOC SIRIM_LOC , Project1.SIRIM_QTY SIRIM_QTY FROM ( SELECT Extent1.SIRIM_LOC SIRIM_LOC , Extent1.SIRIM_TYPE_ID SIRIM_TYPE_ID , Extent1.SIRIM_TYPE_CODE SIRIM_TYPE_CODE , Extent1.SIRIM_TYPE_NAME SIRIM_TYPE_NAME , Extent1.SIRIM_QTY SIRIM_QTY , LTRIM(RTRIM(Extent1.SIRIM_NO)) SIRIM_NO FROM ( SELECT LOG1002V.SIRIM_NO SIRIM_NO , LOG1002V.SIRIM_LOC SIRIM_LOC , LOG1002V.SIRIM_TYPE_ID SIRIM_TYPE_ID , LOG1002V.SIRIM_TYPE_CODE SIRIM_TYPE_CODE , LOG1002V.SIRIM_TYPE_NAME SIRIM_TYPE_NAME , LOG1002V.SIRIM_QTY SIRIM_QTY FROM LOG1002V ) Extent1 WHERE 1 = 1
AND (LTRIM(RTRIM(Extent1.SIRIM_NO)))= #{searchSirimNo}
AND Extent1.SIRIM_TYPE_ID = #{searchCategory}
AND Extent1.SIRIM_LOC = #{searchWarehouse}
) Project1 ORDER BY Project1.SIRIM_NO ASC

SELECT TRNSIT_ID AS trnsitid , TRNSIT_NO AS trnsitno , TRNSIT_DT AS trnsitdt , TRNSIT_FROM AS trnsitfr , TRNSIT_TO AS trnsitto , TRNSIT_CURIER AS trnsitcur , (select curier_name from SYS0016M where CURIER_CODE = TRNSIT_CURIER) curiername , TRNSIT_STUS_ID AS trnsitstusid , TRNSIT_STUS_CODE AS trnsitstuscd , TRNSIT_STUS_NAME AS trnsitstusnm , TRNSIT_CRT_USER_ID_ID AS crtuserid , TRNSIT_CRT_USER_ID_USER_NAME AS crtusernm , TRNSIT_CLOS_DT AS trnsitcdt , TOT_TRNSIT_ITM AS totitm , TOT_CNT AS totcnt FROM LOG1003V LV , SYS0028M S28 WHERE LV.TRNSIT_TO = S28.WH_LOC_CODE
AND LV.TRNSIT_NO = #{strnsitno}
AND LV.TRNSIT_STUS_ID = #{sStatus}
AND LV.TRNSIT_FROM = #{sfrloc}
AND LV.TRNSIT_TO = #{stoloc}
ORDER BY LV.TRNSIT_NO

select ROWNUM RNUM , L36.SRM_TRANS_ITM_ID STII , L36.SRM_TYPE_ID STID , L36.SRM_NO SNO , L36.SRM_RESULT_STUS_ID SRSI , TO_CHAR(L36.SRM_TRANS_UPD_DT , 'dd/mm/yyyy') STOD , TO_CHAR(L36.TR_TRANS_CLOS_DT , 'dd/mm/yyyy') TTCD , S13.CODE_DESC CDESC , S38.CODE CODE , DECODE(S47.USER_ID , NULL , '-' , S47.USER_NAME) UNAME from LOG0036D L36 , SYS0013M S13 , SYS0038M S38 , SYS0047M S47 WHERE 1 = 1 AND L36.SRM_TYPE_ID = S13.CODE_ID AND L36.SRM_TRANS_STUS_ID = S38.STUS_CODE_ID AND L36.SRM_TRANS_UPD_USER_ID = S47.USER_ID(+) AND SRM_TRANS_STUS_ID = 1 AND SRM_TRANSF_ID = #{trnsitid}
AND SRM_RESULT_STUS_ID = #{statusid}
order by SRM_TRANS_ITM_ID

    select DOC_NO_ID, DOC_NO_PREFIX, DOC_NO, TYPE_CODE, TYPE_DESC, ISS_DT, AUTO_REFRESH, AUTO_REFRESH_PRIOD 
	  from SYS0022C
	 where doc_no_id = #{doctype}

mapper.update:
update SYS0022C
   set DOC_NO = #{nno}
  where doc_no_id = #{dno}

update LOG0036D
   set SRM_TRANS_STUS_ID = 8,SRM_TRANS_UPD_DT = sysdate
  where SRM_TRANS_ITM_ID = #{stii}

    update LOG0035D
	   set SRM_TRANSF_STUS_ID = #{utrnsitstus}
	     , CURIER_CODE        = #{ucourier}
	     , SRM_TRANSF_UPD_DT  = SYSDATE
	     , SRM_TRANSF_UPD_USER_ID =#{suserid}
	 where SRM_TRANSF_ID = #{htranid}
